css:
@-moz-document domain("observador.pt") {
body,
#main-nav>ul>li.current,
#mega-menu,
#main-nav>ul>li.current .sub-menu,
.single-obs_longform .longform_content,
.single-obs_longform>.bottomMeta,
#auto_content select{
    background: #383838 !important;
        color: silver !important;
}

.paywall-block-grad:before {
    background: #fff;
    background: linear-gradient(to bottom,rgba(255,255,255,0) 0,#383838 100%);
}

h1, h2, h3, h4, h5, h6,
p,
.news-item, .opinion-item,
a,
body,
#main-nav>ul>li.current .sub-menu,
#main-nav>ul>li.current .sub-menu li,
#main-nav .sub-menu>li a,
#main-nav  .sub-menu:before,
.single-obs_longform header .positioner .content .authors-wrapper .author>a .authors{
    color: silver !important;
}

#main-nav>ul,
.paywall-block-grad:before{
    border-bottom: 0px;
}

.recirculation{
    background: inherit;
}



.mod .meta span.flag {
    color: white;
    padding: 0;
    text-transform: uppercase;
}

.mod .meta .obs-icon-share,
footer .logo svg,
#auto_recommender .wrapper>form>label span:before,
.logo{

    filter: invert(1);
}

#main-nav>ul>li>a,
#main-header .meta_nav .callout a,
aside.podcast_widgetv2 ul .calltoaction{

    color: white !important;
}

/* Change original blue background color */
#main-header,
#main-nav>ul,
.single-obs_longform .longform_content>.block_quote.azul,
.single-obs_longform .longform_content>.block_quote,
aside header h3 a:after{
    background: #42637b !important;
}

/* Change original purple background color */

body.lifestyle #main-header {
    background: #5a4e6f !important;
}

.textcomment-container,
.obs-cta .content,
div.obs-tabs ul.obs-tabs-navigation li,
.recommender div.wrapper,
#share-main{
    background: #464646 !important;
    color: silver;
}

div.obs-tabs ul.obs-tabs-navigation li.current{
    background: #909090 !important;
}

#auto_content .obs-tabs-navigation li {
    border-right: 0.05rem solid #fff;
    width: 22%;
}

.small-news-list img.author-photo, .small-news-list img.photo,
.mod.mod-posttype-opinion.mod-size-4 .image,
.opinion-item .author-photo img.author-photo, .opinion-list .author-photo img.author-photo,
.mod.mod-posttype-opinion.mod-size-4 .image .user_img,
.mod.mod-posttype-opinion .image,
.mod.mod-posttype-opinion .image .user_img{
    background: inherit !important;
    border-color: #4c4c4c !important;
}

.mod.mod-posttype-opinion.mod-size-4 .image{
    background: inherit;
}

.auto_single .news-item .content table tr:nth-child(even){
    background: none !important;
}
}
