/* ==UserStyle==
@name           Brainly Dark
@namespace      github.com/openstyles/stylus
@version        0.9
@description    Inverted Brainly for night owls
@author         Raeffion
==/UserStyle== */

@-moz-document domain("brainly.com") {
/*Header*/
.sg-header__container {
    background-color: #444;
}
/*User Menu*/
.user-box__menu {
    background: #333;
}
.sg-menu-list__link {
    color: #999;
}
.game-box__dashed-progress-bar {
    filter: invert(80%);
}
/*Notification Menu*/
.sg-bubble {
    background: #222;
}
.sg-media--focused {
    background-color: #222;
}
.sg-media--clickable:hover {
    background-color: #333;
}
.sg-media__content {
    color: #ccc;
}
.sg-button-secondary--alt {
    background-color: #111;
}
/*Footer*/
.sg-footer {
    background-color: #222;
}
/*Background*/
body {
    background: #111;
}
.sg-layout__container {
    background-color: #222;
}
.sg-layout__box {
    background-color: #333;
}
.sg-box--dark {
    border-color: #2c2c2c;
    background: #222;
}
/*Text*/
.sg-text,
.sg-header-primary {
    color: #ddd;
}
.sg-header-secondary,
.sg-link--gray {
    color: #999;
}
.sg-headline {
    color: #ccc;
}
/*Buttons*/
.sg-button-secondary--active-inverse {
    background-color: #222;
    color: #ff796b;
    fill: #ff796b;
}
.sg-button-secondary--inverse {
    background-color: #222;
    color: #53cf92;
    fill: #53cf92;
}
.sg-button-secondary--dark-inverse {
    background-color: #222;
    color: #434e66;
    fill: #434e66;
}
.sg-select__element {
    background: #222;
}
/*Sidebar*/
.sg-box {
    border: 0px solid #9fa6b5;
    background-color: #333;
}
.challenge-item {
    background-color: #191919;
    -webkit-mask-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAIAAACQd1PeAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAA5JREFUeNpiYGBgAAgwAAAEAAGbA+oJAAAAAElFTkSuQmCC);
}
.brn-subject-list__subject--active {
    background: #333;
}
.sg-dropdown__hole {
    background-color: #222;
}
/*Challenge Start Buttons*/
.sg-button-secondary--alt-inverse {
    background-color: #222;
    color: #57b2f8;
    fill: #57b2f8;
}
/*Answer Form*/
.sg-textarea {
    background: #333;
    border: 2px solid #e1e8ed;
    color: #020a1b;
}
.wysihtml5-sandbox js-editor-sandbox {
    background-color: rgb(56, 56, 61);
    border-color: rgb(2, 10, 27);
    outline: 0px none rgb(2, 10, 27);
}
.brn-editor__content {
    background: #222;
    padding: 12px;
    border-radius: 8px;
}
/*USER PROFILE PAGE*/
/*Background*/
#html .mint {
    background: #111 !important;
}
div#content-old {
    background-color: #222;
}
#profile {
    background-color: #222;
}
#html .mint #profile #main-left .personal_info {
    border: 2px solid #999;
}
/*Body*/
.mint-header-secondary,
.mint-cake-heading,
#html .mint #profile #main-left .personal_info .header .info .info_top .ranking h2 a,
#html .mint #profile #main-left .personal_info .header .info .info_top .rank h3 span,
#html .mint #profile #main-left .personal_info .header .info .info_top .points h1,
#html .mint #profile h3 {
    color: #999;
}
#html .mint #profile #main-left .personal_info .extra-info li span {
    color: #ddd;
}
#html .mint #profile #main-left .personal_info .extra-info li {
    color: #ddd !important;
}
#html {
    color: #ddd;
}
div#content-old ol.tasks-list .task .task-content a {
    color: #c2c2c2;
}
}
