css:
@-moz-document url-prefix("https://hermitcraft.com")
{
body {
    background: #1F1F1F;
}

#backgroundImg {
    visibility: hidden;
}

#MemberListContainer, #sidebaradcontainer, .navbar-toggle, .list-group-item, #HermitCraftContainer, .btn-default {
    background-color: #313131;
    color: #C9C9C9;
}

.list-group-item {
    border: 1px solid #3e3e3e;
}

.btn-default {
    background-color: #1F1F1F;
    border-color: #3e3e3e;
}

.btn-default:hover {
    background-color: #3e3e3e;
    color: #C9C9C9;
}

.btn-default.active {
    background-color: #313131;
    color: #C9C9C9;
}

ul.video-details>li>a {
    color: #e08804;
}

a, a:visited {
    color: #C9C9C9;
}

a:hover {
    color: #000;
    text-decoration: none;
}

.badge {
    color: #e08804 !important;
    font-weight: bold !important;
    font-size: 14px;
}

#MembersMenu .livetext, #MemberListContainer .list-group-item>.badge {
    color: #ce0e0e !important;
}

ul.video-details>li>a:hover {
    opacity: 0.7;
}

ul.video-details>li>a:visited, ul.video-details>li>span>a.ng-binding {
    color: #C9C9C9 !important;
}

#sidebaradcontainer {
    height: 150px;
    width: 150px;
}

div#sidebaradcontainer {
    background: transparent url(https://i.imgur.com/7ijlCxJ.jpg) no-repeat;
    background-size: 150px;
    margin-left: 45px;
    margin-top: 25px;
    opacity: 0.7;
    border-radius: 15px 15px 15px 15px;
}

#MemberListContainer, #HermitCraftContainer {
    border-radius: 15px 15px 15px 15px;
}

.btn-default, button#btnMore.btn.btn-default {
    border-radius: 5px 5px 5px 5px;
}

#HermitCraftContainer article section {
    margin: 5px 5px 5px 5px
}

#HermitCraftContainer article figure:hover {
    ms-transform: scale(1.2);
    -moz-transform: scale(1.2);
    -webkit-transform: scale(1.2);
    -o-transform: scale(1.2);
    transform: scale(1.1);
    -webkit-transition: all .5s;
    -moz-transition: all .5s;
    -o-transition: all .5s;
    transition: all .5s;
}

#ModSauce {
    background-color: #3e3e3e;
}

#TOSRow.row {
    visibility: hidden;
    max-height: 0px;
    padding: 0 0 0 0;
}

#TOSContainer {
    visibility: hidden;
    max-height: 0px;
}

#FooterContainer {
    
    background-color: #313131;
    color: #C9C9C9;
    border-radius: 5px 5px 5px 5px;
}

#livestream {
    background-color: #313131;
    color: #C9C9C9;
    border-radius: 5px 5px 5px 5px;
}

#livestream a {
    color: #e08804;
}

#livestream a:hover {
    opacity: 0.7;
}

#livestream a:visited {
    color: #000 !important;
}

footer {
    padding: 10px 0px 0px 0px;
}

:-webkit-scrollbar-track-piece
  {
    background-color: #212121;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
  }

::-webkit-scrollbar {
    background-color: rgba(62, 62, 62, .92);
    border: 1.5px solid rgb(62, 62, 62);
    width: 15px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(31, 31, 31, 1);
    border: 1.5px solid rgb(62, 62, 62);
}
}

@-moz-document url-prefix("https://livestream.hermitcraft.com")
{
body {
    background: #1F1F1F;
}

img.backgroundImg {
    visibility: hidden;
}

div.links {
    background-color: #3e3e3e;
    font: 0.8em "Open Sans", sans-serif;
}

a, a:visited {
    color: #C9C9C9;
}

a:hover {
    color: #e08804;
    text-decoration: none;
}
}

@-moz-document url-prefix("https://tag.hermitcraft.com")
{
body {
    background: #1F1F1F;
}

img.backgroundImg {
    visibility: hidden;
}

div.box {
    background-color: #3e3e3e;
}

a, a:visited {
    color: #C9C9C9;
}

a:hover {
    color: #e08804;
    text-decoration: none;
}
}
