/* calendar icon */
.gb_0a.gb_Ae {
	display: none;
}
/* word "calendar" */
.gb_Be.gb_ze {
	opacity: 1;
}
/* center of header */
.gb_Pc.gb_fe.gb_ge.gb_oe.gb_ae.gb_ee.gb_le {
	margin-top: 3px;
}
/* header text and icons */
.gb_Zd.gb_ve,
.gb_sc svg,
.RveJvd.snByac,
div.rF3YF span,
.rSoRzd,
.gb_Zd.gb_ve .Lw7GHd.snByac svg,
.gb_Zd.gb_ve .NlWrkb.snByac,
