@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {






/* *********************************** */
/* START COLOR CONFIGURATION PART      */
/* *********************************** */
	a:hover[href*="MyHattrick"],
	a:hover[href*="Arena"],
	a:hover[href*="Club"],
	a:hover[href*="World"],
	a:hover[href*="Staff"],
	a:hover[href*="Fans"],
	a:hover[href*="Matches"],
	a:hover[href*="Players"],
	a:hover[href*="Manager"],
	a:hover[href*="Forum"],
	a:hover[href*="Transfers"],
	a:hover[href*="Finances"],
	a:hover[href*="Training"],
	a:hover[href*="Challenges"],
	a:hover[href*="Youth"],
	a:hover[href*="XXXXXX"],
	a:hover[href*="XXXXXX"],
	a:hover[href*="Stage"]{
		/* COLLEGAMENTI */
		color:#FF6600!important; 
	}
	#teamLinks a{
		/* TEAMLINKS GROSSI SENZA UNDERLINE */
		color:black!important;
	}
	#teamLinks a:hover{
		/* TEAMLINKS AL PASSAGGIO DEL MOUSE */
		color:#FF6600!important; /* 4.1  */
	}
	#menu {
		/* MENU HATTRICK COLORE SFONDO*/
		background: #FF6600 none  no-repeat scroll left top !important;
	}
	#menu a{
		/* MENU COLORE SCRITTE */
		color: black !important; /* 4.1  */
	}
	#menu a:hover{
		/* MENU HATTRICK AL PASSAGGIO MOUSE */
		background: black !important;
		color: #FF6600 !important; /* 4.1  */
	}
	.sidebarBox .boxHead h2, 
	.subMenuBox .boxHead h2, 
	.main .boxHead h2,
	.sidebarBox .boxHead .boxLeft h2,
	.subMenuBox .boxHead .boxLeft h2,
	.main .boxHead .boxLeft h2{
		/* BOX  : PARTE ALTA BORDI ARROTONDATI*/
		background: #FF6600 none  no-repeat scroll left top !important;
		border: 1px solid black !important;
		color:black!important; /* 4.1  */
	}
	.main .boxHead h2 span, .main .boxHead h2 a {
		/* SCRITTA PARTE CENTRALE BORDI ARROTONDATI*/
		color:black!important; 
	}
	.main .boxHead h2 span, .main .boxHead h2 a:hover{
		/* SCRITTA PARTE CENTRALE BORDI ARROTONDATI al passaggio del mouse*/
		color:white!important;   /*4.3*/
	}
	.sidebarBox .boxBody, 
	.subMenuBox .boxBody, 
	.main .boxBody {
		/* BOX PARTE CENTRALE BORDI*/
		background:#FFFFFF none repeat scroll 0 0!important;
		border-left:1px solid  black !important;
		border-right:1px solid  black !important;
	}
	.sidebarBox .boxFooter .boxLeft, 
	.subMenuBox .boxFooter .boxLeft, 
	.main .boxFooter .boxLeft {
		/* BOX PARTE BASSA BORDI ARROTONDATI*/
		background: #222 none  no-repeat scroll left top !important;
		border: 1px solid black !important;
	}
	#page{
		/* PAGINA BORDI ARROTONDATI*/
		border: 1px solid black !important;
	}
	#top, #header, #content, #bottom, #page{
		/* PAGINA COLORE SFONDO CENTRALE*/
		background: #FFCC99 none  no-repeat scroll left top !important;
	}

	.cfWrapper {
		/* FORUM : BORDINI BOX MESSAGGI */
		border: 1px solid #333  !important;
	}
	.cfHeader {
		/* FORUM : HEADER BOX MESSAGGI */
		background: #333  !important;
		color: #fff  !important;
	}
	.cfHeader a{
		/* FORUM : HEADER LINK BOX MESSAGGI */
		color: white  !important;
	}
	.cfHeader a:hover{
		/* FORUM : HEADER LINK HOVER BOX MESSAGGI */
		color: #FF6600  !important;  /*4.2*/
		text-decoration:none!important;  /*4.2*/
	}
/* *********************************** */
/* END COLOR CONFIGURATION PART        */
/* *********************************** */







/* ************************************ */
/* START IMAGES CONFIGURATION PART      */
/* ************************************ */
	#logo {
		/* LOGO HATTRICK */
		background: transparent url(http://nrgjack.altervista.org/_altervista_ht/style/orangetrick/zune.png)  no-repeat scroll left top !important;
	}
	body{
		/* SFONDO HATTRICK */
		background: url(http://nrgjack.altervista.org/_altervista_ht/style/blacktrick/bg.png)   !important;
	}

	img.scLive, 
	img.scTransfer, 
	img.scMail, 
	img.scNewMail, 
	img.scNotes, 
	img.scSearch, 
	img.scMySupporters, 
	img.scReminders, 
	img.scBookmarks, 
	img.scDigit1, 
	img.scDigit2, 
	img.scDigit3, 
	img.scDigit4,
	img.scDigit5, 
	img.scChallenges, 
	img.scManager, 
	img.scMatches, 
	img.scPlayers, 
	img.scBids, 
	img.scFans, 
	img.scTraining, 
	img.scMatches2, 
	img.scEconomy, 
	img.f_quote1, 
	img.f_quote2, 
	img.f_bold, 
	img.f_italic, 
	img.f_ul, 
	img.f_hr, 
	img.f_player, 
	.bookmarkSmall img, 
	img.f_team, 
	img.f_match, 
	img.f_fed, 
	img.f_message, 
	img.f_series, 
	img.f_www, 
	img.hotlist, 
	img.matchOrder, 
	img.matchOrderSet, 
	img.matchOrderSetDisabled, 
	img.matchOrderWrite, 
	img.matchOrderWriteDisabled, 
	img.matchHTLive, 
	img.matchFriendly,
	img.matchLeague, 
	img.matchCup, 
	img.matchQualification, 
	img.matchMasters, 
	img.supporterRed,
	img.supporterGreen {
		/* TUTTE LE IMMAGINI */
		background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/blacktrick/master_icons.png)   !important;
	}
/* ************************************ */
/* END IMAGES CONFIGURATION PART        */
/* ************************************ */







/* ************************************ */
/* START STRUCTURE CONFIGURATION PART   */
/* ************************************ */
	#logo { 
		/* LOGO HATTRICK */
		height:27px!important;
		top:-5px!important;
		left:2px!important;
	}
	#menu {
		/* MENU HATTRICK */
		-moz-border-radius: 7px;
		-webkit-border-radius: 7px;
		border: 1px solid #000;
		height:30px!important;
	}
	#menu a{
		/* MENU ALTEZZA */
		line-height: 30px !important; 
	}
	#teamLinks a{
		/* TEAMLINKS GROSSI SENZA UNDERLINE */
		text-decoration:none!important;
		font-weight:bold!important;
		font-size:larger!important;
	}
	.sidebarBox .boxHead h2, 
	.subMenuBox .boxHead h2, 
	.main .boxHead h2,
	.sidebarBox .boxHead .boxLeft h2,
	.subMenuBox .boxHead .boxLeft h2,
	.main .boxHead .boxLeft h2,
	#page,
	#top{
		/* BOX,PAGE  BORDI ARROTONDATI ALTI*/
		-moz-border-radius-topleft : 7px !important;
		-webkit-border-top-left-radius : 7px !important;
		-moz-border-radius-topright : 7px !important;
		-webkit-border-top-right-radius : 7px !important;
		margin-left:0px !important;
	}

	.sidebarBox .boxHead , 
	.subMenuBox .boxHead , 
	.main .boxHead ,
	.sidebarBox .boxHead .boxLeft,
	.subMenuBox .boxHead .boxLeft,
	.main .boxHead .boxLeft{
		/* LEVO IMG BOX PARTE ALTA BORDI ARROTONDATI*/
		background: transparent none  no-repeat scroll left top !important;
	}

	.sidebarBox .boxFooter .boxLeft, 
	.subMenuBox .boxFooter .boxLeft, 
	.main .boxFooter .boxLeft,
	#page,
	#bottom {
		/* BOX,PAGE  BORDI ARROTONDATI BASSI*/
		-moz-border-radius-bottomleft : 7px !important;
		-webkit-border-bottom-left-radius : 7px !important;
		-moz-border-radius-bottomright : 7px !important;
		-webkit-border-bottom-right-radius : 7px !important;
	}



/* ************************************ */
/* END STRUCTURE CONFIGURATION PART     */
/* ************************************ */




}
