css:
@-moz-document domain("www.hao123.com") {
/* 背景 */
body {
    background-image: url(https://i.loli.net/2018/08/02/5b62164d654bc.jpg) !important;
    /*     background-position: center bottom !important; */
    background-size: cover !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
}
/* 透明处理 */
.layout-container-inner div:first-child {
    background-color: rgba(255, 255, 255, 0.3)!important;
}
.fixed-bg {
    background-color: rgba(255, 255, 255, 0.8)!important;
}
.slidetoolbarv2 {
    background: rgba(255, 232, 232, 0.5)!important;
}
.slidetoolbar-closebtn {
    background-color: rgba(255, 232, 232, 0.5)!important;
}
.page-bg {
    background: rgba(242, 244, 248, 0.51)!important;
}
.menus {
    background-color: rgba(255, 255, 255, 0.0)!important;
}
.g-bg11 {
    background-color: rgba(255, 255, 255, 0.2)!important;
}
/* 侧边新闻 */
.g-lh14 {
    display: none!important;
}
#box-toplist2 {
    display: none;
}
#box-toplist1 {
    display: none;
}
#box-toplist5 {
    margin-top: 30px;
}
.mt10 {
    margin-top: 30px!important;
}
.toppicture2 {
    margin-top: 30px;
}
#box-toplist6 {
    margin-top: 30px;
}
/* 专版浏览器 */
.sethome {
    display: none!important;
}
/* 字中广告 */
.bar-text {
    display: none!important;
}
/* 顶部广告条 */
#navrecommend-top {
    display: none;
}

/* 中间空白部分 */
.layout-adbar {
    display: none!important;
}
/* 右侧无用按钮 */
#shortcut-box {
    display: none!important;
}
/* 底下部分屏蔽 */
#hao123-bottom {
    display: none!important;
}
.bottom-banner-link-wrapper {
    display: none!important;
}
}
