css:
@-moz-document domain("intern.textbroker.com") {
td,
td.body,
.header_b,
.header_a {
    background: #073642;
    color: #93a1a1;
}

input.button-green,
button.button-green {
    background-image: none;
    background-color: #859900;
    color: #073642;
}

input.button-red,
button.button-red {
    background-image: none;
    background-color: #dc322f;
    color: #073642;
}

table.table-box {
    border: none;
}

element {
    border: none;
}

div.color_bar {
    background: #002b36;
}

div.submenu_a {
    background: #002b36;
}

a.menuItemActive,
a.menuItemactive {
    color: #cb4b16;
}

a.menuButtonActive,
a.menuButtonActive:hover {
    background: #002b36;
    color: #eee8d5;
    border: none;
}

ul.topnavigation li a {
    color: #93a1a1;
}

ul.topnavigation li a {
    background: none;
}

a.menuItem:hover {
    color: #dc322f;
}

ul.topnavigation li a:hover {
    color: #cb4b16;
}

a.menuButton {
    border: none;
    background: #586e75;
    color: #93a1a1;
}

a.menuButton {
    border: none;
}

input.button-red:active,
button.button-red:active,
input.button-green:active,
button.button-green:active,
input.button-norm:active,
button.button-norm:active {
    background-color: #002b36;
    color: #93a1a1;
}

input.button-norm,
button.button-norm {
    background-image: none;
    background-color: #eee8d5;
    color: #073642;
}

td.main {
    color: #93a1a1;
    font-family: PragmataPro;
}

div.box-wrapper {
    background: #002b36;
}

.markItUpEditor {
    font-family: PragmataPro;
}

input.text {
    font-family: PragmataPro;
}

a:hover {
    color: #dc322f;
}

img.logo {
    visibility: hidden;
}

td.headline,
td.infbox-headline {
    color: #cb4b16;
    font-family: PragmataPro;
}

td.info-header {
    background-color: #002b36;
    color: #586e75;
}

td.info-box {
    background: #073642;
    color: #93a1a1;
}

.markItUpEditor {
    background-image: none;
    background: #073642;
    color: #93a1a1;
    border: 3px solid #586e75;
}

.markItUpContainer {
    border: 1px solid #586e75;
    background: #839496;
}

input.text {
    border: 1px solid #586e75;
}
}
