a.review { color: #000000; font-size: 10pt; TEXT-DECORATION: underline}
a.review:link { color: #000000; font-size: 10pt; TEXT-DECORATION: underline}
a.review:active { color: #00dddd; font-size: 10pt; TEXT-DECORATION: underline}
a.review:hover { color: #00dddd; font-size: 10pt; TEXT-DECORATION: underline}
a.review:visited { color: #333333; font-size:10pt; TEXT-DECORATION: underline}

