css:
@-moz-document url-prefix("https://www.google.com") {
/*
     * Name:  Google-Lite
     * Author:  Alan Chou
     * Version: v0.0.1
     */

    /* Background */
html, body, #cnt, .rg_l, #botabar, #hdtbSum, #appbar, .sfbg{
    background: #f1f1f1 !important;
    color: #f1f1f1;
}

.sfbg{
    padding-bottom: 10px;
    box-shadow: 2px 2px 1px #e3e3e3;
}

.rc, .cv2VAd, .kp-blk{
    background: #f7f7f7 !important;
    border-radius: 10px;
    border: 8px solid #f7f7f7;
    box-shadow: 2px 2px 2px 1px #e3e3e3;
    transition: all 0.5s;
}
.rc:hover{
    box-shadow: 2px 2px 2px 1px #bbb;
}



.fbar{
    background: #f1f1f1 !important;
}

.RNNXgb{
    border-radius: 5px !important;
    box-shadow: 0 0 0 white;
    transition: all 0.5s;
}

#hplogo, .gb_cb{
    opacity: 0.9;
    transition: all 0.5s;
}

#hplogo:hover, .gb_cb:hover{
    opacity: 1.0;
    transform: scale(1.1);
}

#SIvCob, #fbar, center input{
    display: none;
}

.e2BEnf h3{ 
    color: black;
}

#nav td{
    transition: 0.5s;
}

#nav td:hover{
    transform: rotate(360deg) scale(1.1);
    box-shadow: 2px 2px 2px 5px #e3e3e3;
    background: #90EE90;
}




#nav .fl , #nav .cur, #nav .navend{
    width: 45px;
    height: 35px;
    padding-top: 10px;
    padding-left: 10px;
    font-size: 20px;

    border-radius: 5px;
    box-shadow: 2px 2px 2px 3px #e3e3e3;
    transition: all 0.5s;
}

#nav .cur{
    height: 15px !important;
    padding-right: 20px;
    background: #4285f4;
}

#nav .fl span, #nav .cur span, #nav .navend .csb {
    display: none;
}

#nav .navend span{
    margin-left: 10px !important; 
    margin-right: 10px !important; 
    color: #f1f1f1;
}

#nav .navend:first-child{
    display: none;
}

#nav .navend:last-child{
    border-left: 5px solid #e3e3e3;
    padding-left: 0px;
    background: #696969;
    box-shadow: 2px 2px 2px 3px gray;
}


}
