css:
@-moz-document url("https://www.luogu.org/") {
.am-form-field
{
    border-radius: 100px 100px 100px 100px!important;
}
#app-body-new
{
    top:0px!important;
}
}

@-moz-document regexp("^((?!blog).)*https://www.luogu.org((?!blog).)*$")
{
/* i really want this to be global */

/*图片地址https://imgse.com/i/5dbGZe*/
body
{
	background: url("/*[[background]]*/") no-repeat;
	background-attachment: fixed;
	background-size: 100%;
}

#app-sidenav
{
    background: none;
}

a[data-v-3e5191a6]:hover,.popup-button:hover
{
    border-radius: 10px 10px 10px 10px;
    opacity:/*[[cl]]*/;
    color: #000;
    transition: color 1s,background 0.45s,box-shadow 0.45s;
}

a[data-v-3e5191a6],.popup-button
{
	background:none;
	color: #fff0;

	filter: none !important;
}

i[data-v-7e88b118]
{
	color: #fff0;
	width: 4em;
	height: 3.8em;
	padding: 1.2em 0em 0em 0em;
}

div[data-v-7e88b118]
{
	background-color: #fff0 !important;
}


i[data-v-7e88b118]:hover
{
	color: #000;
}

a[data-v-7e88b118]
{
	filter: invert(70%);
}

.popup-button
{
	height: 4em;
/*     opacity:0; */
}

.content
{
	height: 5em !important;
	background: rgba(0,0,0,0);
}

.bread-crumb[data-v-3c5eed85]
{
	padding-top: 0;
}

.width-wrap>h1
{
	margin: 0;
}

.lg-content
{
	margin: 0px 0px 0px 0px;
	padding: 0px 0px 0px 65px;
}

.lg-article
{
    background: #fff;
    border-radius: 15px 15px 15px 15px;
    box-shadow: 1px 1px 2px #000;
    opacity:/*[[cl]]*/;
}

.lg-summary
{
    background: #fff;
    border-radius: 15px 15px 15px 15px;
    box-shadow: 1px 1px 2px #000;
    opacity:/*[[cl]]*/;
}

.am-viewport
{
    border-radius: 5px 5px 5px 5px;
    opacity:/*[[cl]]*/;
}

.am-btn,.am-btn:visited,am.btn:active
{
    border-radius: 100px 100px;
    border-color:#000;
    opacity:/*[[cl]]*/;
    background-color:#000;
}

.am-btn:hover
{
    border-color:#FFF;
    background-color:#FFF;
    color: #000;
    transition: color 1s,background 1s;
}

.lg-toolbar
{
    background: #fff0;
    border: none;
}

.am-collapse
{
    box-shadow: 2px 2px 2px #000;
    background-color: white;
}

.lg-header, .lg-header-list>li
{
    background-color: white;
    color: #000 !important;
}

.lg-header-li
{
    color: #000 !important;
}
.am-form-field
{
    border-radius: 100px 100px 100px 100px;
    opacity:/*[[cl]]*/;
    border-color:#000!important;
}

.am-input-group-label
{
    border-radius: 0px 100px 100px 0px!important;
    color:#FFF!important;
    background:#000!important;
    border-color:#000!important;
}

.lg-table-bg0
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    opacity:/*[[cl]]*/;
    box-shadow: 1px 1px 2px #000;
}

.lg-table-bg1
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    box-shadow: 1px 1px 2px #000;
}

.lg-table-bg0 .am-progress
{
	box-shadow: none;
	border-radius: 0;
    opacity:/*[[cl]]*/;
}

.am-pagination>li
{
    background: #fff;
    border-radius: 20px 20px 20px 20px;
    opacity:/*[[cl]]*/;
    box-shadow: 1px 1px 2px #000;
	margin: 4px 0px 0px 4px;
}

.am-comment-main
{
    border: 0px solid;
    background: #fff;
    border-radius: 20px 20px 20px 20px;
    opacity:/*[[cl]]*/;
	box-shadow: 2px 2px 2px #000;
}
.am-comment-main::after,.am-comment-main::before
{
    display: none;
}
.am-comment-hd
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    opacity:/*[[cl]]*/;
}
.am-comment-bd
{
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    opacity:/*[[cl]]*/;
}
.am-panel-primary
{
   background: #fff;
}
.am-pagination>li>a
{
   background: #fff0;
	border:0px solid;
	margin: 2px 2px 2px 2px;
}
.am-pagination>li>a:hover
{
   background: #fff0;
	border:0px solid;
	color: #000;
}
.am-pagination .am-active>a
{
	background: #fff0;
	color: #000;
	font-weight: bold;
}
.am-pagination .am-active>a:hover
{
	background: #fff0;
	color: #000;
}
.lg-sidebar-list>li
{
	margin: 5px 5px 5px 5px;
   border-radius: 20px 20px 20px 20px;
    opacity:/*[[cl]]*/;
   box-shadow: 1px 1px 2px #000;
}
.lg-sidebar-list
{
	box-shadow: none;
}
code,pre
{
	font-family: consolas;
}
.am-nav>li.am-active>a, .am-nav>li.am-active>a:focus, .am-nav>li.am-active>a:hover
{
	background: none;
	font-weight: bold;
	color: #000;
}
.am-nav>li>a:focus, .am-nav>li>a:hover
{
	background: none;
	color: #000;
}
#app-footer, #app-header
{
	margin-left: 0em;
	background: rgba(51,51,51,0)!important;
}
a:hover,.width-wrap>h1
{
	color: #000;
}
.popup[data-v-46951e14]
{
	border: none;
}
.popup-wrap::before
{
	content: "";
	position: absolute;
	top: 0px;
	left: 0px;
	width: 100%;
	height: 100%;
	background: #fff;
	border-radius: 15px 15px 15px 15px;
    opacity:/*[[cl]]*/;
	box-shadow: 2px 2px 1px #000;
	z-index: -1;
}
.tip-twitter
{
	background-color: #fff;
	border-radius: 100px 100px 100px 100px;
    opacity:/*[[cl]]*/;
	color: #000;
	box-shadow: 0px 0px 20px #000;
}
.lg-record-tile
{
	border-radius: 20px 20px 20px 20px;
    opacity:/*[[cl]]*/;
	box-shadow: 2px 2px 2px #000;
}
.lg-record-tile>small:first-child
{
	text-align: center;
	padding-left: 0px;
}
.am-progress
{
	box-shadow: 2px 2px 2px #000;
	border-radius: 100px 100px 100px 100px;
    opacity:/*[[cl]]*/;
}
.lg-left .center>img
{
	border: none;
}
.icon-btn,.colored,.lg-article p a,.fas.fa-search
{
    color: #000!important;
}
.search-wrap.show
{
  background:#000;
}
.am-pagination.am-pagination-centered li a
{
    color:#000;
}
.am-pagination.am-pagination-centered li a:visited,.am-pagination.am-pagination-centered li a:active,.am-pagination.am-pagination-centered li a:link
{
    border-radius: 20px 20px 20px 20px!important;
}
.am-btn-primary:focus
{
    color: #FFF;
    background:#000;
    border-color:#000;
    outline:none;
}
#app-body-new
{
    top:-50px;
}
.center
{
    background:#FFF!important;
    border-color:rgba(0,0,0,0)!important;
    box-shadow:none!important;
}
.center[data-v-98f4ade8]
{
    color:#FFF!important;
    background:#000!important;
}
.sites span[data-v-27d0876a],.apps a[data-v-27d0876a],.popup[data-v-27d0876a]
{
    color:#FFF!important;
    background:#000!important;
}
.popup[data-v-27d0876a]
{
    margin-left:0em;
    position:absolute;
    top:-27px!important;
}

}
