css:
@-moz-document domain('gamedev.ru') {

body {
    background-color: #e1e1e1;
}

div.wide {
    background-color: #f7f1f1;
    font-family: Helvetica;
    font-size: 14px;
    padding-left: 5px;
    padding-right: 5px;
    margin-top: 5px;
    margin-bottom: 5px;
	box-shadow: 0px 0px 10px black;
}

div.bound {
    max-width: 950px;
    background-color: white;
    padding-left: 5px;
    padding-right: 5px;
    font-family: Helvetica;
    font-size: 14px;
}

#tool div.bound {
    background-color: #244c65 !important;
}

div.head {
    background-color: #bbccda;
	border: 1px;
    border-style: solid;
    border-color: #cccbcb;
	min-height: 10px;
    border-bottom: 1px solid gray;
}

div.overflow, div.text {
    font-size: 14px;
    font-family: Helvetica;
    background-color: #f6f6f6 !important;
    margin-bottom: 10px;
    padding: 5px;
}

span.small {
    display: block;
    width: 100%;
    margin-bottom: -15px;
    background-color: #e6e6e6;
}

li.bold {
	display:block;
	float:left;
}

li.small {
	display: block;
	float: left;
	margin-left: 10px !important;
}

div.right.unselectable {
	font-size: 14px;
	font-family: Helvetica;
	margin-top:-5px;
}

#menu ul li {
	font-size: 14px;
	font-family: Helvetica;
	padding-left: 10px;
	padding-right: 2px;
	padding-top: 1px;
	padding-bottom: 1px;
}

div.row br {
	display: none;
}

div.row {
	margin-left: 0px;
	background-color: #e6e6e6;
	padding-top: 1px;
	padding-bottom: 1px;
	font-weight: bold;
	font-family: Helvetica;
    font-size: 14px;
}

div.row:nth-child(odd) {
	background-color: #dadde2;
}

span.row {
	margin-top: -5px;
    padding-bottom: 0px;
}

h3 {
	margin-top: 5px;
	margin-bottom: 5px;
	background: #bbccda;
    padding: 3px;
    text-align: left;
    font-weight: bold;
	font-family: Helvetica;
    font-size: 14px;
}

h1 {
	font-size: 18px;
	font-family: Helvetica;
}

h2 {
	margin-top: 15px;
    margin-bottom: 5px;
    font-size: 16px;
    font-family: Helvetica;
}


div.row a {
    border-left: 1px solid white;
    padding-left: 10px;
}

div.list {
	font-family: Helvetica;
    font-size: 14px;
}

div.title {
	margin-top: 10px;
}

div.text div {
	font-family: Helvetica;
    font-size: 14px;
}

p.date {
	margin-bottom: 0px;
}

div.head ul {
    margin: 0px;
    padding: 0px;
}
  
}
