css:
@-moz-document url-prefix('https://metacpan.org/pod/'), url-prefix("https://metacpan.org/dist/"), url-prefix('https://metacpan.org/release/') {
.navbar.navbar-default, .col-md-9, .nav-list.slidepanel, .row.footer, .row.footer+.row, .row.footer+.row+.row, #index-container, #right-panel-toggle, .nav-list-container, .temporary-banner, .footer {
    display: none;
}
.page-content {
    margin-top: 0;
}
.content {
    margin-left: 0;
    border-left: none;
    margin-top: 0;
    grid-template-columns: 0;
    max-width: 10000px;
}
}
