css:
@-moz-document domain(newsobserver.com) {

#masthead {
    background: url("data:image/png;base64,") !important;

    background-repeat: repeat-x !important;
    background-position: center top !important;
    background-color: #720200 !important;
    color: #EEEEEE !important;
}

#main_nav {
 background: none repeat scroll 0 0 #720200 !important;
}

/*
 * Give borders a gradient
 */

ul.headlines .article h3 a,
#headline_rail > li > h3 > a {
   font-family: verdana, optima !important;
   font-size: 12px !important;
}

/* a little more null real estate between sections to distinguish them */
.home #main_r2,
.home #main_r3 {
   margin-top: 3em !important;
   margin-bottom: 3em !important;
}

/* this is a little iffy.  add subtle color to bkg color to bring
 * these sections together
 */
.home #main_r3 section,
.heading .section_label, .heading .label {
   background-color: #f8f8f8 !important;
}

.heading,
.section_digest .heading {
   font-size: 16px !important;
   border-bottom-color: #888 !important;
}

.section_digest .more_digest_links li {
   font-family: verdana, optima !important;
   font-size: 12px !important;
   /* font-weight: bold !important; */
}

.display_plus_7 #headline_rail li h3 {
   font-family: verdana, optima !important;
   font-size: 14px !important;
}
.display_plus_7 #headline_rail li h3:hover { font-weight: bold !important; }

/* remove top border on first item */
.section_digest ul.headlines li.article:first-child { border-top: none !important; }

/* but change style of remaining items to dotted */
.section_digest .more_digest_links,
.section_digest ul.headlines .article { border-top-style: dotted !important; }

/* no ads! */
.ad,
.story-toolbar,
div#wgt_fnscombo,
div#widget_bundle,
section#yahoo_ads {
   display: none !important;
}

#story_header,
#detail_page #story_header header {
   min-height: none !important;
   margin: 0 !important;
   padding: 0 !important;
   height: auto !important;
}

#detail_page #story_header header h1.entry-title {
   min-height: none !important;
   margin: 0 0 1em 0 !important;
   padding: 0 0 1em 0 !important;
   height: auto !important;
}

/*
 * A font geezers can actually read without Aleve
 */
div.story-body, div.story-body p,
div.story_body, div.story_body p,
div#story_body, div#story_body p,
#detail_page .story_body, #detail_page .story_body p,
div#blog-content p
{
  font-family: "palatino linotype", georgia, "lucida grande" !important;
  font-size: 15px !important;
  line-height: 1.25em !important;
  color: black !important;
}

/*
 * bigger font and more spacing for top headlines
 */
.top_headlines,
.top_headlines .headline a {
   font-size: 13px !important;
}

.top_headlines .headline {
   margin-bottom: 10px !important;
}

/*
 * Less realestate for comments
 */
ul#commentList,
ul#commentList *,
li.comment,
div.commentInfo,
p.commentHeading,
p.commentRating,
div.commentBody,
p.commentTitle,
p.commentBody {
   font-family: 'lucida grande' !important;
   font-size: 10px !important;
}

/*
 * Remove obnoxious "Tools" which popup when you mouse over them
 */
#story-tools,
#story_embedded,
#top-ads,
div.focus_box,
.story_link_share {
   display: none !important;
}

iframe.mceEditorIframe {
   display: block !important;
}

div#blog-content p br {
   margin-bottom: 1em !important;
}

/*
 * Oooooo!  PRETTY!
 */
div.nav ul.main li,
#story_assets h3,
.news_grid_container .header h4 {
   text-shadow:0 1px black !important;
}

.sandbox .head h4 {
   text-shadow:0 1px white !important;
}

/*
 * I prefer dotted rather than dashed
 */
#featured_story_widget,
.stack {
   border-right-style: dotted !important;
}

.divider {
   border-top-style: dotted !important;
}

h5 {
   border-bottom-style: dotted !important;
   padding-bottom: 0px !important;
}

/*
 * Tables should use borders.
 *
 * This fucks up the editor widget on blogs.  Sigh.
 */

table {
  border: 3px double black !important;
  border-collapse: collapse !important;
}

thead th,
thead td {
   background-color: #f1ead7 !important;
   font-weight: bold !important;
   text-align: center !important;
}

th, td {
   padding: 2px 4px !important;
   border: 1px solid silver !important;
}

th p,
td p { margin: .5em 0 !important; }

}
