    font-family:calibri;
    font-size:10px;
    text-transform:uppercase!important;
    color:#ffffff;
}


#browse_months_widget .year .year_navigation {
    font-family:calibri;
    font-size:10px;
    text-transform:uppercase!important;
    color:#ffffff;
    font-weight:bold!important;
    display: block;
    line-height: 40px;
    padding: 0 30px;
    position: relative;
    text-align: center;
}


.l-content .brick.highlighted .highlight {
    display: block;
    position: absolute;
    z-index: 101;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: none!important;
    border: 1px solid #151515;
    border-color: rgba(255,255,255,0.4);
}


#browse_months_widget .year .months ul {
    border-top: 1px solid #151515;
    list-style: none;
    margin: 0;
    padding: 0;
}


.chrome.unfollow {
    color:#ffffff!important;
}


.blue_bar .editor_navigation .header_button button.chrome {
    display: inline-block;
    height: 30px;
    padding: 4px 16px;
    border: 1px solid #151515;
    font-family:calibri;
    font-size:10px;
    text-transform:uppercase!important;
    color:#ffffff;
}

.chrome[disabled], .chrome.disabled, .chrome[disabled]:active, .chrome.disabled:active {
    background: #000000;
    border: 1px solid #151515;
    color: #ffffff;
    cursor: default;
    text-shadow: none;
    padding:4px 16px!important;
}


#browse_months_widget .year .months ul li.empty {
    background: transparent;
    border-top: 0px solid #151515;
}

#browse_months_widget .year .months ul li {
    border-bottom: 1px solid #151515;
    line-height: 19px;
    position: relative;
}
}

@-moz-document url-prefix("https://www.tumblr.com/customize/") {
.title_bar .secondary_bar,
.title_bar .title_header {
      z-index: 0;
      background-color: #000000;
      border-right: 0px solid #151515;
      outline: none!important;
      font-family: calibri;
      text-transform: uppercase;
      font-size: 10px;
}


.ace_search_field {
      background-color: white;
      color: #ffffff!important;
      border-right: 1px solid #cbcbcb;
      border: 0 none;
      -webkit-box-sizing: border-box;
      -moz-box-sizing: border-box;
      box-sizing: border-box;
      display: block;
      float: left;
      height: 22px;
      outline: 0;
      padding: 0 7px;
      width: 214px;
      margin: 0;
}


.field_row_text .field_row_label + .field_row_input textarea {
      margin-left: 10px;
      max-width: 242px;
      font-weight: normal!important;
      font-family: 'Frank Ruhl Libre', times!important;
      color: #c6c6c6!important;
      font-size: 10.6px;
      text-transform: none!important;
      border: 1px solid #151515;
      padding-top: 5px;
      overflow-y: scroll;
}


.content_container,
.panel,
.panel_overlay,
.title_bar_container {
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      box-sizing: border-box;
      border-right: 1px solid #151515!important;
}

.image_picker_popover:after {
      display: none;
}

.image_picker .menu_option:hover {
      background-color: #000000;
}

.menu_option_text,
.image_picker .menu_option {
      font-family: calibri;
      font-size: 10px;
      color: #ffffff!important;
      text-transform: uppercase;
      font-weight: bold;
}


.ace_content {
      position: absolute;
      -moz-box-sizing: border-box;
      -webkit-box-sizing: border-box;
      box-sizing: border-box;
      cursor: text;
      min-width: 100%;
      text-transform: none;
}


.sortable_item .name {
      color: #ffffff!important;
      text-transform: uppercase;
      font-family: calibri;
      font-size: 10px;
}


.button.disabled.blue,
.button.disabled.blue:active,
.button.dark_gray {
      background-color: #000000;
      border-color: #151515;
      color: #ffffff!important;
      text-transform: uppercase;
      font-family: calibri;
      padding: 2px 14px;
      font-size: 10px;
}


input,
textarea {
      font-family: calibri;
      text-transform: uppercase;
      font-weight: bold!important;
      line-height: 1.4;
      font-size: 13px;
      font-style: normal;
      margin: 0;
      background-color: #000000!important;
      color: #ffffff;
      border: 0px solid #d5d9dd;
      border-radius: 0px;
      box-sizing: border-box;
      outline: 0;
}


.info_table p {
      font-family: calibri;
      font-size: 10px;
      text-transform: uppercase;
      color: #ffffff;
}


.info_table samp {
      font-family: calibri;
      font-size: 10px;
      font-weight: bold;
      text-transform: uppercase;
      color: #474747;
}


.field_row.large_and_in_charge .field_row_input {
      font-weight: 700;
      text-align: left;
      background-color: #000000!important;
}


.bluthSkin .mceIframeContainer {
      border-top: 0px solid #151515;
      display: block;
}

.field_row select {
      color: #444;
      color: #ffffff;
      background-color: #000000;
      font-family: calibri;
      font-size: 10px!important;
}


.bluthSkin iframe {
      display: block;
      background: #000000;
}


.field_row,
.field_row .input_prefix,
.field_row input,
.field_row select,
.field_row textarea {
      color: #444;
      color: #ffffff;
      font-family: calibri;
      font-size: 10px!important;
}


.resize_handle {
      display: none;
}


.edit_theme_html.ace_editor {
      position: absolute!important;
      top: 0;
      right: 3px!important;
      bottom: 0;
      left: 0;
}

.scrollbar_container .scrollbar_pill {
      position: absolute;
      top: 0;
      right: 4px;
      bottom: 0;
      left: 0;
      border-radius: 0px;
}


.edit_theme_html.ace_editor {
      position: absolute!important;
      top: 0;
      right: 10px;
      bottom: 0;
      left: 0;
}


.current_theme .theme_text .theme_title,
.theme_text .edit_html {
      font-family: calibri;
      font-size: 10px;
      line-height: 17px;
      font-weight: bold;
      text-transform: uppercase;
}

.current_theme.custom_html .theme_thumbnail,
.delete:before,
.drag_anchor:before,
.panel_link .icon,
.secondary_bar .ace_searchbtn:after,
.secondary_bar .icon:before {
      -webkit-filter: brightness(.5) contrast(3) saturate(0);
}

.field_row_label_inner {
      padding: 12px 10px;
      overflow: hidden;
      text-overflow: ellipsis;
      color: #ffffff!important;
      font-size: 10px!important;
}

.ace-tumblr .ace_gutter-layer .ace_gutter-cell {
      color: #151515;
      background-color: #000000!important;
}

.ace_content {
      background: #000000!important;
}

.content_container {
      top: 45px;
      overflow: auto;
      z-index: 250;
      background-color: #000000!important;
}


.popover_gradient > .popover_inner {
      background: #f2f2f2;
      padding: 0px;
      box-shadow: none!important;
}


.search_subheading,
.menu_inner,
.menu_item_text {
      padding: 10px!important;
      color: #ffffff!important;
}


.popover_menu_item {
      margin-top: 0px!important;
      font-family: calibri;
      font-weight: bold!important;
      font-size: 10px!important;
      color: #ffffff!important;
      padding: 10px!important;
}


.button.green {
      background-color: #000000!important;
      font-weight: bold!important;
      font-family: calibri;
      color: #ffffff;
      font-size: 10px!important;
      text-transform: uppercase;
      border: 1px solid #151515!important;
      padding: 2px 14px;
}

.upload_static_file_container .header {
      outline: 1px solid #151515;
}

.fieldset {
      background-color: #000000;
      color: #ffffff!important;
      font-family: calibri;
      font-size: 10px!important;
      font-weight: bold;
      text-transform: uppercase;
}

.upload_static_file_container .uploaded_file .file_actions {
      padding-right: 20px;
      color: #ffffff!important;
}

.button.blue {
      background-color: #000000;
      font-weight: bold!important;
      font-family: calibri;
      font-size: 10px!important;
      text-transform: uppercase;
      border: 1px solid #151515;
      padding: 2px 14px;
}

.upload_static_file_container .asset_replace,
.upload_static_file_container .file_actions .text_button {
      position: relative;
      display: inline-block;
