      color: #474747;
      font-weight: bold!important;
      font-family: calibri;
      font-size: 10px!important;
      text-transform: uppercase;
}

.field_row_text .field_row_label + .field_row_input textarea {
      margin-left: 10px;
      max-width: 242px;
      font-weight: normal!important;
      font-family: 'Frank Ruhl Libre', times!important;
      color: #c6c6c6!important;
      font-size: 10.6px;
      text-transform: none!important;
      border: 1px solid #151515;
      padding-top: 5px;
}

.bottom_border,
.top_border {
      border: 1px solid #151515;
      margin-left: 10px;
      max-width: 216px;
}


.navigation_row {
      color: #474747;
      font-family: calibri;
      font-size: 10px!important;
      font-weight: bold;
      text-transform: uppercase;
}


.module:not(:first-child) .module_title {
      margin-top: -10px;
      font-family: calibri;
      font-size: 10px!important;
      font-weight: bold;
      text-transform: uppercase;
}

.scrollbar_container .scrollbar_pill {
      background-color: #000000;
      border: 1px solid #151515;
}


.popover_menu_item {
      margin-top: 0px!important;
      background: transparent!important;
      outline: 0px solid #151515;
      font-family: calibri;
      font-size: 10px!important;
      color: #939393!important;
      padding: 0px;
}

.secondary_bar .ace_search_field,
.secondary_bar input,
.secondary_bar input:focus {
      background-color: transparent;
      font-family: calibri;
      font-weight: bold!important;
      text-transform: uppercase;
      font-size: 10px!important;
      color: #ffffff!important;
}

.secondary_bar .ace_replacebtn,
.secondary_bar .ace_searchbtn {
      background-color: transparent;
      font-family: calibri;
      font-weight: bold!important;
      text-transform: uppercase;
      font-size: 10px!important;
