      line-height: 16px;
      background-color: #000000;
}

.panel.resizable .resize_handle {
      display: block;
      border-right: 0px solid #151515;
}


.panel_popover_content {
      border: 1px solid #151515;
      position: relative;
      border-radius: 0px;
      z-index: 999;
      background: #000000;
      box-shadow: 0 0 5px rgba(0, 0, 0, .3);
}

.color_editor .color_picker .color_value input.color_text {
      background: #000000;
      outline: 1px solid #151515;
      ;
      border: 0px solid #151515;
      color: #fff;
      font-family: calibri;
      font-weight: bold!important;
      text-transform: uppercase;
      font-size: 10px!important;
      margin: 0;
      padding: 5px 0 7px;
      text-align: center;
      width: 100%;
}

.color_editor .color_picker .color_value {
      height: 24px;
      border: 0px solid #151515;
      outline: 1px solid #151515;
      position: absolute;
      right: 0;
