css:
@-moz-document domain("wsmud.com") {
/*====================================================
Last updated: 2019/4/20
按F12检查网页元素自己微调
======================================================*/
/* 主要内容窗口 包括游戏各种元素*/
.container {
    position: fixed;
    top: 0;
    left: 0;
    width: 61.8%;
    /* 
    background: #090705;
    border: 1px solid #404000;
    */
}
/* 房间内容 */
.content-room {
    border: 1px solid #d91750;
    /*  */
}
/* 内容通讯窗口 
.content-message {
    border: 1px solid #d91750;
    background: #090705;
    
}*/
/* 聊天窗口 */
.channel {
    position: fixed;
    top: 0;
    right: 0;
    width: 38.2%;
    min-height: 61.8%;
    bottom: 10px;
    background: #0907051f;
    border: 1px solid #d9175030;
}

/* 背包设置排名等弹窗对话框 */
.dialog {
    position: fixed;
    width: 61.8%;
    background: #1c180f;
    z-index: 14;
}
/*购买物品确认对话框*/
.dialog-confirm {
    position: fixed;
    top: 38.2%;
    width: 61.8%;
    border: 1px solid #fffcee;
}
/* 地图 */
.map-panel {
    position: fixed;
    top: 0;
    right: 0;
    width: 38.2%;
    min-height: 23%;
    z-index: 15;
}
.map {
    z-index: 14;
    border: 1px solid #404000;
    background: #9e9d9b;
}
}

@-moz-document domain("wsmud.com") {
/* 插件控制面板 */
.WG_log {
    position: fixed;
    right: 0;
    bottom: 0;
    width: 38.2%;
    min-height: 38.2%;
    border: 1px solid #d9175030;
    /*
    border: 1px solid #404000;
    flex: 1;
    overflow-y: auto;
    max-height: 23em;
    */
}
/*插件右键菜单响应式布局*/
@media(min-width: 1440px) {
    body {
        width: 100%;
    }
}
}

