 @-moz-document domain("www.youtube.com") {
/* hi boy */

    /* Like */
    #like-bar.ytd-sentiment-bar-renderer {
        background: #ffffff !important;
    }

#like-bar.ytd-sentiment-bar-renderer { background: /*[[1]]*/!important; } /* Like bar */
#container.ytd-sentiment-bar-renderer { background: /*[[2]]*/!important; } /* Dislike bar */
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1) { color: /*[[3]]*/; } /* Liked button */
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2) { color: /*[[4]]*/; } /* Disliked button */


a.yt-simple-endpoint.yt-formatted-string {
    color: /*[[5]]*/;
}
}
