css:
@-moz-document domain("2ch.hk") {
/*
 -- 2ch.hk dark monochrome theme
*/

:root {

    --background: #000;
    --foreground: #fff;
    
    --color_0: #000;
    --color_1: #111;
    --color_2: #222;
    --color_3: #333;
    --color_4: #444;
    --color_5: #555;
    --color_6: #666;
    --color_7: #777;
    
}

img {
    filter: grayscale(100%) brightness(20%);
}

img:hover {
    filter: none;
}

.thread_wrap, .thread_wrap > .thread {
    background-color: var(--color_1);
    border: 0;
}

.thread_wrap::after {
    background: none;    
}

.post__title {
    color: var(--foreground);
}

.post__title {
    margin-left: 18px;
}

.qr_reply, .qr__header {
    background-color: var(--background);
}

.input, .button {
    background-color: var(--background);
    color: var(--color_5);
    border: 1px solid var(--color_5);
}

.spoiler, .spoiler > a {
    color: var(--color_7);
    background-color: var(--color_7);
}

.spoiler:hover, .spoiler > a:hover {
    color: var(--foreground);
}

.thread::after {
    border-top: 1px solid var(--color_1);
}

.post_type_reply {
    
    background-color: var(--background);
    
    border-radius: 0px;
    border: 0px solid black;
    
    margin: 10px 0px;
    
    width: 100%;
    
    border-top: 1px solid var(--color_1);
    padding-top: 10px;
}

.post__message_op {
    /* min-height: 100px; */
    width: 100%;
}

.post__detailpart .icon {
    fill: var(--color_1);
}

.pr-tags-container > a {
    background-color: var(--color_3);
    color: var(--color_5);
}

.pr-tags-container > a:hover {
    background-color: var(--color_5);
    color: var(--color_7);
}

#SwitchStyles {
    display: none;
}

.input:focus {
    background-color: var(--color_3);
    color: var(--color_5);
    border: 1px solid var(--color_5);
}

input.turnmeoff,
.adminbar__boards {
    display: none;
}

.bb {
    background-color: var(--background);
    border: 1px solid var(--color_5);
}

.na {
    display: none !important;
}

.tti_info {
    color: var(--color_3);
}

a, .a-link-emulator, .pr-link {
    color: var(--color_7);
    text-decoration: underline;
}

a:hover, .a-link-emulator:hover, .pr-link:hover {
    color: var(--foreground);
    text-decoration: underline;
}

hr {
    border-color: var(--background)
}

.post__anon::after {
    background: none;
}

.post_type_reply::before {
    content: '';
}

html, body {
    background-color: var(--background);
    color: var(--color_5)
}

aside,
.adminbar__menu,
.logo {
    display: none;
}

.cntnt {
    justify-content: center;
}

div.cntnt__right {
    max-width: 1280px;
    min-width: 640px;

    margin: 20px;
}
}
