css:
@-moz-document domain("artstation.com") {
/* hide */
.btn-toolbar,
.separator-sm,
.separator,
.list-inline.artwork-count + .separator + div,
.list-inline.artwork-count > li:nth-child(3),
.tags > h4,
.keyboard-shortcuts > hr,
.keyboard-shortcuts > h5,
.commenting-disabled > i,
[ng-if="project.software_items.length > 0"] > .title,
.more-artworks .title,
hello-world,
marketplace-popular-product,
support-artstation-block,
_ {
	display: none !important;
}

/* content */
body {
	line-height: 1.3;
}

.gallery-single .artwork-info-container {
	background: transparent;
}

.h3 {
	margin-bottom: 0;
}

#project-description p {
	font-size: 1.2rem;
	margin-top: 3px;
}

.gallery-container .mosaic {
	margin-bottom: 20px;
}

.current-collections-list {
	border-color: #fff2;
}

/* 

navbar

*/
.form-control,
#search-input {
	background: transparent;
	border: none;
}

.navbar-artist-profile,
.navbar-inverse,
.navbar-default,
_ {
	background: #000D;
	border: none;
}

.navbar-artist-profile.stuck {
	top: 100px;
}
.navbar-artist-user.stuck {
	top: 52px;
}
.navbar-default .navbar-nav > .active > a {
	background: #fffd;
}

/*

sidebar 

*/
#comments {
	height: .5rem;
	overflow: hidden;
	border-bottom: #fff2 solid 1px;
}

#comments:hover {
	height: auto;
	overflow: visible;
}

/* software used */
.software-used {
	width: 100%;
}

.software-used .software {
	width: auto;
}

.software-used .software p,
.software-used .software img,
_ {
	margin: 3px 10px 0 0;
}

/* buttons */
.headline,
.user-follow-btn {
	display: inline-block;
}

.btn-following,
.btn-primary,
.btn-success,
.button-blocks a,
.button-blocks button,
.dropdown-toggle,
.user-follow-btn button,
_ {
	background: transparent;
	border: none;
}

.user-follow-btn {
	position: absolute;
	top: 2px;
	right: 213px;
}

.button-blocks .row {
	padding: 4px 0 0 62px;
	margin-bottom: 10px;
}
.button-blocks .row div {
	width: auto;
}

.btn-sm {
	padding: 0;
}

/* tags */
.tags {
	margin: 0 0 10px -5px;
}

.gallery-single .artwork-info-container .artwork-info .tags a.label {
	background: transparent;
	border: none;
	padding: 0 4px;
	margin: 1px;
}

/* shortcuts */
.artwork-info-container {
	padding-bottom: 45px;
}

.keyboard-shortcuts {
	position: fixed;
	bottom: 0;
	right: 0;
	width: 365px;
	padding: 10px 25px 10px 20px;
	background: #000;
}

.gallery-single .artwork-info-container .artwork-info .keyboard-shortcuts ul.shortcuts li,
.shortcuts .label {
	border: none;
	padding: 0;
	font-size: 1.1rem;
}

.gallery-single .artwork-info-container .project-comments-form,
.gallery-single .artwork-info-container .project-comments-title,
_ {
	border: none;
	background: none;
}
}
