@-moz-document domain("hattrick.org"){

	
.smallText a
	{
	border : 0px !important
	}

/*linkit*/	
a
	{
	color : #d09400 !important;
	}

a:hover
	{
	color: #000000 !important;
	}	
	
/* etsi yms laatikot */	
table.search,.ok
	{
	border:solid 1px #cccccc !important;
	background-color:#fcf6df !important;
	}

/* laatikoita */
.error,.alert,.edit,.threadNote,.alert_shy,.searchCloud,.kitEdit,.kitDescription
	{
	border:solid 1px #cccccc !important;
	}

/* sivun alaosa (tietoja hattrickista yms) */
#footer 
	{
	visibility : hidden !important;
	}

/*teamilinkit vasen ylÃ¤kulma*/	
#teamLinks
	{
	position : absolute !important;
	top : 120px !Important;
	right : 15px !important;
	}
	
/*tickeri*/
#ticker 
	{
	position : absolute !Important;
	top : 0px !Important;
	margin : 0px !important;
	padding : 0px !Important;
	left :  150px !important;
	}

#ticker div a
	{
	line-height : 15px !important;
	background : none !important;
	color: #ffbc20 !important;
	text-decoration: none !important;
	}

/* poistaa tickerista tÃ¤hdet edestÃ¤ */
#ticker div
	{
	background : none !Important;
	}

/* kÃ¤yttÃ¤jÃ¤t online ja aika teksti */			
#online, #time
	{	
	margin : 0px !important;
	padding : 0px !important;
	position : absolute !important;
	right: 15px !important;
	color: #ffbc20 !important;	
	}
	
#online
	{
	top : 0px !important;	
	}
	
#time
	{
	top : 20px !Important;
	}

/* tausta */
body
	{  
	background: #ffbc20 url('http://i608.photobucket.com/albums/tt168/Koura69/juventustausta.png') top left repeat !important;
	}

/* etusivun hattrickin tiedotteet teksti*/
h1 
	{
    color: #ffbc20 !important;
	}

/*etusivun uutisten otsikot*/
.boxBody h2 
	{ 
    color: #000000 !important;
    border-bottom: solid 1px #cccccc !important;
	}


.sidebarBox .boxHead .boxLeft,.subMenuBox .boxHead .boxLeft,.main .boxHead .boxLeft{background:#000000 !important;}

/* Left menu */
.subMenuBox .boxHead
	{
	background : #000000 !important;
	}

.subMenuBox .boxHead .boxLeft
	{
	background : #000000 !important;
	}

/*Centre box*/
.main .boxHead h2 
	{
	background: #000000 !important;
	}

/*Right menu*/
.sidebarBox h2 
	{
	background : #000000 !important;
	}

	
/* Keskilaatikon titlelinkit */
h2 a 
	{
    color: #ffffff !important;
	}

h2 a:hover 
	{
    color:#ffbc20 !important;
	}

/* pÃ¤ivÃ¤mÃ¤Ã¤rÃ¤t esim. etusivun uutisissa */
.date
	{
	color: #666666 !important;
	font-weight: 400;
	font-size : 11px;
	}

/* vasemman laatikon otsikot: hattrick, yhteisÃ¶ */
h3 
	{
    color: #ffbc20 !important;
	background: #ffffff !important;
	}

/* Linkit vasemassa ja oikeassa laatikossa */
.subMenuBox a, .sidebarBox a 
	{
	background : none !important;
	color: #000000 !important;
	}
	
.subMenuBox a
	{
	text-decoration: none !important;
	}

.sidebarBox a 
	{
	text-decoration: underline !important;
	}

.subMenuBox li a:before
	{
	content : "Â» " !important;
	}
		
.subMenuBox a:hover, .sidebarBox a:hover
	{
	background : none !important;
	color: #ffbc20 !important;
	}

.subMenuBox a:hover
	{
	text-decoration: none !important;
	}
	
.sidebarBox a:hover
	{
	text-decoration: underline !important;
	}
	
	
/* Botti tiimien linkit sarjataulukossa */
div a.shy
	{
    color: #cccccc !important;
	}


/* sivuston ylÃ¤osa top.png */
#top
	{
	background : #000000 url('http://i608.photobucket.com/albums/tt168/Koura69/juventustoptausta.png') top left no-repeat !important;
	}
	
/* headeri: logo, ticker, teamlinks, shortcuts, time, online, menu */
#header
	{
	background : #000000 url('http://i608.photobucket.com/albums/tt168/Koura69/juventusheadertausta.png') no-repeat !important;
	height : 187px !important;
	padding : 0px !important;
	border-left : 1px solid #000000 !important;
	border-right : 1px solid #000000 !important;
	}
	
/* hattrickin logo */
#logo
	{
	background : none !important;
	}

/* logout nappula */
.logout
	{
	position : absolute !Important;
	}

/* keskilaatikko ja oikeanpuoleinen laatikko */
#content
	{
	padding-top : 15px !important;
	background : #000000 !important;
	}

/* menu: hattrick, seura yms. */
#menu
	{
	border-top : 1px solid #000000 !Important;
	border-bottom :1px solid #000000 !important;
	width : 100% !important;
	bottom : 0px !Important;
	left : 0px !Important;
	padding : 0px !Important;
	margin : 0px !Important;
    background : #ffbc20 !important;
	}

#menu a
	{
    color: #000000 !important;
	}

div#menu a:hover 
	{
	color : #ffbc20 !important;
	background-color : #000000 !important;
	}

/*sivuston alareunan kuva */
#bottom
	{
	background : #000000 !important;
	}

/* Foorumi*/
.cfHeader
	{
    background: #000000 !important;
	}

.cfFooter
	{
	background : #000000 !important;
	}
	
.cfFooter a
	{
	color : #ffffff !important;
	}
	
#cfPollHeader
	{
	background: #000000 !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
	}

.cfWrapper
	{
	border: solid 1px #000000 !important;
	}

.cfHeader a
	{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
	}

.cfWrapper a
	{
	text-decoration: none;
	}

.folderHeader a:link 
	{
	color: #ffbc20 !important;
	font-weight: bold !important;
	}

/* oikotiet */	
#shortcuts
	{
	z-index : 9999 !important;
	position : absolute !important;
	top : 158px !Important;
	}


/* muokkaa oikoteitÃ¤ nappi */
#scSettingsLink a
	{
	font-weight : bold !important;
	font-size : 10px !important;
	color : #000000 !important;
	}
	
#scSettingsLink a:hover
	{
	color : #ffffff !important;
	}
	
		
/* MatchLineup */
.name a
	{
	color: #000000 !important;
	}
	
.name a:hover
	{
	color: #ffbc20 !important;
	}


/* Mainokset sivu */
.HTinsert
	{
	border : 1px solid #000000 !important;
	}
	
div[class="insertHeader"]
	{
    background: #ffbc20 !important;
	}

div[class="insertFooter"]
	{
    background: #ffffff !important;
	}
	
div.insertFooter a, div.insertHeader a
	{
	color : #ffffff !important;
	}
	
div.insertFooter a:hover, div.insertHeader a:hover
	{
	color : #000000 !important;
	}
	
	
	
/* Foxtrick pop-up menu */

.myht1 tr, .myht2 tr
	{
	height : 12px !important;
	}

.myht1 td, .myht2 td
	{
	padding: 2px 7px 2px 7px !important;
	}
		
.myht1, .myht2
	{
	font-size : 11px !important;
	}

span.myht1:hover div.myht2
	{
	position: absolute;
	left: 30px !important;
	background-color: #000000 !important;
	border: solid 2px #ffbc20 !important;
	z-index: 99999;
	top : 15px !important;
	}

span.myht1 table>tr>td>a
	{
	text-decoration: underline !important;
	color: #ffffff !important;
	}

span.myht1 table>tr>td>a:hover
	{
	text-decoration: underline !important;
	color: #ffbc20 !important;
	}
		
span.myht1 table>tr>td:hover
	{
	background-color:#000000 !important;
	}

	
}
