css:
@-moz-document domain("git-awards.com") {

body {
  background-color: #282828;
  color: #aaa;
}

/***** Main Page *****/

/*** Top Bar ***/

.navbar-default {
  background-color: #282828;
  border-color: #333;
}

.navbar-default .navbar-brand {
  color: #aaa;
}

.navbar-default .navbar-nav > li > a {
  color: #aaa;
}

.navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > li > a:hover {
  color: #ccc;
}

/* Search box */
.form-control {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

/* 'Search' button */
.btn-default {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.btn-default.active, .btn-default.focus, .btn-default:active, .btn-default:focus, .btn-default:hover, .open > .dropdown-toggle.btn-default {
  background-color: #333;
  border-color: #333;
  color: #aaa;
}

/*** Warning Message ***/
.alert-danger { /* from LearnOpenGL Dark */
  background-color: #380806;
  border-color: #320705;
  color: #aaa;
}

/*** Middle Region ***/

/* Bkgd img */
.intro {
  filter: invert(90%);
}

/* 'Discover your ranking on GitHub' */
.intro label, .intro .form-control {
  filter: invert(100%);
}

.intro .btn-default {
  filter: invert(100%);
}


/***** Top users by Country - git-awards.com/users?country=<query> *****/

/* Country dropdown */
.ui-widget-content {
  background-color: #282828;
  background-image: none;
  border-color: #333;
  color: #aaa;
}

/** Table **/

/* Separator */

.table > thead > tr > th {
  border-bottom-color: #444;
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
  border-top-color: #333;
}

/* Icon */
.thumbnail {
  opacity: 0.5;
  background-color: #282828;
  border-color: #333;
}

/** Page No.s **/

.pagination > li > a, .pagination > li > span {
  background-color: #282828;
  border-color: #333;
}

.pagination > li > a:focus, .pagination > li > a:hover, .pagination > li > span:focus, .pagination > li > span:hover {
  color: #337ab7; /* from without hover */
  background-color: #333;
  border-color: #333;
}

.pagination > .disabled > a, .pagination > .disabled > a:focus, .pagination > .disabled > a:hover, .pagination > .disabled > span, .pagination > .disabled > span:focus, .pagination > .disabled > span:hover {
  background-color: #282828;
  border-color: #333;
}


/***** User Page - git-awards.com/users/<user> *****/

/* Separator */
hr {
  border-top-color: #333;
}

/* Refresh */
.btn-success {
  color: #aaa;
  background-color: #195b19;
  border-color: #134613;
}

/* 'Welcome <user>, your data is updating' on click of Refresh */
.alert-success { /* from LearnOpenGL Dark */
  color: #aaa;
  background-color: #0b290b;
  border-color: #092409;
}

/***** About Page - git-awards.com/about *****/

.panel-default {
  border-color: #333;
}

/* Header */
.panel-default > .panel-heading {
  color: #aaa;
  background-color: #333;
  border-color: #333;
}

/* Content */

.panel-default > .panel-heading + .panel-collapse > .panel-body {
  border-top-color: #333;
}

.panel {
  background-color: #282828;
}

/* Code Snippet */
pre {
  color: #aaa;
  background-color: #282828;
  border-color: #333;
}

}
