css:
@-moz-document domain(4chan.org), domain(4channel.org) {
    html {
        --body-color: #333437;
        --reply-color: #4C4E56;
        --text-color: #EAEAEA;
        --hyperlink-color: #3498db;
        --thread-subject-color: #918FC9;
        --post-meta-color: rgba(255,255,255,.7);
        --thread-hover: #C4C3E4;
        --quote-link: #E32626;
        --poster-name: #43A047;
        --reply-hover: #5C5059;
        --banner-color: #C3393D;
        --mod-color: #C060C0;
        --accent-color: #212121;

        background: var(--body-color);
    }

    body {
        color: var(--text-color);
        background: var(--body-color);
    }

    div.reply {
        background-color: var(--reply-color);
        border: none;
    }

    div.post blockquote.postMessage {
        color: var(--text-color);
        display: block;
    }

    span.subject {
        color: var(--thread-subject-color);
    }

    div.post div.postInfo span.postNum a {
        color: var(--post-meta-color);
    }

    span.subject {
        color: var(--thread-subject-color);
    }

    a.replylink:not(:hover), div#absbot a:not(:hover) {
        color: var(--thread-subject-color) !important;
    }

    div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover {
        color: var(--thread-hover);
    }
    span.postNum.desktop > span > a.replyLink:hover {
        color: var(--thread-hover);
    }

    div.post div.postInfo span.subject {
        color: var(--thread-subject-color);
    }

    .fileText a {
        color: var(--hyperlink-color);
    }

    span.dateTime {
        color: var(--post-meta-color);
    }

    .fileText {
        color: var(--post-meta-color);
    }

    .quoteLink,.quotelink,.deadlink {
        color: var(--quote-link);
        text-decoration: underline
    }

    div.post div.postInfo span.nameBlock span.name {
        color: var(--poster-name);
        font-weight: 500;
    }

    a, a:visited {
        color: var(--hyperlink-color);
        text-decoration: none
    }

    .burichan_new .backlink a, .yotsuba_b_new .backlink a {
        color: var(--thread-subject-color) !important;
    }

    div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover {
        color: var(--thread-hover) !important;
    }

    .burichan_new .backlink a:hover, .yotsuba_b_new .backlink a:hover {
        color: var(--thread-hover) !important;
    }

    .reply:target, .reply.highlight {
        background: var(--reply-hover) !important;
        border: none !important;
        padding: 2px;
    }

    hr {
        width: 95%;
        border-top: 1px solid #272A2F;
    }

    div#boardNavDesktop a {
        color: var(--hyperlink-color);
    }

    .boardTitle {
        color: var(--banner-color);
    }

    .nameBlock.capcodeMod span.name, span.capcodeMod a span.name, span.capcodeMod span.postertrip, span.capcodeMod strong.capcode {
        color: var(--mod-color) !important;
    }

    blockquote.postMessage > a.quoteLink, blockquote.postMessage > a.quoteLink:hover {
        color: #E32626;
    }

    .quoteLink,.quotelink,.deadlink {
        color: #E32626 !important;
        text-decoration: underline
    }

    .preview {
        border-radius: 5px;
        background-color: #808290;
        border: 1px solid #808290 !important;
        /*border-right-width: 2px!important;
        border-bottom-width: 2px!important;*/
    }

    div#boardNavDesktopFoot a {
        font-weight: 400;
        padding: 1px;
        text-decoration: none;
        color: var(--hyperlink-color);
    }

    div.navLinks.navLinksBot.desktop {
        color: #eee;
    }

    body > div.navLinks.desktop {
        color: #eee;
    }

    div.board > div.thread {
        margin-left: 55px;
        margin-right: 55px;
    }

    div.thread > div:nth-of-type(2) > div.reply {
        margin-top: 16px;
    }

    div.post {
        margin: 16px 0;
    }

    div.post div.file .fileThumb {
        margin-bottom: 10px;
        margin-top: 15px;
    }

    div.post.op > div.postInfo.desktop {
        padding-top: 15px;
        background: var(--reply-color);
    }

    div.post.op > blockquote.postMessage {
        padding-bottom: 10px;
        padding-left: 15px;
        margin: 0;
        background: var(--reply-color);
    }

    div.op {
        background-color: var(--reply-color);
        display: block;
    }

    div.sideArrows {
        display: none;
    }

    div.post.op > div.file > div.fileText {
        margin-top: 5px;
    }

    div.boardBanner>div.boardTitle {
        font-size: 40px;
        letter-spacing: 0;
    }
    .boardTitle {
        color: #ecf0f1;
    }

    .globalMessage {
        display: none;
    }

    table#blotter {
        display: none;
    }

    body > hr {
        display: none;
    }

    a:hover {
        color: var(--hyperlink-color) !important;
    }

    input#search-box {
        border: transparent;
        display: block;
        margin: auto;
        margin-bottom: 5px;
        padding-top: 2px;
        background-color: var(--accent-color);
    }

    div#ctrl-top {
        text-align: center;
        color: var(--text-color);
    }

    div.postContainer > div.post {
        border-radius: 5px;
    }

    img[alt="H"] {
        margin-top: 3px  !important;
        margin-left: 3px;
    }

    div.file > div.fileText {
        margin: 3px 0.5ex;
    }

    div.bottomCtrl.desktop {
        color: #eee;
    }

    span.postNum.desktop {
        color: var(--text-color);
    }

    img#toggleMsgBtn {
        margin-left: 55px;
    }

    div.threat-stats {
        margin-right: 55px;
    }

    a, .button {
        color:  var(--thread-subject-color);
    }

    div#popular-threads.box-outer.top-box {
        background: var(--reply-color);
    }

    .box-outer {
        border: none;
    }

    #boards a {
        color: var(--hyperlink-color);
        text-decoration: none;
    }

    .c-thread img {
        border: none;
    }

    .boxbar {
        background: var(--accent-color);
        color: var(--post-meta-color);
    }

    #ft li, #ft li.current, #ft li.fill {
        background: var(--body-color);
    }

    #ft li a {
        color: var(--hyperlink-color);
    }

    #ft li.current, #ft li.fill {
        background: transparent;
        color: transparent;
    }

    #ft li {
        color: var(--body-color);
        border: none;
    }

    #ft ul {
        border: none;
    }
    .top-box {
        background: transparent;
    }

    #fp-menu {
        background: var(--reply-color);
    }

    #fp-menu li:hover {
        background: var(--thread-hover);
        color: #222;
    }

    #disclaimer-dialog {
        border: none;
        box-shadow: none;
        background: var(--reply-color);
    }

    #disclaimer-dialog .hd {
        background: var(--accent-color);
    }
}
