css:
@-moz-document domain("paradoxwikis.com") {
h1,h2,h3,h4,h5,h6 {
    color: #f2f2f2
}

hr {
    color: #f00
}

.editOptions {
    background-color: #1b2836;
    border-color: #19ff24
}
/* i really want this to be global */
input,textarea {
    background-color: #000;
    border-color: #19ff24;
    color: #f2f2f2
}

a,.mw-body a.external,.mw-body a.extiw {
    color: #04cece
}

a:visited,.mw-body a.external:visited,.mw-body a.extiw:visited {
    color: #8badc4
}

a.new,#p-personal a.new {
    color: #ce0606
}

ul {
    list-style-image: none
}

div.thumbinner,.catlinks {
    background-color: #000;
    border-color: #666
}

#toc,div.toc,ul#filetoc,li.gallerybox div.thumb {
    background-color: #000;
    border-color: #666
}

code {
    border: none;
    background-color: inherit;
    color: inherit
}

table.mw_metadata td,table.mw_metadata th,table.wikitable,table.wikitable>*>tr>th,table.wikitable>*>tr>td,pre {
    color: #f2f2f2;
    background-color: #000;
    border-color: #666
}

table.mw_metadata th,table.wikitable>*>tr>th {
    background-color: #595959
}

table.diff {
    background-color: #000
}

td .diffchange {
    color: #19ff24
}

#pagehistory li.selected,td.diff-context {
    background-color: inherit;
    color: inherit
}

#pagehistory li {
    border: 0
}

ul.mw-gallery-packed-hover li.gallerybox:hover div.gallerytextwrapper,ul.mw-gallery-packed-overlay li.gallerybox div.gallerytextwrapper,ul.mw-gallery-packed-hover li.gallerybox.mw-gallery-focused div.gallerytextwrapper {
    background-color: rgba(0,0,0,0.8)
}

#left-navigation div.vectorTabs,#right-navigation div.vectorTabs {
    background-image: inherit;
    background-color: #250b2d
}

#left-navigation div.vectorTabs ul,#right-navigation div.vectorTabs ul {
    background-color: #250b2d
}

#left-navigation div.vectorTabs ul li,#right-navigation div.vectorTabs ul li {
    background-color: #222;
    background-image: -moz-linear-gradient(top,#2e2e2e 20%,#222 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(20%,#2e2e2e),color-stop(100%,#222));
    background-image: -webkit-linear-gradient(top,#2e2e2e 20%,#222 100%);
    background-image: linear-gradient(#2e2e2e 20%,#222 100%)
}

#left-navigation div.vectorTabs li.new a,#right-navigation div.vectorTabs li.new a,#left-navigation div.vectorTabs li.new a:visited,#right-navigation div.vectorTabs li.new a:visited {
    color: #d44
}

#left-navigation div.vectorTabs li.selected,#right-navigation div.vectorTabs li.selected {
    background-color: #1b2836;
    background-image: -moz-linear-gradient(top,#04cece 0,#1b2836 10%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#04cece),color-stop(10%,#1b2836));
    background-image: -webkit-linear-gradient(top,#04cece 0,#1b2836 10%);
    background-image: linear-gradient(#04cece 0,#1b2836 10%)
}

#left-navigation div.vectorTabs li.selected a,#right-navigation div.vectorTabs li.selected a,#left-navigation div.vectorTabs li.selected a:visited,#right-navigation div.vectorTabs li.selected a:visited {
    color: #8badc4
}

#left-navigation div.vectorTabs li a,#right-navigation div.vectorTabs li a {
    color: #04cece
}

#left-navigation div.vectorTabs span,#right-navigation div.vectorTabs span {
    background: transparent
}

div#mw-head #right-navigation div.vectorMenu h3 {
    background: inherit
}

div#mw-head div.vectorMenu h3 span {
    color: #04cece
}

div.vectorMenu h3 a {
    background: inherit
}

div.vectorMenu ul {
    background-color: #141d26;
    border: solid 1px #0c0c0c
}

div.vectorMenu li a {
    color: #04cece
}

div.vectorMenu li.selected a,div.vectorMenu li.selected a:visited {
    color: #8badc4;
    text-decoration: none
}

div.vectorMenu#p-cactions div.menu {
    border-color: #666
}

div.vectorMenu#p-cactions ul {
    border: 0;
    background-color: #000
}

#mw-navigation div#mw-panel div.portal {
    background-image: none;
    border-top: 1px solid #19ff24
}

#mw-navigation div#mw-panel div.portal#p-logo,#mw-navigation div#mw-panel div.portal#p-navigation {
    border-top: none
}

html {
    font-size: 100%
}

html,body {
    height: 100%;
    margin: 0;
    padding: 0;
    font-family: sans-serif
}

body {
    background-color: #141d26
}

.mw-body {
    margin-left: 10em;
    padding: 1em;
    border: 1px solid #250b2d;
    border-right-width: 0;
    margin-top: -1px;
    background-color: #1b2836;
    color: #f2f2f2;
    direction: ltr
}

.mw-body .mw-editsection,.mw-body .mw-editsection-like {
    font-family: sans-serif
}

.mw-body p {
    line-height: inherit;
    margin: .5em 0
}

.mw-body h1,.mw-body h2,.mw-body #firstHeading {
    font-family: "Linux Libertine",Georgia,Times,serif;
    line-height: 1.3;
    margin-bottom: .25em;
    padding: 0
}

.mw-body h1,.mw-body #firstHeading {
    font-size: 1.8em
}

.mw-body h2 {
    font-size: 1.5em;
    margin-top: 1em
}

.mw-body h3,.mw-body h4,.mw-body h5,.mw-body h6 {
    line-height: 1.6;
    margin-top: .3em;
    margin-bottom: 0;
    padding-bottom: 0
}

.mw-body h3 {
    font-size: 1.17em
}

.mw-body h3,.mw-body h4 {
    font-weight: bold
}

.mw-body h4,.mw-body h5,.mw-body h6 {
    font-size: 100%
}

.mw-body #toc h2,.mw-body .toc h2 {
    font-size: 100%;
    font-family: sans-serif
}

div.emptyPortlet {
    display: none
}

ul {
    list-style-type: disc;
}

pre,.mw-code {
    line-height: 1.3em
}

#siteNotice {
    font-size: .8em
}

.redirectText {
    font-size: 140%
}

.redirectMsg img {
    vertical-align: text-bottom
}

.mw-body-content {
    position: relative;
    line-height: 1.6;
    font-size: .875em
}

body.vector-animateLayout .mw-body,body.vector-animateLayout div#footer,body.vector-animateLayout #left-navigation {
    -webkit-transition: margin-left 250ms,padding 250ms;
    -moz-transition: margin-left 250ms,padding 250ms;
    -o-transition: margin-left 250ms,padding 250ms;
    transition: margin-left 250ms,padding 250ms
}

body.vector-animateLayout #p-logo {
    -webkit-transition: left 250ms;
    -moz-transition: left 250ms;
    -o-transition: left 250ms;
    transition: left 250ms
}

body.vector-animateLayout #mw-panel {
    -webkit-transition: padding-right 250ms;
    -moz-transition: padding-right 250ms;
    -o-transition: padding-right 250ms;
    transition: padding-right 250ms
}

body.vector-animateLayout #p-search {
    -webkit-transition: margin-right 250ms;
    -moz-transition: margin-right 250ms;
    -o-transition: margin-right 250ms;
    transition: margin-right 250ms
}

body.vector-animateLayout #p-personal {
    -webkit-transition: right 250ms;
    -moz-transition: right 250ms;
    -o-transition: right 250ms;
    transition: right 250ms
}

body.vector-animateLayout #mw-head-base {
    -webkit-transition: margin-left 250ms;
    -moz-transition: margin-left 250ms;
    -o-transition: margin-left 250ms;
    transition: margin-left 250ms
}

#p-personal {
    position: absolute;
    top: .33em;
    right: .75em;
    z-index: 100
}

#p-personal h3 {
    display: none
}

#p-personal ul {
    list-style-type: none;
    list-style-image: none;
    margin: 0;
    padding-left: 10em
}

#p-personal li {
    line-height: 1.125em;
    float: left;
    margin-left: .75em;
    margin-top: .5em;
    font-size: .75em;
    white-space: nowrap
}

#pt-userpage,#pt-anonuserpage {
    background-position: left top;
    background-repeat: no-repeat;
    padding-left: 15px !important
}

#p-search {
    float: left;
    margin-right: .5em;
    margin-left: .5em
}

#p-search h3 {
    display: none
}

#p-search form,#p-search input {
    margin: 0;
    margin-top: .4em
}

div#simpleSearch {
    display: block;
    width: 12.6em;
    padding-right: 1.4em;
    height: 1.4em;
    margin-top: .65em;
    position: relative;
    min-height: 1px;
    border: solid 1px #aaa;
    color: white;
    background-color: #000;
    background-position: top left;
    background-repeat: repeat-x
}

div#simpleSearch input {
    margin: 0;
    padding: 0;
    border: 0;
    background-color: #000;
    color: white
}

div#simpleSearch #searchInput {
    width: 100%;
    padding: .2em 0 .2em .2em;
    font-size: 13px;
    direction: ltr;
    -webkit-appearance: textfield
}

div#simpleSearch #searchInput:focus {
    outline: none
}

div#simpleSearch #searchInput.placeholder {
    color: #999
}

div#simpleSearch #searchInput:-ms-input-placeholder {
    color: #999
}

div#simpleSearch #searchInput:-moz-placeholder {
    color: #999
}

div#simpleSearch #searchInput::-webkit-search-decoration,div#simpleSearch #searchInput::-webkit-search-cancel-button,div#simpleSearch #searchInput::-webkit-search-results-button,div#simpleSearch #searchInput::-webkit-search-results-decoration {
    -webkit-appearance: textfield
}

div#simpleSearch #searchButton,div#simpleSearch #mw-searchButton {
    position: absolute;
    top: 0;
    right: 0;
    width: 1.65em;
    height: 100%;
    cursor: pointer;
    text-indent: -99999px;
    line-height: 1;
    direction: ltr;
    white-space: nowrap;
    overflow: hidden;
    background-position: center center;
    background-repeat: no-repeat
}

div#simpleSearch #mw-searchButton {
    z-index: 1
}

div.vectorTabs h3 {
    display: none
}

div.vectorTabs {
    float: left;
    height: 2.5em;
    background-position: bottom left;
    background-repeat: no-repeat;
    padding-left: 1px
}

div.vectorTabs ul {
    float: left;
    height: 100%;
    list-style-type: none;
    list-style-image: none;
    margin: 0;
    padding: 0;
    background-position: right bottom;
    background-repeat: no-repeat
}

div.vectorTabs ul li {
    float: left;
    line-height: 1.125em;
    display: inline-block;
    height: 100%;
    margin: 0;
    padding: 0;
    background-color: #f3f3f3;
    background-position: bottom left;
    background-repeat: repeat-x;
    white-space: nowrap
}

div.vectorTabs ul>li {
    display: block
}

div.vectorTabs li.new a,div.vectorTabs li.new a:visited {
    color: #a55858
}


div.vectorTabs li.selected a,div.vectorTabs li.selected a:visited {
    color: #1b2836;
    text-decoration: none
}

div.vectorTabs li.icon a {
    background-position: bottom right;
    background-repeat: no-repeat
}

div.vectorTabs li a {
    display: inline-block;
    height: 1.9em;
    padding-left: .5em;
    padding-right: .5em;
    color: #04cece;
    cursor: pointer;
    font-size: .8em
}

div.vectorTabs li>a {
    display: block
}

div.vectorTabs span {
    display: inline-block;
    background-position: bottom right;
    background-repeat: no-repeat
}

div.vectorTabs span a {
    display: inline-block;
    padding-top: 1.25em
}

div.vectorTabs span>a {
    float: left;
    display: block
}

div.vectorMenu {
    direction: ltr;
    float: left;
    cursor: pointer;
    position: relative
}

body.rtl div.vectorMenu {
    direction: rtl
}

div#mw-head div.vectorMenu h3 {
    float: left;
    background-repeat: no-repeat;
    background-position: bottom right;
    margin-left: -1px;
    font-size: 1em;
    height: 2.5em;
    padding-right: 1px;
    margin-right: -1px
}

div.vectorMenu h3 span {
    display: block;
    font-size: .8em;
    padding-left: .7em;
    padding-top: 1.375em;
    margin-right: 20px;
    font-weight: normal;
    color: #fff
}

div.vectorMenu h3 a {
    position: absolute;
    top: 0;
    right: 0;
    width: 20px;
    height: 2.5em;
    background-position: 100% 70%;
    background-repeat: no-repeat;
    -webkit-transition: background-position 250ms;
    -moz-transition: background-position 250ms;
    -o-transition: background-position 250ms;
    transition: background-position 250ms
}

div.vectorMenu.menuForceShow h3 a {
    background-position: 100% 100%
}


div.vectorMenu div.menu {
    min-width: 100%;
    position: absolute;
    top: 2.5em;
    left: -1px;
    background-color: #141d26;
    border: solid 1px silver;
    border-top-width: 0;
    clear: both;
    text-align: left;
    display: none
}

div.vectorMenu:hover div.menu,div.vectorMenu.menuForceShow div.menu {
    display: block
}

div.vectorMenu ul {
    list-style-type: none;
    list-style-image: none;
    padding: 0;
    margin: 0;
    text-align: left
}

div.vectorMenu ul,x:-moz-any-link {
    min-width: 5em
}

div.vectorMenu ul,x:-moz-any-link,x:default {
    min-width: 0
}

div.vectorMenu li {
    padding: 0;
    margin: 0;
    text-align: left;
    line-height: 1em
}

div.vectorMenu li a {
    display: inline-block;
    padding: .5em;
    white-space: nowrap;
    color: #04cece;
    cursor: pointer;
    font-size: .8em
}

div.vectorMenu li>a {
    display: block
}

div.vectorMenu li.selected a,div.vectorMenu li.selected a:visited {
    color: #1b2836;
    text-decoration: none
}

@-webkit-keyframes rotate {
    from {
        -webkit-transform: rotate(0deg);
        -moz-transform: rotate(0deg);
        transform: rotate(0deg)
    }

    to {
        -webkit-transform: rotate(360deg);
        -moz-transform: rotate(360deg);
        transform: rotate(360deg)
    }
}

@-moz-keyframes rotate {
    from {
        -webkit-transform: rotate(0deg);
        -moz-transform: rotate(0deg);
        transform: rotate(0deg)
    }

    to {
        -webkit-transform: rotate(360deg);
        -moz-transform: rotate(360deg);
        transform: rotate(360deg)
    }
}

@-o-keyframes rotate {
    from {
        -webkit-transform: rotate(0deg);
        -moz-transform: rotate(0deg);
        transform: rotate(0deg)
    }

    to {
        -webkit-transform: rotate(360deg);
        -moz-transform: rotate(360deg);
        transform: rotate(360deg)
    }
}

@keyframes rotate {
    from {
        -webkit-transform: rotate(0deg);
        -moz-transform: rotate(0deg);
        transform: rotate(0deg)
    }

    to {
        -webkit-transform: rotate(360deg);
        -moz-transform: rotate(360deg);
        transform: rotate(360deg)
    }
}

#ca-unwatch.icon a,#ca-watch.icon a {
    margin: 0;
    padding: 0;
    display: block;
    width: 26px;
    padding-top: 3.1em;
    margin-top: 0;
    height: 0;
    overflow: hidden;
    background-position: 5px 60%
}




#ca-unwatch.icon a.loading,#ca-watch.icon a.loading {
    -webkit-animation: rotate 700ms infinite linear;
    -moz-animation: rotate 700ms infinite linear;
    -o-animation: rotate 700ms infinite linear;
    animation: rotate 700ms infinite linear;
    outline: none;
    cursor: default;
    pointer-events: none;
    background-position: 50% 60%;
    -webkit-transform-origin: 50% 57%;
    transform-origin: 50% 57%
}

#ca-unwatch.icon a span,#ca-watch.icon a span {
    display: none
}

#mw-navigation h2 {
    position: absolute;
    top: -9999px
}

#mw-page-base {
    height: 5em;
    background-position: bottom left;
    background-repeat: repeat-x;
    background-color: #141d26;
    background-image: -moz-linear-gradient(top,#1b2836 50%,#141d26 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(50%,#1b2836),color-stop(100%,#141d26));
    background-image: -webkit-linear-gradient(top,#1b2836 50%,#141d26 100%);
    background-image: linear-gradient(#1b2836 50%,#141d26 100%);
    background-color: #1b2836
}

#mw-head-base {
    margin-top: -5em;
    margin-left: 10em;
    height: 5em
}

div#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%
}

div#mw-head h3 {
    margin: 0;
    padding: 0
}

#left-navigation {
    float: left;
    margin-left: 10em;
    margin-top: 2.5em;
    margin-bottom: -2.5em;
    display: inline
}

#right-navigation {
    float: right;
    margin-top: 2.5em
}

#p-logo {
    position: absolute;
    top: -160px;
    left: 0;
    width: 10em;
    height: 160px
}

#p-logo a {
    display: block;
    width: 10em;
    height: 160px;
    background-repeat: no-repeat;
    background-position: center center;
    text-decoration: none
}

div#mw-panel {
    font-size: inherit;
    position: absolute;
    top: 160px;
    padding-top: 1em;
    width: 10em;
    left: 0
}

div#mw-panel div.portal {
    margin: 0 .6em 0 .7em;
    padding: .25em 0;
    direction: ltr;
    background-position: top left;
    background-repeat: no-repeat;
}

div#mw-panel div.portal h3 {
    font-size: .75em;
    color: #fff;
    font-weight: normal;
    margin: 0;
    padding: .25em 0 .25em .25em;
    cursor: default;
    border: none
}

div#mw-panel div.portal div.body {
    margin: 0 0 0 1.25em;
    padding-top: 0
}

div#mw-panel div.portal div.body ul {
    list-style-type: none;
    list-style-image: none;
    margin: 0;
    padding: 0
}

div#mw-panel div.portal div.body ul li {
    line-height: 1.125em;
    margin: 0;
    padding: .25em 0;
    font-size: .75em;
    word-wrap: break-word
}

div#mw-panel div.portal div.body ul li a {
    color: #04cece
}

div#mw-panel div.portal div.body ul li a:visited {
    color: #8badc4
}
.mildtable th {
    background-color: #595959;
    color: #f2f2f2;
}
div#mw-panel div.portal.first {
    background-image: none;
    margin-top: 0
}

div#mw-panel div.portal.first h3 {
    display: none
}

div#mw-panel div.portal.first div.body {
    margin-left: .5em
}

div#footer {
    margin-left: 10em;
    margin-top: 0;
    padding: .75em;
    direction: ltr
}

div#footer ul {
    list-style-type: none;
    list-style-image: none;
    margin: 0;
    padding: 0
}

div#footer ul li {
    margin: 0;
    padding: 0;
    padding-top: .5em;
    padding-bottom: .5em;
    color: #fff;
    font-size: .7em
}

div#footer #footer-icons {
    float: right
}

div#footer #footer-icons li {
    float: left;
    margin-left: .5em;
    line-height: 2em;
    text-align: right
}

div#footer #footer-info li {
    line-height: 1.4em
}

div#footer #footer-places li {
    float: left;
    margin-right: 1em;
    line-height: 2em
}

body.ltr div#footer #footer-places {
    float: left
}

.mw-body .external {
    background-position: center right;
    background-repeat: no-repeat;
    padding-right: 13px
}

.oo-ui-iconElement .oo-ui-iconElement-icon {
    filter: invert(100%)
}

body.page-Main_Page.action-view h1.firstHeading,body.page-Main_Page.action-submit h1.firstHeading {
    display: none
}

#left-navigation div.vectorTabs ul li, #right-navigation div.vectorTabs ul li {
    background-color: #16222f;
    border-style: solid;
    border-width: 1px 1px 0px 0px;
    border-color: #0d141c;
    background-image: none;
}
kbd{
    color: #010101;
    font-family: monospace,Courier;
}
#p-logo a {
    background-size: 57%;
}
td {
    background-color: transparent !important;
}
#mw-content-text > div:nth-last-child(1) div{
    background-color: #141d26 !important;
}
#mf-content {
    border:none !important;
    background: none !important;   
}
.mw-body h2 {
    font-family: arial !important;
}
#mf-content h2{
    font-weight: bold;
}
.header {
    margin-bottom: 20px;
}

dl ~ p{
    font-weight: bold;
}

dd{
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: stretch;
}

dd a{
  flex: 0 0 86%;
  margin-bottom: 10px;
}
p{
    line-height: 2.1 !important;
    font-family: sans-serif;
    color: #d0cdcd;
    font-size: 1.06em;
}
}
