css:
@-moz-document domain("aimware.net") {
body {
    background-color: #333;
    color: #333000;
    border: none;
}

#header {
    background-color: #444;
    border: none;
    margin-top: 19px;
}

#top_header {
    background-color: #333;
    border-top: 1px solid #333;
    border-bottom: 1px solid #333;
}

#footer,
#footer .upper,
#footer .lower {
    background-color: #333;
    color: #fff;
}

#header #logo,
#header #panel .lower {
    background-color: #444;
    border-radius: 4px;
}

#container #header #panel .upper {
    background-color: #555;
    border-radius: 0px;
}

#container #header #panel .upper #search .button {
    background-color: #ba232b;
    border: none;
    border-radius: 0px;
}

#content_container {
    background-color: #444;
    border: none;
    color: #fff;
}

.cheat_list_table,
.cheat_list_table a {
    color: #fff;
    border: none;
    border-radius: 0px;
    background-color: #333b3b;
    background: #444;
}

#sidebar {
    color: #fff;
    background-color: #222;
    border-right: 4px solid #222;
    border-left: none;
}

#sidebar .tab,
#sidebar .ribbon-full {
    background-color: #333;
}

.text_field_2 {
    background-color: #222;
    color: #fff;
}

#sidebar blockquote {
    color: #fff;
}

h1.format_text_red,
h2.format_text_red {
    color: #fff;
    border-bottom: 1px solid #fff;
}

#container #header #panel .upper #search .textbox {
    border-radius: 0px;
}

#header #logo div a img {
    display: block;
}

#content .wrapper,
#content .wrapper a,
#content .wrapper .navigation .active {
    border: none;
    color: #fff;
}

.content #wrapper,
#content .wrapper,
#container #content .wrapper .tborder,
.tcat,
#container #content .wrapper .tborder #inline_row,
.tborder #posts_container #posts,
.editor_control_bar {
    background-color: #444;
    box-shadow: 0 0 0;
    color: #fff;
    border-radius: 4px;
}

.pm_alert {
    background: #ba232b5c;
    border: 1px solid #ba232b;
    border-radius: 4px;
}

.post .post_author {
    background-color: #333;
}

.post_controls {
    background-color: #333;

    border-radius: 0px;
    color: #fff;
}

.codeblock {
    background: #333;
}

code {
    background: #333;
}

.post_body {
    color: #fff;
}

.pagination,
.pagination .pagination_current,
.pagination .pagination_previous,
.pagination .pagination_page,
.pagination .pagination_next,
.pagination a {
    background: transparent;
    color: #ba232b;
}

.postbit_buttons a:link {
    background: none;
}

#container #content .wrapper #notice_box {
    border: 1px solid #ba232b;
    border-radius: 4px;
    background-color: #ba232b5c;
    box-shadow: 0 0 0;
}

.trow1,
.trow2,
.tfoot {
    background: #333 none repeat scroll 0% 0%;
    border-radius: 0px;
    color: #fff;
}

.thead {
    background-color: #3333336b;
    border: none;
}

.trow_sep {
    background-color: #444;
    color: #fff;
}

fieldset,
fieldset table {
    color: #fff;
    border-radius: 0px;
}

blockquote {
    background-color: #333;
    border-radius: 0px;
}

.container .wrapper #quick_reply_form .tborder #quickreply_e {
    background-color: #333;
}

textarea {
    color: #fff;
    border-radius: 0px;
}

#wrapper #slider {
    border: none;
    background-color: #444;
    background-image: none;
}

.order_table_td .tab {
    background-color: #333;
}

.order_table_td_unb .button_payment {
    border: none;
    background: #333;
    background-color: #333;
}

#wrapper form .bit_button {
    border: none;
    background: #333;
    background-color: #333;
}

.arrow_box {
    border: none;
    border-radius: 0px;
    background-color: #222;
    padding: auto;
    border: 1px solid #333;
}

#header .container nav a:hover {
    background-color: #db1212;
}

table {
    color: #fff;
}

.cheat_list_table .cheat_list_td .sup .new {
    background: none;
    background-color: #222;
}

nav ul li:hover a {
    background-color: #222;
}

#wrapper #content_container #content_full form div {
    background: none;
}

#go_page_1_popup {
    background: #222;
    border-radius: 0px;
}

#go_page_1_popup .textbox {
    border-radius: 0px;
}
#go_page_1_popup .button {
    border: none;
    border-radius: 0px;
    background: #333;
    color: #fff;
    padding-left: 20px;
    padding-right: 20px;
	}
}
