css:
@-moz-document url-prefix("https://cn.bing.com/"), url-prefix("https://bing.com") {
body,#b_header {
    background-color: #323232 !important;
}

#HBContent {
	background-color: #161616;
}

.rwrl {
    color: #ffffff !important;
}

#hp_container,.sw_tb,.hpcPrevious {
    background-color: #323232 !important;
}
#bgDiv {
    background-image: none !important;
    background-color: #323232;
}
#hp_ctrls,#hp_bottomCell,#sh_rdiv,#sh_rt,#sh_lt,#sh_igr,#sh_igl,.sc_light,.sc_lightdis,#b_context {
    visibility: hidden !important;
}
#bottom_shader {
    visibility: hidden;
}
#b_results {
    width: 64% !important;
}
.b_algo,.b_ans {
    background-color: #202020 !important;
    margin-top: 16px !important;
    margin-bottom: 16px !important;
    border-radius: 8px !important;
	border: 3px solid #252525 !important;
    padding: 16px !important;
	animation: cancel 0.2s linear;
    animation-fill-mode: forwards;
}

p {
    color: white;
}

cite {
    color: #404040
}

.b_algo:hover,.b_ans:hover {
	animation: hover 0.2s linear;
    animation-fill-mode: forwards;
}

@-webkit-keyframes hover /* Safari & Chrome */
{
	from {box-shadow: none !important;}
	to {box-shadow: 0px 0px 0px 3px #0684CC;}
}

@-webkit-keyframes cancel /* Safari & Chrome */
{
	from {box-shadow: 0px 0px 0px 3px #0684CC;}
	to {box-shadow: none !important;}
}

a {
    text-decoration: none;
    color: #0684CC !important;
    font-weight: medium;
}
a:hover {
   text-decoration: none !important; 
}

.b_searchboxForm {
    border-radius: 4px;
    border: none !important;
    padding: 4px;
}

.b_active {
    background-color: #646464;
    border-radius: 6px;
    border: none !important;
}

.b_active > a {
    color: #ffffff !important;
}

.tipContainer {
    background-color: #646464;
    border: none;
    border-radius: 4px;
}
#tipTitle {
    color: #ffffff;
}
.b_ans,.df_c  {
    background-color: #161616 !important;
}

.b_entityTitle {
    color: #ffffff !important;
}

.tab-menu > ul > li {
    background-color: #161616 !important;
    border: none !important;
    color: #ffffff !important;
}

.tab-navl, .tab-head > div {
    background-color: #323232 !important;
	border-radius: 4px;
}

strong {
    color: #a0a0a0 !important;
}
.nws_cwrp {
    background-color: #323232;
}
.itm_spt {
    color: #ffffff !important;
}

.b_msg {
    background-color: #646464 !important;
    color: #ffffff !important;
    border-radius: 8px;
 	border: 3px solid #252525;
}

.b_expansion_text {
    color: #ffffff !important;
}

.dict_oa {
    border: none !important;
}

.b_footer {
	background-color: #242424;
}

.b_clearfix,.dc_pm > div,.dc_mn {
    color: #ffffff !important;
}

.b_expDesk {
    background-color: #161616;
  	border-left: 3px solid #252525 !important;
  	border-right: 3px solid #252525 !important;
}

select {
    background-color: #161616;
}

#id_hbfo,.HBContent {
    background-color: #161616;
    box-shadow: none !important;
}

.hb_section:hover {
    background-color: #323232;
}

.hb_section {
    background-color: #161616 !important;
}

.bottomTipContainer {
    background-color: #161616;
    border: none !important;
}

.bottomTipContainer > span {
    color: #ffffff !important;
}

.bottomTipContainer > #tipTitle {
    color: #0684CC !important;
}

.sb_pagF > li > a:hover {
    background-color: #242424 !important;
}

.sb_pagF > li > a,.sb_pagN {
    border-radius: 8px;
    border: none !important;
}

.w2_tq {
    border-radius: 4px;
    border: none !important;
}

#sp_requery,#sp_recourse {
    color: #ffffff !important;
}

.b_searchboxForm,.b_searchbox {
    background-color: #161616 !important;
    color: #ffffff;
	box-shadow: none !important;
}

.b_searchboxSubmit {
	background: none !important;
	visibility: hidden !important;
}
.sa_as > ul > li {
	background-color: #242424;
}
.sa_hv { /* Hover */
	background-color: #323232 !important;
}
#est_switch,#est_cn,#est_en {
	visibility: hidden;
}
.trans_button {
	background-color: #242424;
	border-radius: 6px;
}

/* Chat with bing */
#ev_talkbox_max,#ev_talkbox_min,#ev_talkbox_wrapper {
	background-color: #242424;
	border: none !important;
	border-radius: 4px;
}

#ev_talkbox_wrapper {
	margin: 16px;
}

.ev_my_img,.ev_zo_img {
	border-radius: 6px;
}

#ev_min_zoimg {
	border-radius: 4px;
}

#ev_tb_header {
	background-color: #242424 !important;
}

.zo_min_text {
	color: #646464;
}
}

@-moz-document url-prefix("https://cn.bing.com/images/"), url-prefix("https://bing.com/images/") {
#b_content,#rfPane,.carousel-scroll-content,.items,.dg_b {
    background-color: #323232 !important;
}

.item {
    background-color: #323232 !important;
}

.suggestion-color {
    background-color: #646464 !important;
}

[role=presentation] {
    background-color: #242424 !important;
}

.sug {
    background-color: #646464 !important;
}
}

@-moz-document url-prefix("https://cn.bing.com/videos/"), url-prefix("https://bing.com/videos/") {
.mc_vtvc_meta {
    background-color: #161616;
}
}

@-moz-document url-prefix("https://cn.bing.com/academic/") {
.aca_algo {
    background-color: #161616 !important;
}
}
