css:
@-moz-document domain("preshing.com") {

html {
  background-color: #282828;
}

body {
  color: #aaa;
}

img {
  opacity: 0.5;
}

/** Top bar **/
body > nav {
  background: #333;
  border-top-color: #333;
  border-bottom-color: #333;
}

body > nav a {
  text-shadow: #282828 0 1px;
}

body > nav li + li a {
  border-left-color: #333;
}

/** Left column Content **/

aside.sidebar section h1 {
  border-bottom-color: #333;
}

aside.sidebar {
  border-top-color: #333;
}

body > div#main > div#content {
  background-color: #282828;
  border-right-color: #333;
}

/* Code */
pre {
  background-color: #222;
  border-color: #333;
  color: #aaa;
}

.CodeRay {
  background-color: #333;
}

/* Code snippet */
p code, li code {
  background-color: #333;
  border-color: #333;
  color: #aaa;
}

/** 'Recent Posts' in Right column **/
aside.sidebar {
  color: #aaa;
  text-shadow: #282828 0 1px;
}

body > div#main {
  background-color: #333;
}

aside.sidebar li {
  border-bottom-color: #333;
}

.side-shadow-border, aside.sidebar section h1, aside.sidebar li {
  box-shadow: #666 0 1px;
}

/* 'Read on' button at bottom */
#content .blog-index a[rel="full-article"] {
  background-color: #222;
  color: #aaa;
}

/*** Syntax Highlighting in Code ***/

.CodeRay .reserved, .keyword {
  color: #aaa;
}

span.highlight {
  background-color: #013a52; /* #353e3e; */
}


/***** Archives Page - preshing.com/archives/ *****/

#blog-archives h1 {
  color: #999;
}

}
