css:
@-moz-document domain("userstyles.org") {
/*Main color variables*/
:root {
    /*User colors*/
    --main-color: #00ADEE;
    --main-background: #101010;
    --second-background: #101010;
    --hover-background: #232323;
    --main-text: #e5e5e5;
    --dimmer-text: #e5e5e5;

    --shadow: 0 1px 0.5px rgba(0, 0, 0, 0.13);

    /*DeepDark colors*/
    /*
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimmer-text: #CCCCCC;
		*/
    /*KDE colors*/
    /*
		--main-color: #3DAEE9;
		--main-background: #232629;
		--second-background: #2a2e32;
		--hover-background: #31363b;
		--main-text: #eff0f1;
		--dimmer-text: #bdc3c7;
		*/
    /*Vertex Dark colors*/
    /*
		--main-color: #4080fb;
		--main-background: #2B2B2C;
		--second-background: #353638;
		--hover-background: #515254;
		--main-text: #F3F3F5;
		--dimmer-text: #AEAFB0;
		*/
    /*Arc Dark colors*/
    /*
		--main-color: #5294E2;
		--main-background: #343944;
		--second-background: #383C4A;
		--hover-background: #414A59;
		--main-text: #C1C8D1;
		--dimmer-text: #B3BAC5;
		*/
    /*Firefox Dark colors*/
    /*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimmer-text: #606376;
		*/
    /*Firefox57 Dark colors*/
    /*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimmer-text: #D0D0D0;
		*/
    /*Discord colors*/
    /*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimmer-text: #ADA8AA;
		*/
    /*YouTube dark colors*/
    /*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimmer-text: #7F7F7F;
		*/
    /*Mint-Y-Dark colors*/
    /*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimmer-text: #D5DADA;
		*/
    /*9anime colors*/
    /*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimmer-text: #747474;
		*/
    /*Black and white*/
    /*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimmer-text: #828282;
		*/
    /*Yellow (colors from petrocompletions)*/
    /*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimmer-text: #9F9999;
		*/
    /*Yellow 2*/
    /*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimmer-text: #fff8e1;
		*/
    /*Ubuntu grey colors*/
    /*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/
    /*Ubuntu purple colors*/
    /*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/
    /*Orange*/
    /*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimmer-text: #ffede1;
		*/
    /*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
    /*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimmer-text: #986E3F;
		*/
}




/*Changed text highlight*/
::selection {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
}
::-moz-selection {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
}




/*Outline for links*/
a {
    outline-color: var(--main-color) !important;
    text-decoration: none !important;
}




/*Body*/
body {
    background-color: var(--main-background) !important;
}




/*Logo*/
.logo {
    filter: grayscale(100%) brightness(300%);
}




/*Header*/
#header {
    background-color: var(--main-background) !important;
}
#search_input {
    background-color: var(--second-background) !important;
}
#input_text_search {
    color: var(--main-text) !important;
}
.Combined-Shape[src*="blue"] {
    filter: brightness(50%);
}
.Combined-Shape[src*="blue"] {
    filter: grayscale(100%) brightness(200%);
}
#create_new_style a {
    color: var(--main-text) !important;
}
#create_new_style {
    color: var(--main-text) !important;
    border-color: var(--hover-background) !important;
    background-color: var(--main-background) !important;
}
#create_new_style:hover a {
    color: var(--main-color) !important;
}
.header_button a {
    color: var(--main-text) !important;
}
.header_button a:hover {
    color: var(--main-color) !important;
}




/*Sidebar*/
.navigation {
    background-color: var(--main-background) !important;
}
.navigation .links-box .navigation-link:hover {
    background-color: var(--hover-background) !important;
}
.navigation .links-box .navigation-link,
.navigation .links-box {
    box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
}
.navigation .links-box .navigation-link .navigation-title {
    color: var(--main-text) !important;
}
.navigation .links-box .navigation-link .logo-navigation-link[src*="svg"] {
    filter: invert(1) brightness(200%) grayscale(1);
}





/*Tabs*/
.navigation_link:hover {
    background-color: var(--hover-background) !important;
}
.navigation_link_active {
    border-color: var(--main-color) !important;
}
.navigation_title {
    color: var(--main-text) !important;
}
.navigation_link:hover .navigation_title {
    color: var(--main-color) !important;
}
.logo_navigation_link[src*="svg"] {
    filter: invert(100%) grayscale(100%);
}





/*Slideshow*/
#left-arrow-carousel,
#right-arrow-carousel {
    filter: invert(100%);
}
#overlay-info {
    background-color: var(--second-background) !important;
}
#style-info-title-carousel {
    color: var(--main-color) !important;
    opacity: .9;
}
#style-info-author-carousel a {
    color: var(--main-text) !important;
}
#style-info-author-carousel a:hover,
#style-info-title-carousel:hover {
    color: var(--main-color) !important;
    opacity: 1;
}
#overlay-info {
    border-radius: 0 !important;
}
.oval {
    background-color: var(--dimmer-text) !important;
}
.oval-active {
    background-color: var(--main-color) !important;
    box-shadow: var(--shadow) !important;
}





/*Categories*/
.category_title {
    color: var(--main-text) !important;
}
.see_more {
    background-color: var(--hover-background) !important;
    color: var(--main-text) !important;
    box-shadow: var(--shadow) !important;
}
.see_more:hover {
    color: var(--main-color) !important;
}





/*Cards*/
.us-stylecard,
.us-stylecard--short {
    background-color: var(--second-background) !important;
}
.us-stylecard--short .us-stencil {
    background-color: var(--hover-background) !important;
    box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
}
.us-stylecard--short .us-updated {
    color: var(--main-text) !important;
}
.us-stylecard--short .us-title > a {
    color: var(--main-color) !important;
}
.downloads-icon {
    filter: grayscale(100%) brightness(200%);
}
.tooltip_rating .tooltiptext_rating,
.tooltip .tooltiptext {
    background-color: var(--second-background) !important;
    color: var(--main-text) !important;
}





/*Footer*/
#footer {
    background-color: var(--main-background) !important;
}
#copyrights {
    color: var(--main-color) !important;
}
.footer_link a,
#bottomLinks,
.footer_link a:visited {
    color: var(--main-text) !important;
}
.footer_link a:hover {
    color: var(--main-color) !important;
}





/*Editor's choice*/
h1 {
    color: var(--main-text) !important;
}
.view_mode_img[src="/ui/images/icons/squares-on.svg"],
.view_mode_img[src="/ui/images/icons/rectangle-on.svg"] {
    filter: grayscale(100%) brightness(200%);
}
.current,
.div-arrow {
    background-color: var(--main-color) !important;
    color: var(--main-text) !important;
    border: none !important;
    opacity: .9;
}
.pageNumber {
    color: var(--main-text) !important;
}
.pageNumber:hover {
    color: var(--main-color) !important;
}
.div-arrow:hover,
.current:hover {
    background-color: var(--main-color) !important;
    color: var(--main-text) !important;
    opacity: 1;
}

.arrow-icon[src="/ui/images/icons/right-arrow-icon.svg"],
.arrow-icon[src="/ui/images/icons/left-arrow-icon.svg"] {
    filter: grayscale(100%) brightness(200%);
}
.style_card_long,
.line_seperator {
    box-shadow: inset 0 -1px 0 0 var(--hover-background) !important;
}
#title_long a,
.style_card_long #title_long a {
    color: var(--main-text) !important;
}
#title_long a:hover,
.style_card_long:hover #title_long a,
.style_author_long a {
    color: var(--main-color) !important;
}
.style_description_long,
.style_author_long {
    color: var(--dimmer-text) !important;
}
.view_details {
    background-color: var(--main-color) !important;
    color: var(--main-text) !important;
    opacity: .9;
}
.view_details:hover {
    background-color: var(--main-color) !important;
    color: var(--main-text) !important;
    opacity: 1;
}





/*Style*/
#preview_image_div {
    background-position: center center !important;
}
.author-name a,
.text_style_page a,
#as_userscript a,
#notes_textarea a,
#discussion_author a {
    color: var(--main-color) !important;
    opacity: .9;
}
.author-name a:hover,
.text_style_page a:hover,
#as_userscript a:hover,
#as_userscript a:visited,
#as_userscript a:visited:hover,
#notes_textarea a:hover,
#notes_textarea a:visited,
#discussion_author a:visited,
#discussion_author a:visited:hover,
#discussion_author a:hover {
    color: var(--main-color) !important;
    opacity: 1;
}
#share_div,
#headline,
#infomation_value_left,
#information_key,
.title_setting {
    color: var(--main-text) !important;
}
#information_key_left,
.text_style_page,
#discussion_date,
.by-author,
.style_author.ellipsis {
    color: var(--dimmer-text) !important;
}
#information_value,
#applies_link {
    color: var(--main-color) !important;
}
.white_button:hover,
.css_button:hover,
.advanced_button:hover,
#archive_style_button:hover,
#send_feedback_button:hover,
#style_installed_button {
    background-color: var(--hover-background) !important;
    color: var(--main-color) !important;
    border: none !important;
    box-shadow: var(--shadow) !important;
}
.white_button,
.css_button,
.advanced_button,
#archive_style_button,
#send_feedback_button,
#style_installed_button:hover {
    background-color: var(--hover-background) !important;
    color: var(--main-text) !important;
    border: none !important;
    box-shadow: var(--shadow) !important;
}
#update_style_button,
#install_style_button {
    background-color: var(--hover-background) !important;
    color: var(--main-text) !important;
    border: none !important;
    box-shadow: var(--shadow) !important;
}
#update_style_button:hover,
#install_style_button:hover {
    color: var(--main-color) !important;
}
.installStyleButtonTooltip {
    background-color: var(--hover-background) !important;
}
#notes_textarea,
#stylish-code,
.advancedsettings_shown {
    background-color: var(--second-background) !important;
    color: var(--main-text) !important;
    border: 1px solid var(--hover-background) !important;
}
div.setting_div > div > div {
    background-color: black !important;
}
#single_discussion {
    box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
}





/*iframes*/
p,
ul,
i,
body,
.contact-bold-font {
    color: var(--dimmer-text) !important;
}
a,
a:visited,
.sub-title {
    color: var(--main-color) !important;
    opacity: .9 !important;
}
a:hover,
a:visited:hover {
    color: var(--main-color) !important;
    opacity: 1 !important;
}
.contact-font {
    color: var(--dimmer-text) !important;
}
.contact-input {
    background-color: var(--second-background) !important;
    border-color: var(--hover-background) !important;
}
:focus {
    outline-color: var(--main-color) !important;
}



/*login*/
.btn-auth,
input#login-existing,
input#password-existing,
input[type="submit"] {
    background-color: var(--second-background) !important;
    border-color: var(--hover-background) !important;
    color: var(--dimmer-text) !important;
}
.btn-auth:hover,
input[type="submit"]:hover {
    background-color: var(--hover-background) !important;
    border-color: var(--hover-background) !important;
    color: var(--main-color) !important;
}
input#remember-normal {
    filter: invert(100%);
}
a.btn-auth:visited {
    color: var(--dimmer-text) !important;
}




/*Edit style page*/
input:not([class*="color"]),
textarea {
    background-color: var(--second-background) !important;
    border: 1px solid var(--hover-background) !important;
    color: var(--dimmer-text) !important;
}
input:active,
textarea:active,
input:focus,
textarea:focus {
    border-color: var(--main-color) !important;
}
input[id*="option-default"] {
    border: 1px solid transparent !important;
    box-shadow: var(--shadow) !important
}
.edit-setting {
    background-color: var(--hover-background) !important;
}



/*If your browser shows buttons white because of a default white theme, uncomment this*/
/*
	input[type="radio"], input#enable-source-editor-code
	{
		filter: invert(100%);
	}
	*/
/*Edit css*/
.CodeMirror {
    background-color: var(--second-background) !important;
    border-color: var(--hover-background) !important;
}
.CodeMirror-linenumber,
.CodeMirror-gutters {
    background-color: var(--hover-background) !important;
    border-color: var(--hover-background) !important;
}
.errorExplanation {
    background-color: var(--hover-background) !important;
    border-color: var(--hover-background) !important;
}




/*Code text*/
.CodeMirror div.CodeMirror-cursor {
    border-color: var(--main-color);
}
.cm-s-default.CodeMirror {
    background: var(--second-background);
    color: var(--main-text);
}
.cm-s-default div.CodeMirror-selected {
    background: var(--hover-background);
}
.cm-s-default .CodeMirror-line::selection,
.cm-s-default .CodeMirror-line > span::selection,
.cm-s-default .CodeMirror-line > span > span::selection {
    background: var(--hover-background);
}
.cm-s-default .CodeMirror-line::-moz-selection,
.cm-s-default .CodeMirror-line > span::-moz-selection,
.cm-s-default .CodeMirror-line > span > span::-moz-selection {
    background-color: var(--hover-background);
}
.cm-s-default .CodeMirror-gutters {
    background: var(--hover-background);
    border-right: 0px;
}
.cm-s-default .CodeMirror-guttermarker {
    color: var(--main-text);
}
.cm-s-default .CodeMirror-guttermarker-subtle,
.cm-s-default .CodeMirror-linenumber {
    color: var(--dimmer-text);
}
.cm-s-default .CodeMirror-cursor {
    border-left: 1px solid var(--main-color);
}

.cm-s-default span.cm-comment {
    color: var(--dimmer-text);
}
.cm-s-default span.cm-atom,
.cm-s-default span.cm-number {
    color: var(--main-color);
    opacity: .6;
}

.cm-s-default span.cm-property,
.cm-s-default span.cm-attribute {
    color: var(--main-color);
    opacity: 1;
}
.cm-s-default span.cm-keyword,
.cm-s-default span.cm-string {
    color: var(--main-color);
    filter: hue-rotate(60deg);
}
.cm-s-default span.cm-string.cm-property {
    color: var(--main-text);
}

.cm-s-default span.cm-variable {
    color: var(--main-text);
}
.cm-s-default span.cm-variable-2 {
    color: var(--main-color);
    opacity: .6;
}
.cm-s-default .cm-variable-3,
.cm-s-default .cm-type {
    color: var(--main-color);
    filter: hue-rotate(280deg);
}
.cm-s-default span.cm-def {
    color: var(--main-text);
}
.cm-s-default span.cm-bracket {
    color: var(--main-text);
    font-weight: bold;
}
.cm-s-default span.cm-tag {
    color: var(--main-color);
    opacity: .6;
}
.cm-s-default span.cm-link,
.cm-s-default span.cm-keyword {
    color: var(--main-color);
    filter: invert(100%);
}
.cm-s-default span.cm-error {
    border-bottom: var(--hover-background);
    color: var(--main-text);
}
.cm-s-default span.cm-qualifier {
    color: var(--main-text);
}

.cm-s-default .CodeMirror-activeline-background {
    background: var(--second-background);
}
.cm-s-default .CodeMirror-matchingbracket {
    color: var(--main-color);
}
.cm-s-default .CodeMirror-matchingtag {
    background: var(--dimmer-text);
}

.cm-s-default .cm-builtin {
    color: var(--main-color);
}
.cm-s-default .cm-meta {
    color: var(--dimmer-text);
}





/*Forum*/
#Head {
    background: var(--hover-background) !important;
    color: var(--main-text) !important;
}
#Head a:hover {
    color: var(--main-color) !important;
}





/*Search*/
input.DateBox,
input.InputBox,
input.SmallInput,
textarea {
    background: var(--second-background) !important;
    color: var(--main-text) !important;
}
.ItemDiscussion .Meta,
.DataList .Meta {
    color: var(--dimmer-text) !important;
}
mark {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
}
#search_input input {
    border: none !important;
}
.SiteSearch .Button {
    background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -324px no-repeat transparent !important;
}
.SiteSearch .Button:hover {
    background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -260px no-repeat transparent !important;
}

#title_div {
    box-shadow: inset 0 -1px 0 0 var(--hover-background) !important;
}




/*Login*/
a.TextColor,
a .TextColor {
    color: var(--main-text) !important;
}
a.TextColor:hover,
a .TextColor:hover,
a:hover .TextColor {
    color: var(--main-color) !important;
}
.Button,
.customize_button {
    background: var(--second-background) !important;
    border-color: var(--hover-background) !important;
    color: var(--dimmer-text) !important;
    box-shadow: none !important;
    text-shadow: none !important;
}
.Button:hover,
.customize_button:hover {
    background: var(--hover-background) !important;
    border-color: var(--hover-background) !important;
    color: var(--main-color) !important;
    box-shadow: none !important;
    text-shadow: none !important;
}





/*Menu*/
.Sprite16 {
    filter: invert(100%);
}
.Alert {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
}





/*Categories*/
.PanelCategories li:first-child,
.PanelCategories li:nth-last-child(2) {
    border-color: var(--hover-background) !important;
}
.Count {
    background: var(--second-background) !important;
    color: var(--main-text) !important;
}
#Panel .FilterMenu li a,
.PanelInfo li a {
    color: var(--main-text) !important;
}
#Panel .FilterMenu li a:hover,
.PanelInfo li a:hover {
    color: var(--main-color) !important;
}





/*Discussions*/
.Tag {
    background: var(--main-background) !important;
    color: var(--main-text) !important;
}
.HasNew {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
}
.MItem,
span.ShowDiscussionAuthor,
.Pager span {
    color: var(--dimmer-text) !important;
}
.OptionsTitle,
a.Bookmark,
a.Bookmarked,
