css:
@-moz-document domain("58.com") {
.list-info .title a {
    font-size: 20px;
    color: #2866bd;
    font-weight: bold;
}
.house-list-wrap {
    text-align: left;
    font-size: 20px;
}
.unit{
    text-align:right;
}
.list-info .listjjr {
    position: relative;
    color: #ff552e;
    font-weight: bold;
}
.main-wrap {
    width: 1190px;
   /* margin-left: 100px;*/
}
.content-side-left {
    float: left;
}
.header-wrap {
    margin-top: 0;
}
.nav-top-bar {
    width: 100%;
    line-height: 9px;
}
.house-nav-tab {
    overflow: hidden;
    margin-bottom: 5px;
}
.filter-wrap {
    margin-bottom: 5px;
    position: relative;
    z-index: 1;
}
.house-list-wrap li {
    position: relative;
    /*border-bottom: 1px solid #f3f3f3;*/    
    padding: 8px 8px 8px;
    margin-top: 0;
    margin-bottom: 15px;
    border-radius: 5px;
    background-color: #fff;
    box-sizing: border-box;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
}
.content-nav-tab .tuiguang {
    display: none;
}
.list-info .baseinfo {
    float: left;
}
.list-info .jjrinfo {
    margin-bottom: 14px;
    line-height: 14px;
    right: 136px;
    float: left
}
.house-list-wrap .price {
    position: absolute;
    right: 100px;
    margin-top: -3px;
    font-size:15px;
}
.barct {
    padding-top: 5px;
    padding-bottom: 5px;
    border-top: 1px solid #f3f3f3;
}
.baseinfo{
    font-size:18px;
}
.price{
    font-size:14px;
}
/*具体正文*/.pic-desc-word{
    font-size:20px;
}
.c_000{
    font-size:20px;
}
}
