css:
/* Hide shit */
@-moz-document domain('wattpad.com'), url-prefix('http://wattpad.com') {

/* Hide top bar elements */

/* "Go Premium" button */ 
#header-container #header #go-premium-button {
    display: none;
}

/* Discover and Community dropdowns */
#header-container #header>nav>ul>li.button-group {
    display: none;
}

/* Hide recommended stories */

/* Sidebar */
#story-reading #parts-container-new .reading-widget {
    display: none;
}
/* Bottom row */ 
#story-reading #parts-container-new footer .part-recommendations-container .row {
    display: none;
}
#story-reading #parts-container-new .has-border {
    display: none;
}

/* Hide social widgets */

/* Left sidebar */
#story-reading #parts-container-new .share-tools.vertical {
    display: none;
}

/* Bottom */
#story-reading #parts-container-new footer .actions .share-tools {
    display: none;
}

/* Hide information in "funbar" */
#funbar-story .fa-down  {
    display: none;
}
#funbar-story .info {
    display: none;
}
#funbar-story .cover {
    display: none;
}

/* Hide dismissable bottom banner */
.bottom-banner {
    display: none;
}
}
