css:
@-moz-document domain("mwomercs.com") {
h1 {
    margin: 10px 0;
    font-family: LockClock;
    line-height: 1;
    color: inherit;
    text-rendering: optimizelegibility;
    text-transform: inherit;
}
h2 a:link, h3 a:link, h4 a:link, h5 a:link, h6 a:link {
    text-transform: inherit;
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    color: #080;
}
h1, h2, h3, h4, h5, h6 {
    color: #080;
}
.maintitle {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    font-size: 1.1em;
    background-color: #333;
    color: #080;
    padding: 5px 1px;
    margin: 0px;
    border-bottom: 2px solid #FFF;
}
.desc.lighter.blend_links a {
    color: #0f0;
}
table.ipb_table .topic_title {
    margin: 10px 0;
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    line-height: 1;
    color: #2a2;
    text-rendering: optimizelegibility;
}
table.ipb_table .unread .topic_title {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    color: #2f2;
}
a.bbc_url {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    font-weight: bold;
    color: #55f;
}
.spoiler_title {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    font-weight: bold;
    color: #ff8;
    font-size: 10px;
}
.bbc_spoiler_show {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    width: 90px;
    font-size: .7em;
    margin: 0;
    padding: 0;
    color: #000;
    background-color: #ff8;
}
.ipsBadge_orange, .ipsBadge_lightgrey {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    background: #0b0;
    color: #000;
}
.ipsBadge_red {
    background: #f00;
}
.mini_pagination li a {
    color: #000;
    background: #0b0;
    border: 1px solid #d3d3d3;
    padding: 1px 3px;
    font-family: Consolas, "Stratum1 Medium", Arial, sans-serif;
    font-weight: bold;
    font-size: 12px;
}
.desc.lighter.blend_links a {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    color: #fff;
}
.desc.lighter {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    color: #555;
}
.desc, .desc.blend_links a, .posted_info {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    font-size: 12px;
    color: #777777;
}
li a:link {
    color: #080;
}
a, .bbc_url a:link {
    color: #080;
} /**/
.pagination .forward a, .pagination .back a, .pagination .pages li.active {
    background: #080;
    color: #fff;
}
.ipsFilterbar li.active a {
    background: #080;
    opacity: 1;
    color: #000;
}
.ipsFilterbar .active a {
    background: #080;
    opacity: 1;
    color: #fff;
    padding: 4px 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    -webkit-box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.2);
    box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.2);
}
#mainMenu.navbar .nav > li > a.downloadBtn {
    background-color: #282;
    color: #000;
}
#mainMenu.navbar .nav > li > a.downloadBtn:hover {
    background-color: #0f0;
}
#topMenu a:hover {
    color: #000;
    background-color: #0f0;
}
#subNav ul li a:hover {
    color: #0f0;
    text-decoration: none;
}
#textContent .input pre {
    color: #fff;
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
}
.navbar-inverse .nav > li > a:focus, .navbar-inverse .nav > li > a:hover {
    color: #0f0;
    background-color: transparent;
}
.siteMapCategory a:hover {
    color: #0f0;
	text-decoration: none currentcolor solid;
}
.siteMapCategory a {
    color: #080;
}
li a:hover {
    color: #0f0;
}
h4 a:hover {
    color: #0f0;
}
.post_body .post ul li {
    list-style: inside;
    color: #aaa;
    margin: 10px 0 10px 15px;
}
.post_body .post {
    color: #aaa;
}
strong {
    font-family: LockClock, "Stratum1 Medium", Arial, sans-serif;
    font-weight: bold;
    color: #fff;
}
.objective-title {
    text-align: left;
    color: #080;
}
.btn-inverse.disabled {
    color: #ffffff;
    background-color: #400;
    *background-color: #600;
}
.objective-progress {
    width: 0%;
    height: 100%;
    display: block;
    background: linear-gradient(-90deg, #0f0, #020);
    /*animation: Gradient 5s ease infinite;/**/
    animation: none;
    background-size: 200% 200%;
    border-radius: 5px;
}
.statistics_head {
    font-family: Consolas, "Stratum1 Medium", Arial, sans-serif;
    font-size: 12px;
    font-weight: bold;
}
.statistics {
    font-family: Consolas, "Stratum1 Medium", Arial, sans-serif;
    font-size: 10px;
    font-weight: normal;
}
}
