css:
@-moz-document domain("youcomedy.me") {
/* выделение для новых в новостях */
.newslist__item._new { 
	background: #486B4F; 
}
._highlight { 
	background-color: #a0a0a0; }
/* основной фон (без шуток) */
 body {
	font-family:"Trebuchet MS", Tahoma, Arial, sans-serif;
	font-size: 13px;
	line-height: 1.4;
	background-color: #282B2F;
	color: #d0d0d0;
	min-width: 998px;
	position: relative;
	padding: 48px 0 0 !important;
}
/* меню и чат (слева), текст на меню */
 .widget__section {
	width: 100%;
	margin: 0;
	line-height: 1.4;
	position: relative;
	padding: 8px 10px;
	color: #a0a0a0;
	border-bottom: 1px solid #eef0f2;
}
/* фон для элементов меню и чата (слева) */
 .widget__body {
	position: relative;
	background: #484B4F;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}
/* фн для меню и чата слева */
 .widget__head {
	position: relative;
	padding: 10px;
	border-bottom: 1px solid #eef0f2;
	background: #484B4F;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
	display: block;
}
._inverse .widget__head {
	background: #282B2F;
}
.widget__body {
	position: relative;
	background: #484B4F;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
	color: #000;
}
._inverse .widget__body {
	background: #a0a0a0;
}
.widget__title {
	line-height: 1;
	margin: 0;
	font-size: 13px;
	color: #000;
}
/* фон меню (профиль) */
 .widgetMenu .widget__head {
	background: #808080 !important;
	color: #000;
}
/* */
 .ui-widget-content {
	background: #884B4F;
	color: #8f8f8f;
}
/* самое вернее меню */
 .menu {
	width: 100%;
	background: black;
	z-index: 1000;
	height: 48px;
	top: 0;
	left: 0;
	position: absolute;
}
/*  */
 .widgetMenu__profile._shade._shade:after {
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 0% 50%, 100% 50%, color-stop(0%, rgba(255, 0, 0, 0)), color-stop(100%, #808080));
	background-image: -moz-linear-gradient(left, rgba(255, 0, 0, 0) 0%, #808080 100%);
	background-image: -webkit-linear-gradient(left, rgba(255, 0, 0, 0) 0%, #808080 100%);
	background-image: linear-gradient(to right, rgba(255, 0, 0, 0) 0%, #808080 100%);
	color: #000;
}
.widgetMenu__profileRating {
	color: #f66;
	margin-top: 3px;
	font-size: 12px;
	font-weight: bold;
}
/* */
 .widget__title._active {
	background: #808080;
	color: #000;
	border-bottom: 0;
}
/* */
 .widgetActivity .widget__section:before {
	width: 200%;
	left: 0;
	bottom: 0;
	position: absolute;
	content:'';
	height: 10px;
	display: block;
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 00% 0%, 00% 100%, color-stop(0%, rgba(255, 0, 0, 0)), color-stop(50%, #484B4F));
	background-image: -moz-linear-gradient(top, rgba(255, 0, 0, 0) 0%, #484B4F 0%);
	background-image: -webkit-linear-gradient(top, rgba(255, 0, 0, 0) 0%, #484B4F 00%);
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, 0) 0%, #484B4F 00%);
}
.widgetActivity__body:before {
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 100%, 50% 0%, color-stop(0%, rgba(255, 255, 255, 0)), color-stop(70%, #a0a0a0));
	background-image: -moz-linear-gradient(bottom, rgba(255, 0, 0, 0) 0%, #a0a0a0 70%);
	background-image: -webkit-linear-gradient(bottom, rgba(255, 0, 0, 0) 0%, #a0a0a0 70%);
	background-image: linear-gradient(to top, rgba(255, 0, 0, 0) 0%, #a0a0a0 70%);
}
.widgetActivity__body:after {
	bottom: 0;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, rgba(255, 255, 255, 0)), color-stop(70%, #484B4F));
	background-image: -moz-linear-gradient(top, rgba(255, 0, 0, 0) 0%, #484B4F 70%);
	background-image: -webkit-linear-gradient(top, rgba(255, 0, 0, 0) 0%, #484B4F 70%);
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, 0) 0%, #484B4F 70%);
}
.widget__sectionLastSingle .widget__section:last-child:after, .widget__sectionLastDouble .widget__section:last-child:after {
	height: 100%;
	width: 1px;
	top: 0;
	right: -1px;
	content:'';
	display: block;
	position: absolute;
	background: #a0a0a0;
}
.alert, .alert h4 {
	color: #ff0;
}
/* виджет с наведенной мышкой */
 ._notouch .widgetMenu__item:hover {
	background-color: #a0a0a0;
	color: #000
}
/* ссылка (ники в том числе) */
 .link {
	color: #30d0ff;
}
.newslist__item {
	margin: 0 0 4px 0;
	padding: 10px;
	display: block;
	background: #484B4F;
	border-bottom: 1px solid #D6D7D9;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	color: #a0a0a0;
}
.newslist .newslist__item._like .news_content:after {
	display: block;
	content:"\e00e";
	background-color: #F03D65;
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #f00), color-stop(100%, #f00));
	background-image: -moz-linear-gradient(top, #f00, #f00);
	background-image: -webkit-linear-gradient(top, #f00, #f00);
	background-image: linear-gradient(to bottom, #f00, #f00);
}
.newslist .newslist__item._comment .news_content:after {
	display: block;
	content:"\e023";
	background-color: #5C9E16;
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #0f0), color-stop(100%, #0f0));
	background-image: -moz-linear-gradient(top, #0f0, #0f0);
	background-image: -webkit-linear-gradient(top, #0f0, #0f0);
	background-image: linear-gradient(to bottom, #0f0, #0f0);
}
#activityfeed .widget__section._like:after {
	display: block;
	content:"\e00e";
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #f00), color-stop(100%, #f00));
	background-image: -moz-linear-gradient(top, #f00, #f00);
	background-image: -webkit-linear-gradient(top, #f00, #f00);
	background-image: linear-gradient(to bottom, #f00, #f00);
}
#activityfeed .widget__section._comment:after {
	display: block;
	content:"\e023";
	background-image: url('data:image/svg+xml;base64,');
	background-size: 100%;
	background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #0f0), color-stop(100%, #0f0));
	background-image: -moz-linear-gradient(top, #0f0, #0f0);
	background-image: -webkit-linear-gradient(top, #0f0, #0f0);
	background-image: linear-gradient(to bottom, #0f0, #0f0);
}
.filters__filter > a {
	padding: 7px 10px;
	display: block;
	text-align: center;
	text-transform: lowercase;
	background: #484B4F;
	font-size: 11px;
}
._notouch .filters__filter > a:hover {
	background: #a0a0a0;
}
.backpack__itemLevel {
	position: absolute;
	right: -5px;
	bottom: -5px;
	z-index: 1;
	background: #484B4F;
	font-size: 12px;
	color: #fff;
	width: 20px;
	height: 20px;
	border-radius: 20px;
	line-height: 22px;
	vertical-align: middle;
	text-align: center;
	box-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
	font-weight: bold;
}
.panel {
	width: 100%;
	padding: 10px;
	margin-bottom: 10px;
	box-sizing: border-box;
	background-color: #484B4F;
	border-bottom: 1px solid #dedede;
	border-radius: 3px;
}
.panelProfile__avatar {
	width: 170px;
	height: 170px;
	padding: 6px;
	position: relative;
	background: #f00;
	border-radius: 3px;
	margin: 151px 0 20px 20px;
}
.panelProfile__tabs .tabs__tab {
	background: #484B4F !important;
}
.panelProfile__tabs .tabs__tab:hover {
	background: #a0a0a0 !important;
}
.panelProfile__userInfoWrap {
	margin-top: 287px;
	background: #484B4F;
	min-height: 110px;
	padding: 10px 10px 10px 200px;
}
.faqPanel__head {
	color: #aaa;
	line-height: 1;
	font-weight: normal;
	font-size: 36px;
	margin: 0 0 20px;
}
/* ============================================================ */

/* ============================================================ */

/* ============================================================ */

/* Заголовок шутки */
 .item__top {
	background: #484B4F;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	padding: 15px;
	position: relative;
}
/* комментарии */
 .item__bottom {
	color: #a0a0a0;
	background: #484B4F;
	border-bottom: 1px solid #d2d5db;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
	position: relative;
}
/* показать комментарии */
 .item__showAllComments {
	cursor: pointer;
	line-height: 40px;
	text-align: center;
	background: #484B4F;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}
._notouch .item__showAllComments:hover {
	background: #888B8F;
	color: #000;
}
/* */
 .item__container {
	background: #484B4F;
	color: #a0a0a0;
}
/* */
 .item__desc {
	word-break: break-all;
	word-break: break-word;
	word-wrap: break-word;
	margin-top: 0;
	position: relative;
	background: #484B4F;
	padding: 10px 15px;
	-moz-transition-property: background;
	-o-transition-property: background;
	-webkit-transition-property: background;
	transition-property: background;
	-moz-transition-duration: 0.3s;
	-o-transition-duration: 0.3s;
	-webkit-transition-duration: 0.3s;
	transition-duration: 0.3s;
}
.item__controls {
	background: #484B4F;
	border-top: 1px solid #eef0f2;
	border-bottom: 1px solid #eef0f2;
	color: #a0a0a0;
}
/* разделитель комментов */
 .comment + .comment:before {
	content:'';
	display: block;
	margin: -10px auto 10px;
	height: 1px;
	background: #707070;
}
.item__controls {
	background: #484B4F;
	border-top: 1px solid #eef0f2;
	border-bottom: 1px solid #eef0f2;
}
.item__tag {
	background-color: #909090;
	border: 1px solid #e0e0e0;
	font-size: 12px;
	font-weight: bold;
	line-height: 22px;
	padding: 0 5px;
	border-radius: 3px;
	margin: 5px;
	color: #000000;
	display: inline-block;
}
input, textarea {
	padding: 5px;
	resize: none;
	outline: none;
	color:"Trebuchet MS", Tahoma, Arial, sans-serif;
	font-size: inherit;
	font-family: inherit;
	border-radius: 3px;
	border: 1px solid rgba(0, 0, 0, 0.1);
	background: #909090;
	color: #000;
}
._notouch .item__longpost:hover {
	background-color: rgba(20, 20, 20, 0.8);
}
.item__longpost {
	height: 40px;
	line-height: 40px;
	background-color: rgba(20, 20, 20, 0.6);
	position: absolute;
	display: none;
	left: 0;
	right: 0;
	bottom: 0;
	cursor: pointer;
	text-align: center;
}
}
