css:
@-moz-document domain("youcomedy.me") {
.container {
	margin: 0 auto;
	width: 998px;
}/**/
/*
.container-grid {
	max-width: 80%;
}/**/

.main-page-content {
	float: left;
	width: 100%;
	position: relative;
}/**/

.main-page-sidebar {
	width: 20%;
	height: 300px;
	left: 50%;
	margin-left: -48%;
	position: absolute;
	top: 68px;
}/**/

.main-page-contentWrapper { 
	left: 50%;
	margin-left: -25%;
	margin-top: 10px;
	position: relative;
	width: 70%; }
/**/
.item__contentImage { 
	margin: auto; 
	display: block; 
	width: 80%; }
.popup__content { 
	width: 65%; 
	padding: 0 0; 
	min-width: 728px; }

.backpack__itemLevel {
	position: absolute;
	right: -5px;
	bottom: -5px;
	z-index: 1;
	font-size: 12px;
	width: 20px;
	height: 20px;
	border-radius: 20px;
	line-height: 22px;
	vertical-align: middle;
	text-align: center;
	box-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
	font-weight: bold;
}
.panel {
	width: 100%;
	padding: 10px;
	margin-bottom: 10px;
	box-sizing: border-box;
	border-bottom: 1px solid #dedede;
	border-radius: 3px;
}
.panelProfile__avatar {
	width: 170px;
	height: 170px;
	padding: 6px;
	position: relative;
	border-radius: 3px;
	margin: 151px 0 20px 20px;
}
.panelProfile__userInfoWrap {
	margin-top: 287px;
	min-height: 110px;
	padding: 10px 10px 10px 200px;
}

}
