css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kinox.to"), domain("kinos.to"), domain("kinox.TV"), domain("kinox.ME"), domain("kinox.SI"), domain("kinox.IO"), domain("kinox.SX"), domain("kinox.AM"), domain("kinox.NU"), domain("kinox.SG"), domain("kinox.GRATIS"), domain("kinox.MOBI"), domain("kinox.LOL"), domain("kinox.WTF"), domain("kinox.FUN"), domain("kinox.FYI"), domain("kinox.CLOUD") {
html {
    background: url("https://i.ibb.co/TM5wB5v/dd-bg.jpg") repeat-x scroll left top #040813 !important;
}

body {
    background: 0 0 !important;
}

#header {
    opacity: .8 !important;
}

#CoverFlow>*,#dontbeevil,.CommentArea,.MirrorModule,.ModuleContent,.ModuleDefault,.beep {
    background-color: rgba(0,0,0,.2)!important;
    background-image: none!important;
    color: #fff !important;
}

.browse.left,.browse.right {
    width: 0;
    height: 0;
    -webkit-transform: rotate(360deg)!important;
    border-style: solid!important;
    transition: 1s !important;
}

.browse.right {
    background-image: none!important;
    border-width: 15px 0 15px 26px!important;
    border-color: transparent transparent transparent rgba(0,0,0,.4) !important;
}

.browse.right:hover {
    border-color: transparent transparent transparent rgba(0,0,0,.7) !important;
}

.browse.left {
    border-width: 15px 26px 15px 0!important;
    border-color: transparent rgba(0,0,0,.4) transparent transparent !important;
}

.browse.left:hover {
    border-color: transparent rgba(0,0,0,.7) transparent transparent !important;
}

#navigation {
    border: 0 !important;
}

#mainmenu,.browse.left {
    background-image: none !important;
}

#mainmenu,.BL,.DropDown,li {
    border: 0 !important;
}

#navigation,.ModuleHead.mHead,.bottom_navi,.head.mHead,.top_navi,tr {
    background-image: none!important;
    background-color: rgba(0,0,0,.6) !important;
}

.display.SearchTable,.display.SearchTable.SortMe.CommonTable {
    background: 0 0 !important;
}

.top_navi {
    text-decoration: none!important;
    text-shadow: none !important;
}

#mainmenu,.top_navi {
    color: #fff !important;
}

.item>a,.sub>span,a,legend {
    color: #add8e6 !important;
}

.MiniEntry,.ModuleAnnounceMent,.Odd.parentalguidiance {
    background-color: rgba(0,0,0,.5)!important;
    background-image: none!important;
    color: #fff !important;
}

.Details.StyleA,.Even.parentalguidiance,.foot {
    background-color: rgba(0,0,0,.6)!important;
    background-image: none !important;
}

.OverlayLabel,label {
    color: #fff !important;
}

.a4dbf {
    display: none !important;
}

#AjaxStream>a>img {
    height: 100px!important;
    background-color: #000!important;
    opacity: .2 !important;
}

#first,#last,#mainmenu,#next,#prev,.first.paginate_button.paginate_button_disabled,.last.paginate_button.paginate_button_disabled,.next.paginate_button.paginate_button_disabled,.previous.paginate_button.paginate_button_disabled {
    background-color: rgba(0,0,0,.6) !important;
}

.LetterExtension {
    background-color: rgba(0,0,0,.4) !important;
}

.LetterMode.LetterMode_active,.paginate_active,.selected {
    background-color: rgba(255,255,255,.3)!important;
    color: #000 !important;
}

.paginate_button {
    background-color: rgba(0,0,0,.3)!important;
    transition: 1s !important;
}

.paginate_button:hover {
    background-color: rgba(0,0,0,.8) !important;
}

ul.PopupMenu {
    background: rgba(0,0,0,.7)!important;
    box-shadow: none !important;
}

.entry_description,tbody {
    color: #fff !important;
}

#search,.CommonModuleTable,.Specialnews.parentalguidiance:hover {
    background: rgba(255,255,255,.3) !important;
}

#Block_aed34b9f60ee115dfa7918b742336277,.FullModuleTable.CommonTable {
    background: rgba(255,255,255,.1) !important;
}

#header {
    height: 90px;
    background-image: url("https://i.ibb.co/s23KmZY/header.jpg") !important;
}

.UltraOpt {
    display: none  !important;
}

a.OverlayLabel, span.OverlayLabel {
    font-size: 14px  !important;
    font-family: 'Poppins', sans-serif;
    font-weight: 300;
    src: url("https://fonts.gstatic.com/s/poppins/v5/pxiByp8kv8JHgFVrLDz8Z11lFd2JQEl8qw.woff2") format("woff2");
}

.Opt.rightOpt.Hint, .Opt.leftOpt.Headlne {
    font-size: 13px  !important;
}
}
