css:
@-moz-document domain("ekampus-beta.anadolu.edu.tr"), domain("denemesinavi.anadolu.edu.tr") {
div #soru.well.well-lg {
	background-color: #f5f5f5;
	border-radius: 5px;
	box-shadow: rgba(0, 0, 0, .05) 0 1px 1px inset;
	box-sizing: border-box;
	margin-bottom: 20px;
	min-height: 20px;
	padding: 10px;
}

#AnswerExplanation {
	background-color: #f5f5f5;
	border: 1px solid #bbbaba;
	border-radius: 5px;
	box-shadow: rgba(0, 0, 0, .05) 0 1px 1px inset;
	box-sizing: border-box;
	margin-bottom: 10px;
	min-height: 10px;
	padding: 10px;
}

.btnnextprev {
	background-color: #8ae3ff;
	padding-top: 10px;
	border: 1px solid #7d7b7b;
	padding-right: 50px;
	margin-bottom: 20px;
	font-weight: 700;
	padding-bottom: 10px;
	padding-left: 50px;
}
div #mycont.container {
	box-sizing: border-box;
	font-family: Hanuman;
	margin-left: auto;
	margin-right: auto;
	padding-left: 15px;
	padding-right: 15px;
	width: 1100px;
}

.btn-default {
	color: rgb(51, 51, 51);
	background-color: rgb(255, 255, 255);
	border-color: rgb(23, 23, 23);
}

.btn:hover {
	color: rgb(0, 0, 0);
	text-decoration: none;
	border: solid 1px;
}


label {
	box-sizing: border-box;
	font-weight: 400;
	margin-bottom: 5px;
	max-width: 100%;
}
body {
	background-color: #484848;
	box-sizing: border-box;
	color: #333;
	font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 14px;
	line-height: 1.42857;
	margin: 0;
}
.container {
	background-color: #e6e4e4;
	color: #333;
}

#correctAns {
	width: 100%;
	border-radius: 5px;
	border: 1px solid #bbbaba;
	box-shadow: rgba(0, 0, 0, .39) 0 1px 1px inset;
	background-color: #bcffae;
	font-size: 30px;
	padding: 5px;
	font-weight: 700;
	margin: 20px 0px 10px;
	text-align: center;
}
}
