css:
@-moz-document regexp("https://www\\.newgrounds\\.com/(?!help).*") {
/* NG '04 (ver.06) "7/24/2019 7:31 PM EST") theme by solitonmedic */
/* assets used belongs to Newgrounds.com, Inc. */


.background {
    background-image: url('https://images2.imgbox.com/e3/0f/X0mrn7YP_o.png') !important;
    background-repeat: repeat-x !important;
    background-position: initial !important;
}
/* Divider (Appears in notification pods, between forum posts, review boxes etc) */
 div .column::after, article .column::after, section .column::after {
    content:"--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------";
    background-image: url(https://images2.imgbox.com/15/24/g0pcoRxQ_o.png) !important;
    color: #0000ff00;
}

/* forum stuff */
div.forum_list div.forum *[class^="bbs-"]::before {
    height: 59px;
    width: 54px;
    background: blue;
    background-size: 53px 51px;
}
div.forum_list div.forum .bbs-new::before {
    background-position: 0px -78px;
    background: url(https://images2.imgbox.com/ad/f1/WAD1nkNz_o.gif) no-repeat -1px 0px;
    left: -7px;
}
div.forum_list div.forum .bbs-old::before {
    background-position: 0px -78px;
    background: url(https://images2.imgbox.com/c1/e1/Zq6LCTeI_o.gif) no-repeat -1px 0px;
    left: -7px;
}

/* Icons in the BBS' frontpage*/
div.forum_legend *[class^="bbs-"]::before {
    background: url(https://images2.imgbox.com/68/48/n9M8cJzO_o.png);
}
div.forum_legend .bbs-old::before {
    background-position: 0px 1px;
    width: 56px;
    left: -10px;
}
div.forum_legend .bbs-new::before {
    background-position: 0px 45px;
    width: 56px;
    left: -7px;
}

/*Global background color modifier*/
div.pod-body, article.pod-body, section.pod-body, form.pod-body, footer.pod-body, form.podcontent, div.podcontent {
    background-color: #2f3238;
}
div.forum_list div.row {
    background-color: #2f3238;
}

div.forumthread-status span::before {
    width: 44px;
    height: 35px;
}
div.item-forumthread-medium div.forumthread-status {
    height: 25px;
    padding-bottom: 8px;
    padding-right: 10px;
}

/* Forum icons */
div.forumthread-status span.icon-sticky::before {
    background: url(https://images2.imgbox.com/1c/dc/clwGcoSo_o.gif);
}
div.forumthread-status span.icon-locked::before {
    background: url(https://images2.imgbox.com/5e/6b/0E54tMVT_o.gif);
}
div.forumthread-status span.icon-general.active::before {
    background: url(https://images2.imgbox.com/00/e8/AmY20zoI_o.gif);
}
div.forumthread-status span.icon-new.active::before {
    background: url(https://images2.imgbox.com/97/b0/HLOnjUS3_o.gif);
}
div.forumthread-status span.icon-general::before {
    background: url(https://images2.imgbox.com/fb/f1/h522gtlC_o.gif);
}
div.forumthread-status span.icon-new::before {
    background: url(https://images2.imgbox.com/aa/41/zY3opXYa_o.gif);
}
div.forumthread-status span.icon-news::before {
    background: url(https://images2.imgbox.com/1f/13/OK4XhVr2_o.gif);
}

/* Non-supporter and Supporter stars in BBS posts */
/*Supporter*/
div.bbs-post-author-bg.supporter > div:first-child /* top */ {
    background: url(https://images2.imgbox.com/42/b4/uFMSfV5a_o.png) top right no-repeat;
}
div.bbs-post-author-bg.supporter > div /* mid */{
    background: url(https://images2.imgbox.com/1e/96/LfLHzBX7_o.png) top right repeat-y;
}
div.bbs-post-author-bg.supporter > div:last-child /* bottom */ {
    background: url(https://images2.imgbox.com/2d/b0/kdLj6cno_o.png) top right no-repeat;
}
/*Member*/
div.bbs-post-author-bg > div:first-child {
    background: url(https://images2.imgbox.com/a0/f1/MtE3lNEe_o.png) top right no-repeat;
    height: 225px;
    width: 185px;
    flex: unset;
}
div.bbs-post-author-bg > div {
    background: url(https://images2.imgbox.com/07/6a/MHkxSucL_o.png) top right repeat-y;
    width: 181px;
    flex: 1;
}
div.bbs-post-author-bg > div:last-child {
    background: url(https://images2.imgbox.com/92/72/rn93KZjt_o.png) top right no-repeat;
    height: 12px;
    flex: unset;
}

/* submission ratings */
 div.pod-head h2.rated-t::before, header.pod-head h2.rated-t::before, div.podtop h2.rated-t::before {
    background: url(https://images2.imgbox.com/f5/3b/RvPQ18lv_o.gif) 0px 0px !important;
}
div.pod-head h2.rated-e::before, header.pod-head h2.rated-t::before, div.podtop h2.rated-t::before {
    background: url(https://images2.imgbox.com/db/33/hmNRiAM4_o.gif) 0px 0px !important;
}
div.pod-head h2.rated-m::before, header.pod-head h2.rated-t::before, div.podtop h2.rated-t::before {
    background: url(https://images2.imgbox.com/65/dd/RkYhd1Qi_o.gif) 0px 0px !important;
}
div.pod-head h2.rated-a::before, header.pod-head h2.rated-t::before, div.podtop h2.rated-t::before {
    background: url(https://images2.imgbox.com/39/ed/VGbUlQ4l_o.gif) 0px 0px !important;
}

/* Gradient modifiers for various headers*/
div.pod-head, header.pod-head, div.pod-fakehead, header.pod-fakehead, div.podtop, *.header-gradient {
    background: #211208;
    background: -webkit-linear-gradient(top, #945f18 0%, #311507 48%, #332109 53%, #211208 100%) !important;
    background: linear-gradient(to bottom, #945f18 0%, #311507 48%, #332109 53%, #211208 100%) !important;
}

/* Review boxes, input areas, text editors and etc */
textarea  {
    min-height: 1em;
    text-overflow: ellipsis;
    border: 0;
    background-color: #cc450c !important;
    -webkit-box-shadow: inset 0px 2px 2px 0px #fd3841 !important;
    -moz-box-shadow: inset 0px 2px 2px 0px rgba(0, 0, 0, 0.3) !important;
    box-shadow: inset 0px 2px 2px 0px #fd3841 !important;
    background: -moz-linear-gradient(top, #667884 0%, #a3b2b9 100%) !important;
    background: -webkit-linear-gradient(top, #cc6600 0%, #ff9966 100%) !important;
    background: linear-gradient(to bottom, #cc6600 0%, #ff9966 100%) !important;
}

select {
border: 0;
    background-color: #cc450c;
    -moz-box-shadow: inset 0px 2px 2px 0px rgba(0, 0, 0, 0.3);
    background: -moz-linear-gradient(top, #667884 0%, #a3b2b9 100%) !important;
    background: -webkit-linear-gradient(top, #cc6600 0%, #ff9966 100%) !important;
    background: linear-gradient(to bottom, #cc6600 0%, #ff9966 100%) !important;
}


.ql-editor  {
    margin-top: 1px;
    background-color: #fda238 !important;
    border-bottom-left-radius: 4px !important;
    border-bottom-right-radius: 4px !important;
    min-height: 80px;
    max-height: 320px;
    -webkit-box-shadow: inset 2px 4px 11px -2px #ff000c !important; 
    -moz-box-shadow: inset 2px 4px 11px -2px rgba(0, 0, 0, 0.6) !important;
    box-shadow: inset 2px 4px 11px -2px #ff000c !important;
}

/* Color modifiers for type and other markups, etc*/
.ql-editor p {
    color: black !important;
}

.ql-editor ul {
    color: black !important;
}

.ql-editor ol {
	color: black !important;
}

.ql-editor a {
    color: blue !important; /* links */
}

/**/
div.notification-bar .search input {
    border: none !important; 
    margin: 0px !important;
    padding: 4px 12px !important;
    padding-right: 24px !important;
    border-radius: 999px !important;
    background: #a45454 !important;
    width: calc(100% - 36px) !important;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    color: #ffcc00 !important;
    background-image: url(https://images2.imgbox.com/5d/de/zEMm3X8J_o.png) !important; /* Embeds a image right inside the main search bar*/
    background-blend-mode: overlay !important;
}

input[type=text] {
    background-color: #fda238;
    background: -moz-linear-gradient(top, #667884 0%, #a3b2b9 100%) !important;
    background: -webkit-linear-gradient(top, #fd5c3e 0%, #fda238 100%) !important;
    background: linear-gradient(to bottom, #fd5c3e 0%, #fda238 100%) !important;
}

div.expand-condensed /* Changes gradients and shadow effect on notification pods*/ {
	background: -webkit-linear-gradient(top, rgba(15, 11, 12, 0) 0%, #2f3238 66%, #2f3238 100%);
	background: linear-gradient(to bottom, rgba(15, 11, 12, 0) 0%, #2f3238 66%, #2f3238 100%);
}
}
