css:
@-moz-document domain(servicedesk.microsoft.com) {
html, body, div, .area-path-search[disabled], .sd-area-path-container[disabled], .c-textarea textarea[readonly], sd-layout .sd-select.c-select-menu[aria-disabled=true]>a, input.c-text-field[type=text][readonly], input.c-text-field[type=number][readonly], input[type=email][readonly], input, .form-group .form-control.c-text-field, .c-textarea textarea, .dialog-body .available-group-container ul, .dialog-body .group-container ul, sd-layout .sd-select.c-select-menu .c-menu-item a, .customershell .main-content .pivots-panel, .loading-overlay:before {
    background: #222 !important;
    color: #eaeaea !important;
}
.c-menu, .sd-area-path .node-item.node-focused, .feedback-button {
    background: #222;
}
.c-button div {
    background: none !important;
}
a.c-hyperlink, .c-glyph:after, .c-glyph:before, .c-glyph:hover:after, .c-glyph:hover:before, email-body .email-reader a:link, email-header .email-fields .c-hyperlink, a.c-action-trigger, button.c-action-trigger {
    color: #b6b5ff !important;
}
.c-table thead th, .c-table thead th button, sd-layout .sd-select.c-select-menu>a, .c-select-menu>a, .sd-glyph-twentyFourSeven::before, .sd-glyph-collaboration::before, .sd-glyph-ra::before, .sd-glyph-pubsec::before, .sd-rich-text-list-popper ul .list-item {
    color: #aaa;
}
.c-search button, .c-search input[type=search] {
    background: #222;
    color: #eaeaea;
    border: 0.5px solid #aaa;
}
sd-search button.c-button {
    border: none !important;
}
.dashboard-container, .sd-tile-layout, .tile-style, .tile-group-flex {
    background: #222 !important;
    color: #eaeaea;
    box-shadow: none !important;
}
.sd-tile-layout .tile-group-flex .tile-style {
    border: 0.5px solid #aaa;
}
.c-checkbox input[type=checkbox]+span:before {
    box-shadow: inset 0 0 0 1px #aaa, inset 0 0 0 10px transparent !important;
}
::placeholder {
    color: #999 !important;
}
section header a {
    color:#e2bfff !important;
}
sd-layout .bg-neutral-90, sd-layout .layout-wrap div {
    background: #111 !important;
    color: #eaeaea;
}
sd-layout .bg-neutral-80 {
    background: #333333 !important;
    color: #eaeaea !important;
}
sd-layout .ct-table .ct-row-detail-expandable.ct-row-expanded, sd-layout .ct-table .ct-row-detail-expandable:hover, sd-layout .ct-table .ct-row-expandable.ct-row-expanded, sd-layout .ct-table .ct-row-expandable:hover {
    background-color: #333333 !important;
    border-color: none !important;
}
.customershell .main-content .pivots-panel .c-pivot header, sd-layout sd-pivots .c-pivot header, .sd-rich-text-list-popper ul .list-item:hover {
    background: #333 !important;
    color: #eaeaea;
}
span {
    color: #aaa !important;
}
.case-basic-info .section-2 .c-select-menu .c-menu-item>a {
    color: #eaeaea;
}
.c-menu-item a, .customershell .pivots-panel commercial .ct-table .ct-row-expandable.ct-row-expanded, .customershell .pivots-panel commercial .ct-table .ct-row-expandable:hover {
    background: #333;
}
::selection {
    background: #2d8e22;
    color: #bababa;
}
::-webkit-scrollbar, ::-webkit-scrollbar-corner, ::-webkit-scrollbar-track-piece {
    background: #101010 !important;
}
::-webkit-scrollbar {
    width: 17px !important;
    height: 17px !important;
}
::-webkit-scrollbar-thumb {
    background: #333333;
    border: 3px solid #101010 !important;
    border-radius: 8px !important;
}
::-webkit-scrollbar-thumb:hover {
    background: #444444;
}
}
