css:
@-moz-document domain("my.meest.us") {
    .dashboard-empty-content h4 {
        line-height: 70px;
    }
    .block-styled .block-content {
        height: auto;
        min-height: 70px;
    }
    #main-container>.content:nth-child(2) {
        display: flex;
        flex-direction: column;
        width: 100%;
    }
    #main-container>.content:nth-child(2)>.block:nth-child(1) {
        order: 2;
    }
    #main-container>.content:nth-child(2)>.block:nth-child(2) {
        order: 1;
    }
    #js-shipment-grid-form tr.shipment-item td:nth-child(4) {
        font-size: 10px;
    }
    table.table-row-block > thead > tr > th{
        font-size: 14px;
    }
    table.table-row-block > thead > tr > th:nth-child(7),
    table.table-row-block > thead > tr > th:nth-child(8){
        font-size: 12px;
    }
    table.table-row-block tr > td:last-child{
        min-width: 200px;
    }
    table.table-row-block tr > td:nth-child(8),
    table.table-row-block tr > td:nth-child(7){
        width: 100px;
    }
    span[data-original-title^="Митне"] {
        color: #8c0101;
    }
    span[data-original-title^="Відправка"][data-original-title*="Львів"] {
        color: #279f0c;
    }
    span[data-original-title^="ДОСТАВЛЕНО"] {
        background: #ecffe6;
    }
    span[data-original-title^="ВЫДАНО"] {
        background: #ffffe6;
    }
    span[data-original-title^="ДОСТАВЛЕНО"], 
    span[data-original-title^="ВЫДАНО"] {
        color: #24c200;
    }
    span[data-original-title*="доручення"]{
        color: #279f0c;
    }
    span[data-original-title*="Надходження"] {
        font-weight: bold;
    }
    table.table-row-block>tbody>tr {
        font-size: 12px;
    }
    table.table-row-block>tbody>tr>td {
        padding: 5px;
    }
    span.shipment-status-label {
        font-size: 12px;
    }
    .text-success, #js-shipment-grid-form tr.shipment-item[data-is-status-sent="0"] td.text-success {
        color: #0030a4;
    }
    #js-shipment-grid-form tr.shipment-item[data-can-be-delivered="0"] td.text-primary-light {
        color: #a2a2a2;
    }
    #js-shipment-grid-form tr.shipment-item[data-can-be-delivered="0"][data-is-status-sent="1"] {
        opacity: 0.7;
    }
    #js-shipment-grid-form tr.shipment-item[data-can-be-delivered="0"][data-is-status-sent="1"]:hover {
        opacity: 1;
    }
    #js-shipment-grid-form tr.shipment-item[data-can-view-invoice="0"] td:nth-child(6) {
        color: #8c0101;
    }
}
