css:
@-moz-document url-prefix("https://www.lifewire.com/") {
body {
    background: #000;
    color: #eee;
}
.card, .footer {
    background: #000;
}
.header, .footer__dotdash-family {
    background-color: #444;
}
.leaderboard-deferred-footer, .leaderboard-footer, .leaderboard {
    background-color: #222;
}
.masonry-list-section {
    background-color: #181818;
}
.breadcrumbs-list-title a, .byline a, .card__title span {
    color: #eee;
}
.article-heading, .article-content, .article-content h3 {
    color: #ccc;
}
}
