css:
@-moz-document domain("zhihu.com") {
/* 全局字体 */
body {
    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;
}
.Topstory {
    padding: 0 !important;
}
/* 隐藏侧边栏 */
.TopstorySideBar,
.Question-sideColumn,
.GlobalSideBar,
.TopstoryItem--advertCard,
.css-1qefhqu,
.GlobalLeftSideBar {
    display: none !important;
}
.AppHeader-inner {
    width: 1000px !important;
    padding: 0 !important;
    box-sizing: content-box !important;
}
.AppHeader-logoContainer {
    width: 120px !important;
}
/* 宽屏显示内容 */
.Topstory--new .Topstory-container {
    max-width: 1000px !important;
    margin:0 auto !important;
}
.TopstoryMain,
.Question-mainColumn,
.Topstory-mainColumn,
.TopstoryV2-mainColumn {
    width: 100% !important;
}
.Topstory-mainColumn {
    margin: 0 !important;
}
.TopstoryItem .ZVideoItem {
    margin: 0;
}
.TopstoryItem .ZVideoItem .RichContent {
    margin-top: 20px;
    overflow: hidden;
}
/* 更多图标对齐 */
.ContentItem-more .ContentItem-arrowIcon {
    vertical-align: -2px;
}
.Layout-main.av-card,
.ArticleItem,
.AnswerItem {
    font-size: 120% !important;
}
/* 优化代码显示 */
.RichText pre {
    padding-top: .6em;
    padding-bottom: .6em;
    font-size: 90% !important;
    padding: 0.8em 1.2em !important;
}
.RichText code,
.RichText pre {
    font-family: 'JetBrains Mono', FiraMono-Regular, Menlo, Courier, monospace !important;
}
/* 优化专栏显示 */
.TitleImage,
.Post-Header,
.Post-RichTextContainer,
.ContentItem-time,
.Post-topicsAndReviewer,
.PostIndex-Contributions,
.Post-NormalMain > div,
.Post-NormalSub .Comments-container {
    width: 800px !important;
}
.Post-RichText {
    font-size: 18px;
}
/* 优化搜索页 */
.Footer {
    display: none !important;
}
.SearchResult-Card .ContentItem-title {
    color: #0084ff !important;
}
.Search-container .RichContent.is-collapsed .RichContent-inner {
    font-size: 16px !important;
}
/* 优化我的主页 */
.Profile-lightList {
    padding: 6px 20px;
}
.Profile-lightItem:first-child {
    border-top: 0 none !important;
}
.Profile-lightItem:last-child {
    border-bottom: 0 none !important;
}
.ProfileMain .RichContent {
    line-height: 1.8
}
}
