css:
@-moz-document url-prefix(https://www.watch2gether.com/rooms/) {
.ui.grid>[class*="six wide"].column {
    width: 100% !important;
}
#userbar {
    height: 100px !important;
}
.fixed {
    position: static !important;
}
/* Fix container */
 #client-container {
    border: 1px solid #ddd;
}
/* Bigger Player */
 #player {
    width: 100% !important;
}
#userbar-chat {
    width: 20% !important;
}
.main-content {
    padding-top: 130px;
}
.ui.segment[class*="bottom attached"] {
    background-color: gray;
}
#player-video-info {
    background-color: rgb(27, 28, 29) !important;
    color: white !important;
    border: 0.5px solid gray !important;
}
#player-menu>.menu {
    border: 0.5px solid gray !important;
}
#search-results-grid {
    border: 0;
}
.ui.card, .ui.cards>.card {
    background: #1b1c1d;
}
#search-results-data .header {
    color: white;
}
.ui.card>.content p, .ui.cards>.card>.content p {
    color: gray;
}
.ui.card .meta :last-child {
    color: gray;
}
.ui.card>.extra a:not(.ui), .ui.cards>.card>.extra a:not(.ui) {
    color:white;
}
#w2g-video {
    border: 1px solid gray !important;
}
.adsuserbar img, .adsuserbar div {
    display:none !important;
}
#userbar .card .content .header {
    color: white;
}
.ui.cards>.card {
    border: 1px solid gray !important;
}
.ui.action.input:not([class*="left action"])>input {
    background: #1b1c1d !important;
    border: 1px solid gray !important;
}
.ui.action.input:not([class*="left action"])>.button:last-child, .ui.action.input:not([class*="left action"])>.buttons:last-child>.button, .ui.action.input:not([class*="left action"])>.dropdown:last-child {
    background: #facd3b !important;
    border: 1px solid gray !important;
    color: black;
}
.ui.form .field .ui.input input, .ui.form .fields .field .ui.input input {
    color: lightgray;
}
.ui.segment:last-child, .footer {
    background: #1b1c1d !important;
}
.emoji-attribution {
    color: white !important;
}
.ui[class*="center aligned"].segment {
    background: none !important;
}
#chat-messages-table .ui.pointing.label {
    background: lightgray !important;
}
}
