@-moz-document domain("mashtips.com") {
/*__________________________________________________________________________________*/

.td-big-grid-post,
.td-block-span12,
.td-mega-span,
.td-pb-span4,
.td-ps-bg .td-social-network,
.td-related-row .td-related-span4,
.td_block_15 .td-block-span4,
.td_social_type {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.td-big-grid-post:hover,
.td-block-span12:hover,
.td-mega-span:hover,
.td-pb-span4:hover,
.td-ps-bg .td-social-network:hover,
.td-related-row .td-related-span4:hover,
.td_block_15 .td-block-span4:hover,
.td_social_type:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}


li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

.td-ps-bg .td-social-network,
selector:hover {
	opacity: 1!important;
}

}

