@-moz-document domain("gitlab.com") {
body,
textarea.js-gfm-input {
    font-family: Vazir;
}

.md:not(.use-csslab) li {
    margin-right: 28px;
    padding-right: 0;
}

/* Typography */
.md:not(.use-csslab) h2 {
    font-weight: 900;
}

.md:not(.use-csslab) ul.task-list>li.task-list-item {
    padding-left: 28px;
    margin-left: 0 !important;

    padding-right: 28px;
    margin-right: 0 !important;
}

.md:not(.use-csslab) ul.task-list>li.task-list-item>input.task-list-item-checkbox {
    right: 8px;
    left: 0px;
}

/** Right Sidebar */
.right-sidebar {
	text-align: right;
}
}
