css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com")
{
  .video-extras-sparkbar-likes
  {
    background: #ff0000 none repeat scroll 0% 0%;
  }

  .video-extras-sparkbar-dislikes
  {
    background: #ff0000 none repeat scroll 0% 0%;
  }

  .like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled::before
  {
    background: transparent url("https://i.hizliresim.com/k9EzRm.png");
  }

  .like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content
  {
    color: #ff0000;
  }

  .like-button-renderer-dislike-button.yt-uix-button-toggled::before
  {
    background: transparent url("https://i.hizliresim.com/grX0D2.png");
  }

  .like-button-renderer-dislike-button.yt-uix-button-toggled .yt-uix-button-content
  {
    color: #ff0000;
  }

  .watch-view-count
  {
    color: #ff0000;
  }

  .comments .like-count
  {
    color: #ff0000;
  }

  .comments .sprite_like_on
  {
    background: transparent url("https://i.hizliresim.com/WqbvyL.png");
  }

  .comments .sprite_dislike_on
  {
    background: transparent url("https://i.hizliresim.com/lqEGDk.png");
  }

  .comments .sprite_like
  {
    background: transparent url("https://i.hizliresim.com/16NWop.png");
  }

  .comments .sprite_dislike
  {
    background: transparent url("https://i.hizliresim.com/bVyX0d.png");
  }
}
