css:
@-moz-document domain(playground.ru) {
:root {
  --color1: #63b97f;
  --color2: #4B8B60;
  --color3: #2A2A2C;
  --color4: #212122;
  --color5: #19191A;
  --color6: #87878e;
  --color7: #333333;
  --color8: #5b5b5f;
--color30: #b31919;
    --color31: #6c0f0f;
  --color9: rgba(33,33,34,0.9);
  --color10: rgba(92, 96, 101, 0.16);
  --color11: rgba(29, 32, 35, 0.32);
  --filt2:hue-rotate(270deg)brightness(1.3)saturate(1.5) !important;  
  --filt22:hue-rotate(270deg)brightness(1.3)saturate(1.5) !important;
  --dots: url('https://i.imgur.com/qAUbD7O.png');
  --brigther_font_color: #b6b6bd;
}

.page-main-column {
    background: var(--color5);
    min-height: 800px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.2);
    padding: 20px;
}
 /*[[mini_dialog]]*/
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: var(--color6);
    background-color: var(--color3);
}

 /*[[mini_dialog]]*/
hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid var(--color3);
    margin: 0 0 1em;
    padding: 0;
}
.article-metadata {
    border-color: var(--color3);
    border-style: solid;
    border-width: 1px 0;
    padding: 4px 0;
    margin-bottom: 30px;
}
.article-title {
    font: normal 4.2rem/1 "PT Sans Narrow", Arial, sans-serif;
    color: var(--color6);
    display: inline;
}
.article-related-content .files-listing .item {
    padding: 8px 0;
    font: bold 1.3rem/1 Arial, sans-serif;
    border-top: 1px solid var(--color3);
    margin: 0;
}
.article-related-content a {
    color: var(--color6);
    text-decoration: none;
}
.comment-row {
    padding: 20px 0;
    min-height: 110px;
    border-bottom: 1px solid var(--color3);
    border-top: 1px solid var(--color3);
    position: relative;
    margin-top: 0;
}
a {
    color: var(--color6);
    text-decoration: none;
}
.download-content-list .module-content .item {
    margin: 0;
    padding: 15px 0 20px 0;
    position: relative;
    border-bottom: 1px solid var(--color3);
    border-top: 1px solid var(--color3);
}
.article-content {
    font: normal 1.6rem/1.5 Arial, sans-serif;
    color: var(--color6);
}
.btn-download {
    text-align: left;
    margin-top: 5px;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
    background-color: transparent;
    background-image: linear-gradient(to right, var(--color3) 0%, var(--color3) 50%, var(--color3) 100%);
    background-position: right center;
    transition: 0.5s;
    background-size: 200% auto;
    border-color: var(--color3);
    color: var(--color6);
}
.btn-download .btn-right {
    padding: 0 20px 5px 25px;
    overflow: hidden;
    border-left: 1px solid var(--color3);
}
.btn-download:hover {
    background-position: left center;
    color: var(--color30);
    box-shadow: 0 0 4px rgba(0,0,0,0.4);
}
.btn-download .btn-left {
    padding: 5px 22px 5px 12px;
    border-right: 1px solid var(--color7);
    font-size: 3rem;
}
.content-comments-header {
    margin-bottom: 20px;
    background: var(--color4);
    color: var(--main-color);
    padding: 15px 0;
    text-align: center;
    font-weight: bold;
    text-transform: uppercase;
}
.content-rating-area {
    padding: 15px;
    background: var(--color4);
    margin: 20px 0;
    text-align: center;
}
.content-block {
    margin: 0 0 20px 0;
    background-color: var(--color4);
    padding: 30px 20px;
    box-shadow: 0 0 1px rgba(0,0,0,0.2);
}
.user-profile-header .side-two {
    padding-left: 50px;
    border-left: 1px solid var(--color3);
}

.progress {
    height: 20px;
    margin-bottom: 20px;
    overflow: hidden;
    background-color: var(--color3);
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,.1);
    box-shadow: inset 0 1px 2px rgba(0,0,0,.1);
}


.module-header-navigation {
    border-bottom: 1px solid var(--color3);
    margin: 10px 0 0;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: var(--color6);
    background-color: var(--color5);
    background-image: none;
    border: 1px solid var(--color3);
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
.btn-standart {
    color: var(--color30);
    text-shadow: 0 1px 1px var(--color31);
    background-color: var(--color31);
    *background-color: var(--color31);
    background-image: -moz-linear-gradient(top, var(--color4), var(--color4) );
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from( var(--color4) ), to( var(--color4 ) ) );
    background-image: -webkit-linear-gradient(top, var(--color4), var(--color4) );
    background-image: -o-linear-gradient(top, var(--color4), var(--color4) );
    background-image: linear-gradient(to bottom, var(--color4), var(--color4) );
    background-repeat: repeat-x;
    border: 1px solid var(--color3);
    *border: 0;
    border-color: var(--color3) var(--color3) var(--color3);
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
    border-bottom-color: var(--color3);
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    *zoom: 1;
    -webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
}
.btn-standart:hover, .btn-standart:focus, .btn-standart:active, .btn-standart.active, .btn-standart.disabled, .btn-standart[disabled] {
    color: var(--color31);
    background-color: var(--color4);
    *background-color: var(--color5);
}
.social-accounts .btn {
    margin-left: 5px;
    font-size: 1.2rem;
    border-radius: 100%;
    border: 1px solid var(--color30);
    width: 28px;
    height: 28px;
    text-align: center;
    padding: 0;
    line-height: 26px;
    vertical-align: middle;
    font-size: 1.5rem;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    text-align: left;
    list-style: none;
    background-color: var(--color4);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid var(--color3);
    border: 1px solid rgba(0,0,0,.15);
    border-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.175);
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
}
.user-panel-area .user-panel-navigation .content {
    background: var(--color5);
    border: none;
    padding: 10px;
    text-align: left;
    display: block;
    white-space: normal;
    min-width: 300px;
    color: var(--color1);
    font-size: 12px;
    line-height: 1.7;
}
.user-panel-area .user-panel-navigation .footer {
    padding: 5px 10px 7px;
    background: var(--color5);
    border-top: 1px solid var(--color3);
    clear: both;
    border-radius: 0 0 4px 4px;
}
.user-panel-navigation .content a {
    color: var(--color6);
}
.user-events-stream .events-item {
    padding: 20px;
    background-color: var(--color4);
    box-shadow: 0 0 1px rgba(0,0,0,0.2);
}
.user-events-stream .event-preview {
    color: var(--color6);
}
.user-events-stream .quote {
    background: var(--color3);
    padding: 20px 50px 20px 20px;
    border-radius: 5px;
    display: block;
    position: relative;
}
.user-events-stream .quote:before {
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0 10px 10px 10px;
    border-color: transparent transparent var(--color3) transparent;
    position: absolute;
    top: -10px;
    left: 50px;
}
.btn-default {
    color: var(--color6);
    background-color: var(--color4);
    border-color: var(--color3);
}
.btn-default.active, .btn-default.focus, .btn-default:active, .btn-default:focus, .btn-default:hover, .open>.dropdown-toggle.btn-default {
    color: var(--color30);
    background-color: var(--color3);
    border-color: var(--color3);
}
.modal-header {
    min-height: 16.43px;
    padding: 15px;
    border-bottom: 1px solid var(--color3);
}
.modal-content {
    position: relative;
    background-color: var(--color4);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #999;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 6px;
    outline: 0;
    -webkit-box-shadow: 0 3px 9px rgba(0,0,0,.5);
    box-shadow: 0 3px 9px rgba(0,0,0,.5);
}
.dropdown-menu>li>a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: 400;
    line-height: 1.42857143;
    color: var(--color6);
    white-space: nowrap;
}
.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover
{
    color: var(--color6);
    text-decoration: none;
    background-color: var(--color5)
}
.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover {
    color: #555;
    background-color: var(--color5);
}
.main-page .page-main-column {
    background-color: var(--color4);
}
.features-slider-item a {
    display: inline-block;
    color: #fff;
    border-left: 1px solid var(--color30);
    border-bottom: 1px solid var(--color30);
}
.story-container {
    background-color: var(--color5);
    margin-bottom: 30px;
    position: relative;
    display: block;
}
.main-page .table-striped > tbody > tr:nth-of-type(odd) {
    background-color: var(--color5);
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid var(--color3);
}
.game-announcement-card {
    background-color: var(--color5);
    position: relative;
    white-space: normal;
    overflow: hidden;
}
.file-announcement .announcement-item {
    overflow: hidden;
    border-bottom: 1px solid var(--color3);
    padding: 10px 0;
    margin-bottom: 0;
    margin-top: 0;
}
.announcement-block.prizes .prize-item {
    white-space: normal;
    width: 177px;
    margin: 0 5px;
    background: var(--color5);
    text-align: center;
    position: relative;
    vertical-align: top;
    display: inline-block;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.2);
}
.announcement-flash-game {
    padding: 15px;
    border-radius: 10px;
    text-align: center;
    display: block;
    background: var(--color5);
    font: normal 1.3rem/1 Arial, sans-serif;
    position: relative;
    overflow: hidden;
}
.announcement-flash-nav .item {
    font: normal 1.5rem/1 Arial, sans-serif;
    margin: 0 3px 5px;
    display: inline-block;
    padding: 3px 5px 5px;
    background-color: var(--color5);
}
.flash-thumb {
    float: left;
    width: 168px;
    margin: 0 10px 20px 10px;
    line-height: 1;
    background: var(--color5);
    padding-bottom: 10px;
    min-height: 184px;
    height: 184px;
    position: relative;
}

.flash-partition-header a {
    color: var(--color6);
}
.table-bordered {
    border: 1px solid var(--color3);
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: var(--color4);
}
.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border: 1px solid var(--color3);
}
.user-image-frame-column {
    position: absolute;
    background: var(--color4);
    right: 0;
    top: 0;
    width: 350px;
    height: 100%;
}
.post-rules-content {
    box-shadow: 0 -5px 20px rgba(0, 0, 0, 0.2);
    background-color: var(--color3);
    padding: 20px 40px 30px 30px;
    font-size: 1.4rem;
    line-height: 1.5;
}
.bs-fieldset {
    background: var(--color3);
    padding: 10px 15px;
    margin-bottom: 15px;
    border-radius: 5px;
}
.user-games-set .item {
    padding: 10px;
    box-shadow: 0 0 1px rgba(0,0,0,0.2);
    background-color: var(--color4);
    margin: 0 0 12px 0;
}
.poll-module .block-content {
    padding: 20px 10px 10px 20px;
    background: var(--color3);
}
.main-navigation .horisontal {
    border-bottom: 1px solid var(--color3);
}
.wiki-content-area .module-header {
    border-bottom: 1px solid var(--color3);
    padding-bottom: 5px;
    margin: 0 0 20px 0;
}
.thumbnail {
    display: block;
    padding: 4px;
    margin-bottom: 20px;
    line-height: 1.42857143;
    background-color: var(--color4);
    border: 1px solid var(--color3);
    border-radius: 4px;
    -webkit-transition: border .2s ease-in-out;
    -o-transition: border .2s ease-in-out;
    transition: border .2s ease-in-out;
}
.media-announce-item {
    margin: 0 0 14px;
    display: flex;
    background-color: var(--color4);
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.comments-content .inner blockquote {
    border: 1px solid var(--color3);
    font-weight: normal;
    margin: 10px 0;
    font-size: 100%;
    border-radius: 3px;
    background: var(--color4);
}
.video-related-content .item {
    margin-bottom: 22px;
    background-color: var(--color4);
    display: block;
}
.video-related-content .item:hover {
    background-color: var(--color3);
}
.gp-game-card {
    background: var(--color4);
    padding: 20px 10px;
    margin: 6px 0 40px;
}
.gp-game-card-header {
    padding: 6px 5px 6px 20px;
    background: var(--color3);
    margin: 20px -10px 20px;
    font: normal 1.2rem/1 "PT Sans", Arial, sans-serif;
    text-transform: uppercase;
    text-shadow: 1px 1px 1px var(--color3);
}
.games-catalogue-flow .item {
    display: flex;
    background-color: var(--color4);
    margin: 15px;
    flex-basis: calc(50% - 30px);
    position: relative;
}
.page-aside .announcement-block .block-content {
    background-color: var(--color4);
    padding: 20px;
}
.forums-popular-game {
    margin-bottom: 10px;
    white-space: nowrap;
    overflow: hidden;
    background-color: var(--color4);
}
.forums-popular-game :hover {
    background-color: var(--color3);
}
.forum-category .item {
    margin: 0;
    border-bottom: 1px solid var(--color3);
    padding: 7px 5px 10px 5px;
}
}
