css:
@-moz-document domain(brick-hill.com) {
:root {
    --background-color: #FADEAE;
    --card-bg: #F9F6E2;
    --card-bg-dark: #f0edd8;
    --card-bg-darker: #f4f1dd;
    --card-title: #F9F6E2;
    
    --turquoise:#59BBAA;
    --turquoise-dark:#459780;
    --blue: #6fb6db;
    --red: #E91A2B;
    --red-darker: #9d111c;
    --green: #24822A;
    --orange: #F17D0C;
    --yellow: #FFC746;
    --yellow-darker: #dbaa3a;
    --brown: #63553C;
}
html, body {
    background-color: var(--background-color);
    /*background: url("https://i.imgur.com/oH0VH8e.png");*/
}
.card .content, .item-page, .tabs .tab-body {
    background-color: var(--card-bg);
}
.card .top {
    font-weight: 600;
    border-radius: 10px 10px 0px 0px;
}

.card .content {
    border-radius: 0px 0px 10px 10px;
}
.card .top {
    border: 0px solid transparent;
    color: var(--card-title) !important;
    background-color: var(--turquoise);
}
.card .top.red {
    border: 0px solid transparent;
    color: var(--card-title) !important;
    background-color: var(--red);
}

.card .top.green {
    border: 0px solid transparent;
    color: var(--card-title) !important;
    background-color: var(--green);
}
.card .top.orange {
    border: 0px solid transparent;
    color: var(--card-title) !important;
    background-color: var(--orange);
}
.card .top.blue {
    border: 0px solid transparent;
    color: var(--card-title) !important;
    background-color: var(--blue);
}
/* Tabs */
.tabs .tab-body {
    border: none;
}
.tabs {
    border-radius: 10px 10px 0px 0px;
    background-color: var(--turquoise);
}
.tabs .tab {
    border-radius: 10px 10px 0px 0px;
    background-color: transparent;
    border: none;
    color: var(--card-bg);
}
.tabs .tab.active {
    color: var(--yellow);
    background-color:var(--turquoise-dark);
}
/* Forum Tool Bar */
.card.forum-links .content {
    border-radius: 10px !important;
    background-color: var(--turquoise);
    
}
.card.forum-links .content a {
    color: var(--card-title) !important;
}
.divide {
    display: none;
}
/* Tag */
.tag {
    background: var(--yellow);
    color: var(--brown);
    font-weight: 600;
}
.tag::after {
    border-right-color: var(--yellow);
}

/* Read Thread */
.thread-info[style*="background-color: #F9F9F9"] {
    background-color: var(--card-bg-dark) !important;
}
/* Unread Thread */
.thread-info:hover {
    background-color: var(--card-bg-darker) !important;
}
/* Buttons */
.forum-button, .shop-search-button, .item-create-button, .forum-create-button {
    border-radius: 10px;
    padding: 10px 20px;
    background-color: var(--card-bg);
    color: var(--brown);
    font-weight: 600;
    border: 0px solid transparent;
    border-bottom: 2px solid #BAB494;
}
.forum-button.red {
    background-color: var(--red);
    color: var(--card-bg);
    border-color: var(--red-darker);
}
.forum-button.blue, .forum-button.green, .item-create-button, .shop-search-button, .forum-create-button {
    color: var(--card-bg);
}
/* Inputs */
button[style^="padding:4px;background-color:#F8F8F8;color:#CBCBCB;width:70px;"] {
    border: 0px solid transparent;
    padding: 10px !important;
    border-bottom: 2px solid #d4d4d4;
}
input {
    padding: 10px;
}
.search, textarea, input {
    border-radius: 10px;
    font-weight: 600;
    border: 0px solid transparent;
    border-bottom: 2px solid #d4d4d4;
}
/* Profile */
.profile-card:hover, .clan-card:hover {
    background-color: var(--card-bg-dark);
}
/* Shop */

.thumbnail.round-top[style*="background-color:#4F4F4F"] {
    background-color: #72BBAA !important;
}
.item-card-details {
    border-radius: 0px 0px 10px 10px;
    color: var(--card-bg);
    background-color: #EBE5CF;
}

/* -Search*/
.card[style="border-radius:5px;height:100%;"] .content {
    border-radius: 10px;
}
.shop-search .fas {
    color: var(--card-bg-dark);
}
.col-10-12.push-1-12 .col-3-12 .card[style^="border-radius:5px"] .content  {
    border-radius: 10px;
}
.shop-search input {
    border: 0px solid transparent;
    border-bottom: 2px solid var(--yellow-darker);
    border-radius: 10px;
    background-color: var(--yellow);
    color: var(--card-bg);
    font-weight: 600;
}
.shop-search input::placeholder {
    color: var(--card-bg-dark);
    font-weight: 600;
}
}
