@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {



/* *********************************** */
/* START COLOR CONFIGURATION PART      */
/* *********************************** */
	#teamLinks a,
	#online,
	#time,
	#ticker a,
	#footer a{
		/* TEAMLINKS GROSSI SENZA UNDERLINE */
		color:#800000!important;
	}
	#teamLinks a:hover,
	#ticker a:hover{
		/* TEAMLINKS AL PASSAGGIO DEL MOUSE */
		color:#CC0000!important; /* 4.1  */
	}
	.subMenuBox h3,.boxBody h2,h3.feed{
		color:red!important;   /*4.3*/
 		background: #fffc7e!important;
 		border: solid 1px yellow !important;
	}
	a{
		color:#400000!important;  
	}
	a:hover{
		color:#800000!important;  
		text-decoration:none!important; 
	}

	#menu {
		/* MENU HATTRICK COLORE SFONDO*/
		background: #CC0000 none  no-repeat scroll left top !important;
		border: 1px solid #000;
	}
	#menu a{
		/* MENU COLORE SCRITTE */
		color: yellow !important;
	}
	#menu a:hover{
		/* MENU HATTRICK AL PASSAGGIO MOUSE */
		background: #800000 !important;
	}
	.sidebarBox .boxHead h2, 
	.subMenuBox .boxHead h2, 
	.main .boxHead h2,
	.sidebarBox .boxHead .boxLeft h2,
	.subMenuBox .boxHead .boxLeft h2,
	.main .boxHead .boxLeft h2{
		/* BOX  : PARTE ALTA BORDI ARROTONDATI*/
		background: #CC0000 none  no-repeat scroll left top !important;
		border: 1px solid #000 !important;
		color:yellow!important; 
	}
	.main .boxHead h2 span, .main .boxHead h2 a {
		/* SCRITTA PARTE CENTRALE BORDI ARROTONDATI*/
		color:yellow!important; 
	}
	.main .boxHead h2 span, .main .boxHead h2 a:hover{
		/* SCRITTA PARTE CENTRALE BORDI ARROTONDATI al passaggio del mouse*/
		color:#800000!important;  
	}
	.sidebarBox .boxBody, 
	.subMenuBox .boxBody, 
	.main .boxBody {
		/* BOX PARTE CENTRALE BORDI*/
		background:#fff none repeat scroll 0 0!important;
		border-left:1px solid  #000 !important;
		border-right:1px solid  #000 !important;
	}


	.sidebarBox .boxFooter, 
	.subMenuBox .boxFooter, 
	.main .boxFooter {
		/* BOX PARTE BASSA BORDI ARROTONDATI*/
		background: #fff none  no-repeat scroll left top !important;
		border-left: 1px solid #000 !important;
		border-right: 1px solid #000 !important;
		border-bottom: 1px solid #000 !important;
	}

	.sidebarBox .boxFooter .boxLeft, 
	.subMenuBox .boxFooter .boxLeft, 
	.main .boxFooter .boxLeft{
		visibility:hidden !important;
	}
	#page{
		/* PAGINA BORDI ARROTONDATI*/
		border: 1px solid #989898 !important;
	}
	#top, #header, #content, #bottom, #page{
		/* PAGINA COLORE SFONDO CENTRALE*/
		background: #fffc7e none  no-repeat scroll left top !important;
	}

	.cfWrapper {
		/* FORUM : BORDINI BOX MESSAGGI */
		border: 1px solid #FF9966  !important;
	}
	.cfHeader {
		/* FORUM : HEADER BOX MESSAGGI */
		background: #FF9966  !important;
		color: #000000  !important;
	}
	.cfFooter {
		/* FORUM : FOOTER BOX MESSAGGI */
		background: #FFE4C4  !important;
		color: #fff  !important;
	}
	.cfHeader a{
		/* FORUM : HEADER LINK BOX MESSAGGI */
		color: #000000  !important;
	}
	.cfHeader a:hover{		/* FORUM : HEADER LINK HOVER BOX MESSAGGI */
		color: #FF6600  !important;  /*4.2*/
		text-decoration:none!important;  /*4.2*/
	}

	.date,#ft_bonuswage{
		color:#888!important;
	}


/* *********************************** */
/* END COLOR CONFIGURATION PART        */
/* *********************************** */







/* ************************************ */
/* START IMAGES CONFIGURATION PART      */
/* ************************************ */
	#logo {
		/* LOGO HATTRICK */
		background: transparent url(http://i38.tinypic.com/2ngra8j.jpg)  no-repeat scroll left top !important;

	}

	body{
		/* SFONDO HATTRICK */
		background: url(http://nrgjack.altervista.org/_altervista_ht/bgmio.png)   !important;
	}
	

  img[src*="/micro_star.gif"] {
    padding: 15px 0 0 16px!important;
    width: 0 !important;
    height: 0 !important;
    background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/b_star.png)  !important;
  }

  img[src*="/page/master_page.png"] {
    visibility:hidden  !important;
  }



  #ticker div {
    padding-left: 18px!important;
    background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/y_star.png)  !important;
  }




	img.scLive,
	img.scTransfer,
	img.scMail,
	img.scNewMail,
	img.scNotes,
	img.scSearch,
	img.scMySupporters,
	img.scReminders,
	img.reminder,
	img.scBookmarks,
	img.scDigit1,
	img.scDigit2,
	img.scDigit3,
	img.scDigit4,
	img.scDigit5,
	img.scChallenges,
	img.scManager,
	img.scMatches,
	img.scPlayers,
	img.scBids,
	img.scFans,
	img.scFed,
	img.scTraining,
	img.scMatches2,
	img.scEconomy,
	img.f_quote1,
	img.f_quote2,
	img.f_bold,
	img.f_italic,
	img.f_ul,
	img.f_hr,
	img.f_player,
	.bookmarkSmall img,
	img.f_team,
	img.f_match,
	img.f_fed,
	img.f_message,
	img.f_series,
	img.f_www,
	img.hotlist,
	img.matchOrder,
	img.matchOrderSet,
	img.matchOrderSetDisabled,
	img.matchOrderWrite,
	img.matchOrderWriteDisabled,
	img.matchHTLive,
	img.matchFriendly,
	img.matchLeague,
	img.matchCup,
	img.matchQualification,
	img.matchMasters,
	img.supporterRed,
	img.supporterGreen,
	img.playerUp,
	img.playerDown,
	img.matchHTReLive,
	img.globe,
	img.brokenLink {
		/* TUTTE LE IMMAGINI */
		/* background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/master_icons_dark.png)   !important;*/
	}
/* ************************************ */
/* END IMAGES CONFIGURATION PART        */
/* ************************************ */







/* ************************************ */
/* START STRUCTURE CONFIGURATION PART   */
/* ************************************ */
	#logo { 
		/* LOGO HATTRICK */
		height:147px!important;
		width:293px!important;
		top:-5px!important;
		left:2px!important;
padding:0!important;
	}
#header{
		height:180px!important;}
	#menu {
		/* MENU HATTRICK */
		-moz-border-radius: 9px;
		-webkit-border-radius: 9px;
		height:30px!important;
		padding-left:3px!important;
	}

	#menu a{
		/* MENU ALTEZZA */
		line-height: 30px !important; 
	}
#teamLinks{
margin-top:-45px!important; 
margin-left:300px!important;
}

#time{
position:relative;
top:-30px!important;

}
#online{
position:relative;
top:-30px!important;

}

#ticker{
margin-left:110px!important;
}

	#teamLinks a{
		/* TEAMLINKS GROSSI SENZA UNDERLINE */
		text-decoration:none!important;
		font-weight:bold!important;
		font-size:larger!important;
	}
	.sidebarBox .boxHead h2, 
	.subMenuBox .boxHead h2, 
	.main .boxHead h2,
	.sidebarBox .boxHead .boxLeft h2,
	.subMenuBox .boxHead .boxLeft h2,
	.main .boxHead .boxLeft h2,
	#page,
	#top,
	h3.feed,
	.boxBody h2,
	.subMenuBox h3{
		/* BOX,PAGE  BORDI ARROTONDATI ALTI*/
		-moz-border-radius-topleft : 9px !important;
		-webkit-border-top-left-radius : 9px !important;
		-moz-border-radius-topright : 9px !important;
		-webkit-border-top-right-radius : 9px !important;
		margin-left:0px !important;
	}

	.sidebarBox .boxHead , 
	.subMenuBox .boxHead , 
	.main .boxHead ,
	.sidebarBox .boxHead .boxLeft,
	.subMenuBox .boxHead .boxLeft,
	.main .boxHead .boxLeft{
		/* LEVO IMG BOX PARTE ALTA BORDI ARROTONDATI*/
		background: transparent none  no-repeat scroll left top !important;
	}

	.sidebarBox .boxFooter, 
	.subMenuBox .boxFooter, 
	.main .boxFooter,
	#page,
	#bottom{
		/* BOX,PAGE  BORDI ARROTONDATI BASSI*/
		-moz-border-radius-bottomleft : 9px !important;
		-webkit-border-bottom-left-radius : 9px !important;
		-moz-border-radius-bottomright : 9px !important;
		-webkit-border-bottom-right-radius : 9px !important;
	}


	h3.feed,
	.boxBody h2,
	.subMenuBox h3{
	text-align:center!important;
	}



/*FOXTRICK FIX*/
#foxtrick_links_box>div.boxHead>div.boxLeft{margin:0 1px 0 0 !important;padding:0 !important;}
#foxtrick_links_box>div.boxHead>div.boxLeft>div>h2{margin: 0 -9px  0 0 !important;}
.ft_sidebarBoxUnfolded>div.boxLeft>div{margin: 0 8px  0 0 !important;}



	
/* ************************************ */
/* END STRUCTURE CONFIGURATION PART     */
/* ************************************ */



}
