css:
@-moz-document url-prefix("https://www.anime-planet.com/") {
/* Made by hugonun */
/* https://github.com/hugonun */
/* License: CC-BY 4.0 */

/* Recolor main background color */
html, body, #siteHeader, #siteContainer {
  background-color:#222222 !important;  
}
/* Recolor main font color */
body {
  color:#eeeeee  !important;
}

/* Recolor header */
#siteHeader a, #siteHeader label {
  color:#fc5342 !important;
}
/* Other */
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
  color:#fc5342;
}
a {
  color:#fc5342;
}
a:hover {
  color:#eeeeee;
}
button, .button {
  background-color:#214988;
  color:#fff !important;
}
button:hover, .button:hover, button:focus, .button:focus {
  background-color:#eeeeee; 
  color: #214988 !important;
}
input, textarea {
  background-color: #333333;
  color:white;
}
.dropSearch input, .dropSearch select {
  background-color:transparent  !important;
}
.dropSearch {
  background-color:#333333  !important;
}
.dropSearch::after {
  background-color:transparent  !important;
}
#siteNav>ul>li:hover a,#siteUser:hover .loggedIn a {
  color:white !important;
}
#siteNav > ul > li ul, #siteUser .loggedIn ul {
  background-color:#333333 !important;
}
.cta {
  background-color: rgba(220,220,220,0.06);
}
.ui-tooltip {
  background-color: #222222;
  border-color: #333333;
}
.left .ui-tooltip-content {
  border-color: #222222;
  border-right-color: #222222;
}
.statusArea {
  background-color:#222222;
  color:white;
}
.card h2, .card h4, .card .cardName {
  color:#fc5342;
}
.card h2:hover,.card h4:hover,.card .cardName:hover {
 color:white;
}
.homepage section {
  border-bottom-color: #333333;
}
.quickFilter ul li {
  border-color:#333333;
}
.pagination a, .alpha a, .tabs ul.tabsUl li a {
  color:#fc5342;
}
.tabs .tab {
  border:none;
}
.tabs ul.tabsUl li a {
  color:#fc5342;
}
.selected, .selected a {
  color: white !important;
}
.subNav .selected, .subNav .selected a {
  color: black !important;
}
#advancedFilter .tab {
  background-color:#333333;
  border:none !important;
}
#advancedFilter ul.tabsUl li[class*="active"] a {
  background-color: #333333;
  border-color: #333333;
}
.tabs ul.tabsUl li[class*="active"] a {
  color:white;
}
.pure-form input[type="text"], .pure-form input[type="password"], .pure-form input[type="email"], .pure-form input[type="url"], .pure-form input[type="date"], .pure-form input[type="month"], .pure-form input[type="time"], .pure-form input[type="datetime"], .pure-form input[type="datetime-local"], .pure-form input[type="week"], .pure-form input[type="number"], .pure-form input[type="search"], .pure-form input[type="tel"], .pure-form select, .pure-form textarea {
  box-shadow:none;
}
.pillFilters {
  background-color:#222222;
  border:2px solid #333333;
}
.pillBottle .pill {
  background-color:white;
}
.pure-table thead {
  background-color:#333333;
  color:white;
}
.pure-table.striped tr:nth-child(2n-1) td {
  background-color:none;
  border:none;
}
.pure-table th a, .pure-table th span {
  color:white;
}
.pure-table.striped tr:nth-child(2n) td {
  border:none;
}
.tags li {
  background-color:#181818;
}
.pure-table th {
  border:none;
}
.tabs ul.tabsUl li[class*="active"] a {
  background-color:#222222;
  border: 1px solid #333333;
}
#comments {
  border: 1px solid #333333;
}
.ui-dialog, .ui-dialog .ui-widget-header, .ui-dialog .ui-dialog-content {
  background-color: #333333;
}
/* Xenforo */
#content .pageContent, .formPopup .controlsWrapper, #QuickSearch #commonSearches .button, .primaryContent, .xenOverlay .formOverlay .textCtrl:focus, .xenOverlay .formOverlay .textCtrl.Focus, .discussionList .sectionHeaders dd a[href]:hover, #header, .footer .pageContent, .Menu .sectionFooter, .xenOverlay .formOverlay .textCtrl {
  background-color: #222222 !important;
}
.sectionMain, .node .nodeLastPost, .sectionFooter, .Menu, .xenForm fieldset, .xenForm .formGroup, #DiscussionListOptionsHandle a, #QuickSearch, .heading, .xenForm .formHeader, .navTabs .navTab.selected.PopupOpen .navLink {
  background-color: #222222 !important;
}
.nodeList .categoryStrip .nodeTitle a {
  color:#fc5342 !important;
}
.primaryContent a, .pollBlock .question .questionText, .thread_view .threadAlerts dt {
  color:#fc5342 !important;
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
  border:none;
}
.discussionListItems .discussionListItem:nth-child(2n-1), .Menu .primaryContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
  border:none !important;
}
.discussionList .sectionHeaders, .nodeList .categoryStrip, .subHeading {
  border:none !important;
}
.pageContent a:link, .pageContent a:visited, .xenOverlay .formOverlay a {
  color:#fc5342 !important;
}
.discussionListItems .discussionListItem.sticky {
  background-color:#282828 !important;
}
.discussionListItems .discussionListItem {
  background-color:#222222 !important;
  border:none !important;
}
.discussionListItem .posterAvatar, .discussionListItem .stats {
  background:none !important;
}
.message .messageMeta {
  background-color:#333333 !important;
  border:none !important;
}
.messageUserBlock .arrow {
  border-right-color: #333333 !important;
}
.message .messageContent, .message .editDate, .message .likesSummary {
  border-right-color:#222222 !important;
  border-left-color:#222222 !important;
}
.messageUserBlock, .messageUserBlock div.avatarHolder, .Menu .secondaryContent, .profilePage .mast .section.infoBlock, .pollBlock .pollOption label:hover {
  background-color:#333333 !important;
}
.messageUserBlock h3.userText {
  background-color:#333333 !important;
}
.message a.reply {
  color:white !important;
}
.bbCodeQuote .quoteContainer, .bbCodeQuote .attribution {
  background-color: #333333 !important;
}
.bbCodeQuote .attribution {
  color:white;
}
.navTabs .navTab.selected .tabLinks {
  background-color:#3333 !important;
  border:none !important;
}
.pageContent nav .crumbs > .crust:first-child {
  margin-left:0;
}
.breadcrumb, .breadcrumb .crust a.crumb, .breadcrumb .crust a.crumb:hover, .breadcrumb .crust:hover a.crumb {
  background-color:#333333 !important;
}
.breadcrumb .crust .arrow {
  background-color:transparent !important;
}
.breadcrumb .crust .arrow span {
    border: none !important;
}
}
