#words {
    border-radius: 10px !important;
}

#inputfield {
    font-family: 'Montserrat', sans-serif !important;
}


body {
    font-family: 'Montserrat', sans-serif !important;
    background-color: black !important;
    background: none;
}

#speedtest-main #words {
    margin-left: auto !important;
    margin-right: auto !important;
}

.main-layout #main-content,
.main-layout .main-content {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
}

@media (min-width: 1200px) {
    .col-lg-4 {
        width: 32%;
    }
}

body * {
    box-shadow: none;
}

small {
    color: black !important;
}

body {
    background: rgb(254, 203, 111);
    background: -moz-linear-gradient(left, rgba(254, 203, 111, 1) 0%, rgba(252, 155, 112, 1) 15%, rgba(237, 106, 111, 1) 30%, rgba(203, 78, 108, 1) 48%, rgba(140, 65, 104, 1) 65%, rgba(81, 60, 99, 1) 82%, rgba(57, 59, 98, 1) 100%);
    background: -webkit-linear-gradient(left, rgba(254, 203, 111, 1) 0%, rgba(252, 155, 112, 1) 15%, rgba(237, 106, 111, 1) 30%, rgba(203, 78, 108, 1) 48%, rgba(140, 65, 104, 1) 65%, rgba(81, 60, 99, 1) 82%, rgba(57, 59, 98, 1) 100%);
    background: linear-gradient(to right, #ffb7c5 0%, #ffb7c5 15%, #ffb7c5 30%, #ffb7c5 48%, #ffb7c5 65%, #ffb7c5 82%, #ffb7c5 100%);
    color: #bdbdbd;
    object-fit: cover;
}

h2 {
    color: black;
}

span:not(.glyphicon) {
    font-family: 'Times New Roman', sans-serif !important;
    font-weight: 100 !important;
}

#row1:nth-child(2) {
    color: red !important;
}

.panel-success > .panel-heading {
    background-color: #e09100;
}

.panel-body {
    background-color: #ffb939
}

#table-data td {
    background-color: hsla(212, 100%, 100%, 0.4);
}

.highlight {
    border-radius: 7px !important;
    background-color: hsla(212, 100%, 100%, 0.4) !important;
    opacity: 1;
}

.highlight-wrong {
    border-radius: 7px !important;
    background-color: hsla(360, 100%, 51%, 0.4) !important;
    opacity: 1;
}

.btn-default {
    background-color: hsla(209, 100%, 100%, 0.5) !important;
    border: none;
}

.btn-default:hover {
    background-color: hsla(209, 100%, 100%, 0.7) !important;
    border: none;
}

.btn-primary {
    background-color: hsla(209, 100%, 51%, 0.5) !important;
    border-color: hsla(209, 100%, 51%, 0.01) !important;
}

.btn-primary:hover {
    background-color: hsla(209, 100%, 30%, 0.5) !important;
    border-color: hsla(209, 100%, 30%, 0.01) !important;
}

.btn-success {
    background-color: hsla(113, 100%, 51%, 0.44) !important;
    border-color: hsla(113, 100%, 51%, 0.01) !important;
}

.btn-success:hover {
    background-color: hsla(113, 100%, 30%, 0.44) !important;
    border-color: hsla(113, 100%, 30%, 0.01) !important;
}

#speedtest-main #auswertung-box #auswertung-result #result-table td > #share-on-facebook-button {
    background-color: hsla(209, 100%, 51%, 0.7) !important;
    border-color: hsla(209, 100%, 51%, 0.01) !important;
}

#speedtest-main #auswertung-box #auswertung-result #result-table td > #share-on-facebook-button:hover {
    background-color: hsla(209, 100%, 30%, 0.7) !important;
    border-color: hsla(209, 100%, 30%, 0.01) !important;
}

#badge-box {
    background-color: hsla(212, 100%, 4%, 0.1);
}


#auswertungbox {}

#words {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
    opacity: 1 !important;
    border: none !important;
    resize: both;
    max-width: 1200px;
    min-width: 200px;
    min-height: 110px !important;
    font-size: 2.2em !important;
}

#speedtest-main #row1 span {
    opacity: 1 !important;
}

#inputfield {
    border: none !important;
    box-shadow: none !important;
    border-radius: 15px;
    font-weight: bold !important;
}

#input-row {
    background-color: transparent !important;
}

#participation {
    background-color: orange;
}

#words {
    max-height: 610px !important;
}

#activity-module div.list-group-item {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
    border: none;
}



#auswertung-result {
    font-family: 'Montserrat', sans-serif !important;
    font-size: 1px !important;
    border: none;
    box-shadow: none !important;
    border-radius: 5px !important;
    background-color: hsla(212, 100%, 4%, 0.1) !important;
    color: black;
    margin-left: auto !important;
    margin-right: auto !important;
    position: relative !important;
    width: min-content !important;
}

.ads {
    display: none;
}

.value {
    font-size: 13px !important;
}

.name {
    background-color: orange;
}

.nav-tabs {
    position: relative;
    left: 12px;
    bottom: 1px;
}

.value {
    background-color: darkorange !important;
}

#table-data {
    background-color: hsla(212, 100%, 4%, 0.1) !important;
    color: white;
    border-collapse: collapse !important;
    border: none !important;
    border-radius: 15px !important;
}

td.ago {
    color: white !important;
}

.nav-tabs {
    border: none !important;
}

.table > thead > tr > th {
    border-bottom: solid 1px black !important;
}


::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgb(0, 0, 0);
    border-radius: 10px;
    background-color: #000 !important;
}

::-webkit-scrollbar {
    width: 12px;
    background-color: #212121;
}

::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(255, 148, 198, .3);
    background-color: hsl(301, 100%, 92%) !important;
}



#content-bg {
    background: none;
}

#main-content-trenner {
    background: none;
    background-color: transparent !important;
}

@media (min-width: 992px) {
    .col-md-6 {
        width: 49%;
        margin-left: 5px;
    }
}

td {
    background-color: transparent !important;
    border: none !important;
}

td.name {
    background-color: transparent !important;
    border: none !important;
}

td.value {
    background-color: transparent !important;
    border: none !important;
}


.nav-tabs {
    border-bottom: 1px solid #ff812d;
}

.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    background-color: hsla(212, 100%, 1%, 0.1);
    border: 1px solid hsla(212, 100%, 4%, 0.01);
}

.nav-tabs > li > a {
    background-color: hsla(212, 100%, 4%, 0.2);
    border: 1px solid hsla(212, 100%, 4%, 0.01);
}

table#profile-data-table {
    background-color: hsla(212, 100%, 0%, 0.1) !important;
    border-radius: 10px;
}

table#profile-data-table tr:nth-child(even) {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
    border-radius: 10px;
}

table#join-competition-table tr:nth-child(odd) {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
}

table * {
    border-color: black !important;
}

#wpm {
    background-color: #f7cb7b;
}

table {
    border-radius: 10px;
}

.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
    background-color: orange;
}

#speedtest-main #auswertung-box #auswertung-result h3 {
    background-color: transparent !important;
    background: none;
}

#speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score div,
#speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score {
    display: none;
}

#timer,
.glyphicon-refresh:before,
#speedtest-main #reload-btn {
    color: white !important;
    background-color: hsla(212, 100%, 0%, 0.05) !important;
    border: none;
    border-radius: 15px !important;
}

.glyphicon-refresh:before {
    background-color: #f000 !important;
}

#speedtest-main #reload-btn {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
}

.list-group-item {
    background-color: hsla(212, 100%, 0%, 0.05) !important;
    border: none;
}

.list-group-item:hover {
    background-color: hsla(212, 100%, 0%, 0.15) !important;
    border: none;
}

.list-group-item.active {
    background-color: hsla(212, 100%, 0%, 0.25) !important;
    border-right: none;
    border: none;
}
.list-group-item.active:hover {
    background-color: hsla(212, 100%, 0%, 0.35) !important;
    border: none;
}

#badge-box h3#box-title {
    background: none;
    background-color: hsla(212, 100%, 4%, 0.05) !important;
}


#table-data tr:nth-child(even) {

    background-color: hsla(212, 100%, 4%, 0.05) !important;
}


h3,
p {
    color: white !important;
}

#activities #myActivitiesTabContent {
    border: none !important;
}
#activities #myActivitiesTabContent tr:nth-child(odd) {
    background-color: hsla(212, 100%, 4%, 0.05) !important;
}

.panel {
    background-color: transparent !important;
    border: none;
}

.panel-primary {
    border: none;
}

.panel-body,
#participation {
    background-color: hsla(212, 100%, 1%, 0.05) !important;
}

.panel-primary > .panel-heading {
    background-color: hsla(212, 100%, 1%, 0.05) !important;
    color: white;
    border: none;
}

.content-box {
    background-color: hsla(212, 100%, 1%, 0.05) !important;
}

.table-striped tr:nth-child(even) {
    background-color: hsla(212, 100%, 1%, 0.05) !important;
}

#speedtest-main #reload-btn:hover {
    background-color: hsla(212, 100%, 0%, 0.3) !important;
}

#speedtest-main #input-row #inputfield {
    background: url("https://i.imgur.com/Bc4LpS4.png") !important;
    background-position: right !important;
    background-repeat: no-repeat;
}

.top-first-bg{
    background: url("https://i.imgur.com/mm8KXyF.png") !important;
    background-color: black !important;
    opacity: 1;
    color: black !important;
}

.top-first-bg *{
    color: black !important;
    font-weight: bold;
}

.panel-success>.panel-heading {
    background-color: rgba(0, 0, 0, 0.2);
    color: white;
    border-bottom: 1px solid black !important;
}

#ergebnis {
    border: none !important;
}

.btn-default {
    background-color: rgba(0, 0, 0, 0.2) !important;
    color: white !important;
}

#timer.hide-time {color: rgba(0, 0, 0, 0.0) !important;}

}
