css:
@-moz-document domain("facebook.com")
{
  #contentCol,#headNavOut,#left_column,#navAccount,#navAccount ul,#right_column,.jewel,.jewelBox,.photo_container,.photo_table,.ufi_section
  {
    border: none!important;
  }

  body
  {
    background-color: rgba(255,255,255,0)!important;
  }

  html#facebook
  {
    background: url("http://tolchx.com/wallpapers/imagen(405).jpg")fixed;
    background-size: 100% auto;
  }

  body>#blueBar
  {
    background: 0 0!important;
  }

  .jewelBox
  {
    background-color: rgba(255,255,255,.9)!important;
  }

  .fbTimelineCapsule,.timelineLayout #contentArea
  {
    background-color: rgba(255,255,255,.1)!important;
  }

  #headNavOut
  {
    background: 0 0!important;
  }

  #pageNav a
  {
    color: #111!important;
  }

  #pageNav a:hover
  {
    color: #333!important;
    background: 0 0!important;
  }

  #navAccount ul
  {
    background-color: rgba(255,255,255,.9)!important;
  }

  #contentCol,#left_column,#right_column
  {
    background-color: rgba(255,255,255,.4)!important;
  }

  #pagelet_main_column_personal,#timeline_tab_content
  {
    background-color: rgba(255,255,255,.1)!important;
    border: none!important;
  }

  .add_comment_text,.box_header,.inputtext,.uiMorePager
  {
    background-color: rgba(255,255,255,.5)!important;
  }

  .photo_container,.photo_table,.ufi_section
  {
    background: 0 0!important;
  }

  #ego,#pagelet_adbox,#sidebar_ads
  {
    display: none!important;
  }

  #u_0_13,#u_0_1o,div#u_0_1n,u_0_2h
  {
    color: #00f;
    background: rgba(255,255,255,.5);
  }

  div#leftCol,div._5rzs
  {
    background: rgba(255,255,255,.4);
  }

  div#pagelet_canvas_nav_content,div._5qqe
  {
    background: #333;
    opacity: .1;
  }

  div#u_0_0,div._4-u2.mbm._5v6e,div._5rzs
  {
    background: rgba(255,255,255,.3);
  }

  #blueBarHolder #blueBar,#blueBarNAXAnchor,div#blueBarNAXAnchor._4f7n._xxp.fixed_elem::after
  {
    background: #3a5795!important;
  }

  .bubbles ._50kd ._kso,.bubbles div._kso
  {
    -webkit-border-radius: 2px!important;
    background: 0 0;
  }

  .navLink
  {
    color: #fff!important;
  }

  ._rw
  {
    -webkit-filter: grayscale(20%);
    border-radius: 5px!important;
  }

  .bubbles div._kso
  {
    border: 1px solid #B3D9FF;
  }

  .fbNubFlyoutTitlebar
  {
    background: #3a5795!important;
    border: 1px solid #3a5795!important;
    border-radius: 1px!important;
  }

  .uiSideNav .sideNavItem .hasCount:hover,.uiSideNav .sideNavItem .noCount:hover
  {
    text-shadow: 2px 2px 2px rgba(57,255,20,.75)!important;
    color: #ff1414!important;
  }

  .fbbody,.fbbody a
  {
    color: #00f;
  }
}

@-moz-document url-prefix("https://www.facebook.com/pages")
{
  div#rightCol
  {
    width: 125px!important;
  }

  div#u_0_0
  {
    width: 980px!important;
  }

  div#u_0_3h,div#u_0_3q
  {
    color: #00f;
    background: rgba(255,255,255,.5);
  }
}

@-moz-document domain("facebook.com")
{
  .loggedout_menubar_container
  {
    background: 0 0!important;
  }

  .fbIndex #globalContainer #content,.fbIndex #globalContainer #dropmenu_container,.fbIndex #globalContainer #pageFooter
  {
    display: none!important;
  }

  .login_form_label_field,.login_form_label_field a,label.uiInputLabelLabel
  {
    color: #666!important;
  }

  .fbIndex .loggedout_menubar
  {
    width: auto!important;
  }

  .fbIndex .loggedout_menubar_container
  {
    position: fixed!important;
    width: 465px!important;
    height: 82px!important;
    min-width: 0!important;
    top: 45%!important;
    left: 5%!important;
  }

  .loggedout_menubar
  {
    background: rgba(0,0,0,.6)!important;
    padding: 0 10px 14px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    box-shadow: 20px 20px 40px rgba(0,0,0,.5);
    border: 1px solid #000;
  }

  .fbFeedbackContent
  {
    background-color: rgba(255,255,255,.8)!important;
  }

  ._5vsj .UFIRow,._5vsj._5vsj._5vsj,UFIList,fbFeedTickerStory tickerStoryClickable,tickerActivityStories
  {
    background-color: rgba(255,255,255,0)!important;
  }

  ._1zw4,._4-u2,._4lh ._2-d1
  {
    background-color: rgba(255,255,255,.4)!important;
  }

  ._6m2
  {
    background-color: rgba(255,255,255,.5)!important;
  }

  ._4lh,.fbTimelineTimePeriod
  {
    background-color: rgba(255,255,255,0)!important;
  }
}
